INSERT INTO `article` (`id`, `title`, `description`, `category_id`, `catname`, `content`, `status`, `ispremium`, `create_at`, `publish_at`, `view`, `avatar`) VALUES
(1, 'Liverpool sẽ mất những gì nếu Barca bán Coutinho?', 'Với phong độ tệ hại hiện tại, Coutinho đang trên đường rời khỏi Barca. Không chỉ là chuyện giữa Coutinho và Barca, Liverpool cũng không muốn thương vụ này xảy ra một chút nào, vì họ cũng sẽ chịu ảnh hưởng không nhỏ.', 3, 'Tin quốc tế', '<p>Coutinho là cái tên gây thất vọng nhiều nhất trong 2 trận đấu của Barca với Liverpool,\r\n        tiền vệ người Brazil gần như mất hút trong cả 2 lượt trận. Đây là tình trạng thường xuyên\r\n        diễn ra ở mùa giải này của tiền vệ sinh năm 1992, ban lãnh đạo và cả hội cổ động viên đã\r\n        không còn đủ kiên nhẫn với Coutinho. Ngay trong hè này, Barca sẽ lên kế hoạch tìm một bến\r\n        đỗ mới cho anh để dọn chỗ cho bản hợp đồng tương lai Antoine Griezmann.</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F1%2Fcoutinho.jpg?alt=media&token=0473ad25-4545-443f-8280-bc9bb01d382a\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Coutinho không còn cơ hội ở Barca\r\n    </p>\r\n    <p>Với những diễn biến đang xảy ra, Liverpool coi như sẽ đánh mất khoản lợi nhuận ước chừng 25 triệu\r\n        euro trong thương vụ Barca bán sớm Coutinho.</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F1%2Fgriezmann.jpg?alt=media&token=05a58617-2f5f-49dd-a520-a4453438c54c\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Griezmann được chọn làm cái tên thay thế cho Coutinho\r\n    </p>\r\n    <p>Barca hiện tại đang có những liên hệ với tiền đạo Antoine Griezmann khi anh này vừa chính thức chia tay Atletico.\r\n        Còn Liverpool, họ vừa kết thúc Premier League với tư cách Á quân, thầy trò Klopp cũng đang hướng đến danh hiệu\r\n        cao quý nhất mùa này đó là chiếc cup C1. Trận chung kết giữa Liverpool và Tottenham sẽ diễn ra vào lúc\r\n        2h00 ngày 2/6 tại sân vận động Wanda Metropolitano.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F1%2Fcoutinho.jpg?alt=media&token=0473ad25-4545-443f-8280-bc9bb01d382a'),
(2, 'ĐT Việt Nam hậu King\'s Cup: Xếp hạng FIFA ra sao, giữ nổi top 16 châu Á không?', 'Thứ hạng hiện tại của ĐT Việt Nam trên BXH FIFA ra sao sau trận thua\r\n            đáng tiếc ở chung kết King\'s Cup 2019?', 2, 'Tin trong nước', '<p><b> King\'s Cup</b> thuộc FIFA Day, mỗi trận đấu đều ảnh hưởng đến điểm số trên bảng xếp hạng FIFA. Một vị trí\r\n        cao sẽ giúp ĐT Việt Nam có vị trí trong top 16 châu Á - tương đương nhóm hạt giống số 2 ở vòng loại\r\n        thứ 2 World Cup 2022 khu vực châu Á và sẽ rơi vào bảng đấu \"dễ thở\".</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F1%2F1.jpg?alt=media&token=a65544cf-b056-4433-accc-cff80ed6a572\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">ĐT Việt Nam chính thức bảo toàn vị trí trong top 16 châu Á -\r\n        tương đương nhóm hạt giống số 2 tại vòng loại World Cup 2022 khu vực châu Á</p>\r\n\r\n    <p>Ở chung kết, chúng ta đã cầm hòa Curacao trong 90 phút chính thức và thua trên chấm luân lưu nên\r\n        vẫn được cộng thêm 0,7 điểm để bảo toàn vị trí thứ 97 FIFA (15 châu Á) với 1232,2 điểm.</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F1%2F2.jpg?alt=media&token=9c540327-bdf0-4fbe-80d3-45a4c90e7dad\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">ĐT Việt Nam chơi tốt trước Curacao</p>\r\n    <p>Hiện tại, hai đối thủ cạnh tranh quyết liệt với ĐT Việt Nam cho suất trong top 16 châu Á là Jordan\r\n        (hạng 16, 1226 điểm) và Palestine (hạng 17, 1224 điểm). Vào đêm qua, Jordan đã thất thủ 1-5 trước\r\n        Slovakia và bị trừ 2,8 điểm trên BXH FIFA (còn 1226 điểm). </p>\r\n\r\n    <p>Tháng 6 này, Jordan còn 1 trận với Indonesia (hạng 159) và được cộng 3 điểm nếu chiến thắng (1229 điểm),\r\n        còn Palestine phải đụng độ Kyrgyzstan - đội bóng xếp hạng 95 thế giới và\r\n        được cộng 5,3 điểm nếu chiến thắng (1229,3 điểm).</p>\r\n    <p>Tuy nhiên từng đó là chưa đủ để họ vượt qua Việt Nam và đồng nghĩa, thầy trò HLV Park Hang Seo\r\n        chính thức thuộc nhóm 2 vòng loại World Cup 2022 khu vực châu Á mà không cần quan tâm hai trận đấu của Jordan.\r\n        Palestine trong tháng 6. Có thể nói, ĐT Việt Nam đã xuất sắc hoàn thành nhiệm vụ tại King\'s Cup.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F1%2F1.jpg?alt=media&token=a65544cf-b056-4433-accc-cff80ed6a572'),
(3, 'Chàng trai mắc ung thư từ năm 2 tuổi trở thành doanh nhân trẻ đáng ngưỡng mộ ', ' Jack Witherspoon, 19 tuổi, đã may mắn sống sót sau ba lần chiến đấu với bệnh bạch\r\n            cầu ác tính và trở thành doanh nhân trẻ tuổi đáng ngưỡng mộ nhờ niềm đam mê nấu ăn.', 5, 'Doanh nhân', '<img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F1%2Fdoanhnhan1.jpg?alt=media&token=40bff46d-aed2-4ddc-8edd-7cda28ae9531\"\r\n        alt=\"\" class=\"w-100\">\r\n    <p>Đầu bếp và doanh nhân 19 tuổi Jack Witherspoon, phát hiện và chiến đấu với căn bệnh ung thư ác tính từ\r\n        năm 2 tuổi, đã ra mắt sản phẩm liên doanh đầu tiên của mình, Chef Jack\'s Kitchen, vào tháng 11. Đó là\r\n        một loại bánh quy nướng hương vị vani và đường quế có tên skonie. Doanh nhân trẻ tuổi nổi tiếng này\r\n        cũng là tác giả của một cuốn sách dạy nấu ăn khi cậu mới 8 tuổi, sau khi kết thúc điều trị lần thứ\r\n        hai với bệnh bạch cầu lymphocytic cấp tính.</p>\r\n\r\n    <p>“Tôi sẽ không viết được ra một cuốn sách và cũng chẳng bao giờ học nấu ăn nếu không bị ung thư. Thật khó\r\n        để nói ra điều gì khiến tôi cảm thấy tự hào. Tôi chỉ biết rằng những điều xảy ra đối với tôi đều có quan\r\n        hệ mật thiết với nhau. Tôi muốn chỉ cho những đứa trẻ khác làm thế nào để biến mọi tình huống xấu nhất\r\n        trở thành một cơ hội nào đó. Điều đó đúng với mọi thứ tôi đã làm và cả với công ty của mình”, Witherspoon chia\r\n        sẻ.</p>\r\n\r\n    <p>Bây giờ, khi đã có nhiều trải nghiệm trong công việc kinh doanh, Jack Witherspoon đã quyên góp một phần tiền\r\n        thu được từ việc bán sách và skonie cho viện nghiên cứu bệnh bạch cầu ở trẻ em. Đến nay, quỹ tài trợ đã huy\r\n        động được hơn 150.000 đô la (khoảng 3,5 tỷ đồng). Witherspoon nói với Business Insider rằng Chef Jack\'s Kitchen\r\n        sắp ký hợp đồng với hệ thống dịch vụ thực phẩm khổng lồ Sodexo để cung cấp skonies và các món nướng khác cho\r\n        quán ăn trong các bệnh viện trên toàn quốc.</p>\r\n\r\n    <img class=\"w-100\" src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F1%2Fdoanhnhan2.jpg?alt=media&token=bf9178fe-90dc-47c1-bd99-81a38d1c3ac5\"\r\n        alt=\"\" >\r\n    <p style=\"color: #0040ff; text-align:center;\">Skonies giống như một sự giao thoa giữa bánh nướng và bánh quy.\r\n        (Nguồn: BI)</p>\r\n\r\n    <p style=\"font-weight: bold\">Sáng kiến lúc nửa đêm</p>\r\n    <p>“Tôi nhớ ngày đó rõ như ban ngày,” Witherspoon chia sẻ, “Tôi đã ở trong bệnh viện và lúc đó là 12 đêm. Không có\r\n        chương trình\r\n        nào dành cho trẻ em. Lúc đó là vào những năm đầu thập niên 2000, vì vậy họ chỉ có chương trình truyền hình cáp.\r\n        Khi mẹ\r\n        tôi đang chuyển tiếp kênh thì chương trình Food Network lọt vào tầm mắt của tôi. Tôi đã xem tất cả các tập mà họ\r\n        có khi\r\n        tôi còn ở đó. Đó là điều duy nhất tôi có thể làm để giải trí. Nó thực sự là nguồn động lực quan trọng mà tôi cần\r\n        khi\r\n        đang phải điều trị ung thư tại bệnh viện”.</p>\r\n    <p>Witherspoon đã làm việc cùng với mẹ của mình để lập ra danh mục các công thức nấu ăn của nhiều đầu bếp nổi tiếng\r\n        như Rachel\r\n        Ray và Emeril Lagasse, sau đó họ có thể thử làm chúng sau khi anh ấy khỏi bệnh. Khi Witherspoon được ra viện, họ\r\n        bắt đầu\r\n        làm việc theo danh mục đã đề ra trước đó. Witherspoon nói với Business Insider rằng họ đều thực hiện một công\r\n        thức nấu ăn\r\n        mới mỗi tối, trong khi phát triển 60 công thức đặc trưng trong cuốn sách mà anh đã viết.</p>\r\n    <p>Mẹ của Witherspoon tiếp tục ủng hộ con trai của mình, giờ đây đã là sinh viên đại học, trên con đường kinh doanh\r\n        chuỗi cửa\r\n        hàng bánh quy nướng của anh. Theo Witherspoon, mẹ cậu là “bộ não” và người tiếp thị tuyệt vời đằng sau thương\r\n        hiệu này.\r\n        Bà đã giúp anh đàm phán và kêu gọi nguồn vốn từ các nhà đầu tư tiềm năng.</p>\r\n    <p>Witherspoon đang theo đuổi chuyên ngành kinh doanh tại một trường cao đẳng cộng đồng tại địa phương của mình và\r\n        hy vọng sẽ\r\n        chuyển sang Đại học California ở Los Angeles trong khi tiếp tục xây dựng doanh nghiệp của mình. Anh đã không bị\r\n        ung thư\r\n        trong gần tám năm sau khi tái phát lần thứ ba khi 11 tuổi.</p>\r\n    <p>Chef Jack\'s Kitchen hiện đang bán skonies trực tuyến và tại một cửa hàng tạp hóa ở bãi biển Redondo tại nơi mà\r\n        Witherspoon\r\n        đang sinh sống. Nhưng anh cũng rất háo hức muốn làm bánh trước mặt những đứa trẻ đang phải ăn thức ăn nhàm chán\r\n        ở bệnh viện,\r\n        điều mà anh cũng đã phải trải qua thời thơ ấu.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F1%2Fdoanhnhan1.jpg?alt=media&token=40bff46d-aed2-4ddc-8edd-7cda28ae9531'),
(4, 'Tài sản của \"Tỷ phú người sắt\" “bốc hơi” 4,9 tỷ USD trong chưa đầy nửa năm ', 'Cổ phiếu của Tesla Inc. chưa bao giờ khởi đầu một năm tồi tệ\r\n                     hơn so với năm 2019 và Elon Musk đang phải trả giá lớn nhất.', 5, 'Doanh nhân', '<p>Tính tới ngày 31/5, giá cổ phiếu của công ty do Elon Musk - Tỷ phú được mệnh danh là \"người sắt\" ngoài đời thực \r\n                    - đứng đầu đã giảm 43% kể từ đầu năm, khiến giá trị cổ phần của Musk bị “bốc hơi” 4,9 tỷ USD do Phố Wall \r\n                    ngày càng hoài nghi về nhu cầu của người tiêu dùng đối với xe điện của công ty.</p>\r\n\r\n                <p>Tỷ phú Elon Musk, 47 tuổi, hiện đang xếp thứ 46 trên bảng xếp hạng tỷ phú Bloomberg với giá trị tài sản \r\n                    ròng là 19,7 tỷ USD, giảm 17 bậc so với vị trí thứ 29 vào đầu năm nay.</p>\r\n                \r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F2%2F1.jpg?alt=media&token=6cfe0edd-9c10-4aaf-b92a-4f43dc83b788\" alt=\"\" width=\"700\" height=\"500\">\r\n                <p style=\"color: #0040ff; text-align:center;\">CEO Tesla Elon Musk (nguồn: Bloomberg)</p>\r\n\r\n                <p>Sự kiện này đã gây ra thiệt hại tổng cộng 7,8 tỷ USD đối với cổ phần của Tesla, trong đó gây \r\n                    thiệt hại chủ yếu tới bốn cổ đông lớn nhất là Elon Musk, Tencent Holdings Ltd., Quỹ đầu tư\r\n                     công cộng Ả Rập Saudi và Larry Ellison. Trong đó, chỉ tính riêng tháng Năm, con số thiệt \r\n                     hại lên tới 2,7 tỷ USD. Tesla, Tencent và PIF đều từ chối yêu cầu bình luận về sự kiện này.</p>\r\n                <p>Ellison, người đồng sáng lập Oracle Corp, người từng lên tiếng bảo vệ Elon Musk trước những bê bối,\r\n                     đã mua 3 triệu cổ phiếu vào năm ngoái và gia nhập hội đồng quản trị Tesla vào tháng 12/2018.</p>\r\n                <p>Ngoài các cổ đông lớn trên, JPMorgan Chase & Co. có thể cũng chịu chung “số phận” này. Bởi Quỹ Đầu tư \r\n                    Quốc gia (PIF) của Ả Rập Saudi hiện nắm giữ khoảng 5% cổ phần Tesla nhưng đã có biện pháp phòng ngừa \r\n                    rủi ro khi thực hiện thỏa thuận chia sẻ số cổ phiếu này với JPMorgan Chase & Co. hồi tháng 1/2019, \r\n                    theo Financial Times.</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F2%2F2.jpg?alt=media&token=084311f6-7a15-4afa-8f9e-7d9d925d9788\" alt=\"\" width=\"700\" height=\"400\">\r\n                <p style=\"color: #0040ff; text-align:center;\">Tesla trong tình trạng bấp bênh về số phận tương lai</p>\r\n                <p>Kể từ thời điểm thỏa thuận này có hiệu lực vào ngày 17/1 cho đến nay, cổ phiếu Tesla đã sụt 46%, \r\n                    đồng nghĩa giá trị số cổ phần mà PIF nắm giữ giảm hơn 1,3 tỷ USD. Tuy nhiên, Jessica Francisco, \r\n                    phát ngôn viên của JPMorgan có trụ sở tại New York, đã từ chối bình luận về sự kiện này.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F2%2F1.jpg?alt=media&token=6cfe0edd-9c10-4aaf-b92a-4f43dc83b788'),
(5, 'Ông Trump tuyên bố ngừng \"vô thời hạn\" đánh thuế hàng hóa Mexico và đây là lý do', 'Tổng thống Mỹ Donald Trump vừa chính thức tuyên bố sẽ bỏ kế hoạch áp thuế với Mexico \r\n                    khi quốc gia láng giềng hứa sẽ có những bước đi mới để ngăn chặn dòng người nhập cư vào Mỹ.', 6, 'Tài chính', ' <p style=\"font-weight: bold\">Thỏa thuận được mong chờ</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TaiChinh%2F1%2Ftrump.jpg?alt=media&token=d4e81a70-bd0d-4343-8f8e-90fd02ab3335\"\r\n        alt=\"\" class=\"banner\">\r\n    <p>\"Tôi vui mừng thông báo với các bạn rằng Mỹ đã đạt được một thỏa thuận với Mexico. Các khoản thuế, dự kiến\r\n        có hiệu lực với hàng hóa nhập khẩu của Mexico từ ngày 10/6, sẽ bị đình chỉ vô thời hạn\", ông Trump cho biết\r\n        trong một dòng thông điệp trên mạng xã hội tối 7/6 theo giờ Mỹ.</p>\r\n    <p>Theo thỏa thuận, Mexico sẽ mở rộng việc triển khai lực lượng vệ binh quốc gia trên phạm vi toàn quốc, trong đó\r\n        \"ưu tiên cho khu vực biên giới phía nam\", để ngăn chặn dòng người nhập cư vào Mỹ. Những người xin tị nạn vào Mỹ\r\n        sẽ được đưa trở về Mexico, nơi họ chờ yêu cầu của mình được Mỹ giải quyết. Về phần mình, Mỹ cam kết gia tăng tốc\r\n        độ xét duyệt tị nạn với những người đang chờ đợi ở Mexico. </p>\r\n    <p>Quyết định của Tổng thống Trump được mô tả là bước ngoặt kịch tính sau khi ông chủ Nhà Trắng liên tiếp chỉ trích\r\n        Mexico vì không ngăn dòng người di cư từ Trung Mỹ vào nước này thông qua quốc gia láng giềng phía nam. Quyết\r\n        định\r\n        của ông Trump cũng đánh dấu chiến thắng cho Tổng thống Mexico Andres Manuel Lopez Obrador, người liên tiếp gây\r\n        áp\r\n        lực để loại bỏ những mối đe dọa thuế quan từ phía Mỹ. </p>\r\n    <p>Thỏa thuận giữa hai nước Bắc Mỹ cũng chính thức vô hiệu hóa kế hoạch đánh thuế 5% mà ông Trump muốn áp dụng với\r\n        hàng\r\n        hóa nhập khẩu của Mexico cũng như xóa bỏ nguy cơ mức thuế này tăng lên 25% vào tháng 10 theo đe dọa của ông chủ\r\n        Nhà\r\n        Trắng.</p>\r\n    <p style=\"font-weight: bold\">Phản ứng các bên</p>\r\n    <p>Ngay lập tức, quyết định này được hoan nghênh bởi các nghị sĩ Cộng hòa và nhóm người phản đối với lập luận thuế\r\n        quan\r\n        áp vào hàng hóa Mexico sẽ gây thiệt hại cho nền kinh tế Mỹ, làm tăng tổn hại với tăng trưởng việc làm cũng như\r\n        trì\r\n        hoãn thỏa thuận thương mại giữa Mỹ, Canada và Mexico được gọi là USMCA. Ngoài ra, Mexico còn là thị trường nhập\r\n        khẩu\r\n        lớn thứ 2 của Mỹ. </p>\r\n    <p>\"Tôi chờ mong được làm việc với Chính quyền và những đồng nghiệp của mình ở Thượng viện và Hạ viện Mỹ nhằm thông\r\n        qua\r\n        USMCA kịp thời để các công ty và công nhân Mỹ có thể gặt hái được những lợi ích từ thỏa thuận cập nhật và hiện\r\n        đại này\",\r\n        Nghị sĩ Kevin Brady của bang Texas, cho hay.</p>\r\n    <p>Một số Nghị sĩ khác của đảng Dân chủ lại tỏ ra mỉa mai Tổng thống Trump. Chuck Schumer, lãnh đạo phe thiểu số ở\r\n        Thượng viện Mỹ,\r\n        đăng đàng nói rằng: \"Bây giờ, vấn đề đã được giải quyết. Tôi chắc chắn rằng chúng ta sẽ không nghe thấy những\r\n        điều gì khác\r\n        trong tương lai\".</p>\r\n    <p>Trong tuyên bố chính thức, phía Mexico cho biết thỏa thuận giữa họ và Mỹ đã đạt được sau nhiều ngày đàm phán, đặc\r\n        biệt là\r\n        cuộc thảo luận kéo dài 12 giờ vào ngày 7/6. Bộ trưởng Ngoại giao Mexico Marcelo Ebrard cho biết thỏa thuận công\r\n        bằng cho\r\n        cả đôi bên. \"Chúng tôi gặp nhau ở điểm giữa\", ông Ebrard nói về lợi ích của cả Mỹ và Mexico trong thỏa thuận.\r\n    </p>\r\n    <p>Trong tuyên bố chung được công bố cùng ngày, hai bên cho biết sẽ hợp tác để \"thực thi một giải pháp lâu dài\". Các\r\n        cuộc\r\n        thảo luận giữa hai bên cũng sẽ tiếp tục diễn ra sau đó. Cả Mỹ và Mexico đều đồng ý để xây dựng một Trung Mỹ\r\n        thịnh vượng,\r\n        bước đi lâu dài nhằm giải quyết những nguyên nhân cơ bản của tình trạng di cư.</p>\r\n    <p style=\"font-weight: bold\">Gánh nặng cho Mexico</p>\r\n    <p>Duncan Wood, giám đốc Viện Mexico tại Trung tâm học giả quốc tế Woodrow Wilson ở Washington, cho rằng, Mexico có\r\n        thể tránh\r\n        bị đánh thuế nhưng \"sẽ phải trả giá đắt\". Thực tế, thỏa thuận sẽ khiến hàng chục nghìn người xin yêu cầu tị nạn\r\n        sẽ phải\r\n        chờ đợi tại Mexico trong thời gian đơn xin tị nạn của họ được Mỹ xử lý. Điều này đồng nghĩa với việc Mexico sẽ\r\n        phải cung\r\n        cấp nhà ở, việc làm, giáo dục và cả những dịch vụ chăm sóc sức khỏe cho họ. Đây là một gánh nặng rất lớn.</p>\r\n    <p>Việc ông Trump đe dọa đánh thuế hàng hóa nhập khẩu từ Mexico đã khiến thị trường tài chính náo loạn. Các chuyên\r\n        gia kinh tế\r\n        cũng dự báo về nguy cơ suy thoái gia tăng ở Mỹ, nền kinh tế lớn nhất thế giới, nếu Mỹ quyết tâm đánh thuế hàng\r\n        hóa Mexico.\r\n        Một cuộc chiến thương mại toàn cầu có thể khiến tổng sản phẩm quốc nội giảm 0,8%, tương đương 800 tỷ USD vào\r\n        giữa năm 2021.</p>\r\n    <p>Sự không chắc chắn và những cuộc đàm phán liên tiếp trong vài ngày qua đã khiến đồng peso, đồng tiền được giao\r\n        dịch nhiều nhất ở Mỹ - Latin,\r\n        biến động mạnh mẽ. Thỏa thuận giữa Mỹ và Mexico đạt được ngoài giờ giao dịch nên chưa thể xác định tác động của\r\n        nó với đồng tiền này\r\n        cho tới chiều 2/6.</p>\r\n    <p>Những thông tin tích cực về cuộc đàm phán thương mại giữa Mỹ và Mexico cũng khiến thị trường chứng khoán Mỹ phản\r\n        ứng tích cực\r\n        trong phiên giao dịch ngày 7/6. Chỉ số trung bình công nghiệp Dow Jones tăng 263,28 điểm lên 25.983,94 điểm, dẫn\r\n        đầu bởi đà\r\n        tăng của Microsoft và Apple. S&P 500 nhích 1%, đóng cửa phiên với 2.873,34 điểm, khi cổ phiếu nhóm công nghệ có\r\n        diễn biến vượt\r\n        trội. Nasdaq Composite tăng 1,7% lên 7.742,10 điểm.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TaiChinh%2F1%2Ftrump.jpg?alt=media&token=d4e81a70-bd0d-4343-8f8e-90fd02ab3335'),
(6, 'Thực hiện kết luận của Thủ Tướng Chính phủ, Vân Đồn (Quảng Ninh) tập trung phát triển BĐS du lịch cao cấp', 'Giai đoạn 2019-2030, Vân Đồn tập trung thu hút đầu tư phát triển các sản phẩm, dịch vụ du lịch hiện đại mô \r\n                    phỏng trên nền tảng công nghệ thông tin được xem là hướng đi mới của đặc khu kinh tế này.', 7, 'Bất động sản', '<img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BatDongSan%2F1%2Fbds1.jpg?alt=media&token=0eed6525-fe96-48a7-b6e4-4c572a3a1eb1\"\r\n        alt=\"\" class=\"banner\">\r\n    <p>Thực hiện kết luận của Thủ tướng Chính Phủ về xây dựng phát triển kinh tế Vân Đồn (Quảng Ninh) giai\r\n        đoạn 2019-2030, địa phương này sẽ có những hướng đi cụ thể trong các lĩnh vực. Trong đó, đối với\r\n        ngành kinh tế du lịch, Vân Đồn sẽ tập trung phát triển cơ sở hạ tầng du lịch và các loại hình sản\r\n        phẩm song song với hướng phát triển ngành du lịch để thu hút lượng khách đến địa phương.</p>\r\n\r\n    <p style=\"font-weight: bold\">Phát triển du lịch cao cấp:</p>\r\n    <p>Phấn đấu đến năm 2030 thu hút khoảng 2,5 triệu lượt khách du lịch, trong đó có trên 170 nghìn lượt\r\n        khách nước ngoài đến Vân Đồn. Theo đó, địa phương này tạo lập môi trường du lịch thân thiện, hấp\r\n        dẫn kết hợp với xây dựng và hoàn thiện hệ thống hạ tầng du lịch đồng bộ để phát triển ngành du lịch. </p>\r\n\r\n    <p style=\"font-weight: bold\">Về sơ sở hạ tầng du lịch:</p>\r\n\r\n    <p>Tiếp tục ưu tiên, hướng dẫn, hỗ trợ và đôn đốc các NĐT chiến lược triển khai thực hiện các dự án đầu tư\r\n        khu phức hợp du lịch, nghỉ dưỡng, giải trí cao cấp casino; khu phức hợp, nghỉ dưỡng giải trí cao cấp tại\r\n        xã Vạn Yên, tại đảo Ngọc Vừng…nhằm tạo ra các sản phẩm du lịch cao cấp thu hút khách du lịch, đặc biệt là khách\r\n        châu Á. </p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BatDongSan%2F1%2Fbds2.jpg?alt=media&token=3977f118-b442-42f0-be69-d50fff12555a\" alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Giai đoạn 2019-2030, Vân Đồn tập trung thu hút đầu tư\r\n        phát triển các sản phẩm, dịch vụ du lịch hiện đại, cao cấp</p>\r\n\r\n    <p>Đồng thời, hướng dẫn đôn đốc các NĐT hoàn thiện thủ tục đầu tư và triển khai xây dựng các tổ hợp khách\r\n        sạn cao cấp tại khu vực Bãi Dài, khu Đô thị Cái Rồng, xã Hạ Long, xã Vạn Yên, đảo Minh Châu, đảo Quan Lạn…</p>\r\n\r\n    <p style=\"font-weight: bold\">Về phát triển các loại hình sản phẩm du lịch: </p>\r\n    <p>Giai đoạn 2019-2030 Vân Đồn tập trung thu hút đầu tư phát triển các sản phẩm dịch vụ du lịch hiện đại:\r\n        Các trò chơi tương tác, mô phỏng trên nền tảng công nghệ thông tin và các công viên giải trí hoặc công\r\n        viên theo chủ đề quy mô quốc tế; xây dựng các công viên theo chủ đề văn hóa mô phỏng, tái hiện lại văn\r\n        hóa lịch sử có giá trị cao của địa phương, khu vực quốc gia gắn với mối liên hệ vùng. </p>\r\n    <p>Bên cạnh đó, nghiên cứu thu hút các sản phẩm du lịch hấp dẫn, chất lượng cao có khả năng khai thác,\r\n        thu hút khách du lịch quanh năm, đặc biệt là trong mùa đông. Chẳng hạn như, các khu nghỉ dưỡng giải\r\n        trí công viên nước trong nhà kính duy trì ở nhiệt độ thích hợp, có hồ tạo sóng, đường trượt nước và\r\n        các trò chơi dưới nước.</p>\r\n    <p>Ngoài ra, hướng đi của Vân Đồn phát triển các sản phẩm du lịch cao cấp kết hợp chăm sóc sức khỏe; kết\r\n        hợp với học tập, trải nghiệm và thể thao nhằm thu hút khách du lịch vừa thỏa mãn nhu cầu tham quan vừa\r\n        kết hợp được đa dạng sự trải nghiệm của khách du lịch. </p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BatDongSan%2F1%2Fbds1.jpg?alt=media&token=0eed6525-fe96-48a7-b6e4-4c572a3a1eb1'),
(7, 'Thị trường tỉnh sẽ là “con gà đẻ trứng vàng” cho Bách hóa Xanh ', 'Đại diện của Điện máy Xanh khi ấy cho biết, họ nhìn nhận thị trường là cả vùng thị xã, thị trấn của các tỉnh lị.', 9, 'Trung tâm mua sắm', '<p>Nếu hiểu theo nghĩa ấy thì dung lượng thị trường là vô cùng lớn và Điện máy Xanh còn rất nhiều vùng đất mới để mở\r\n        mang.\r\n        Giờ đây, khi Điện máy Xanh đã xác lập được vị thế không ai có thể phủ nhận thì câu chuyện này một lần nữa đang\r\n        được\r\n        lặp lại với Bách hóa Xanh. Thay vì chen chân trên một thị trường nhỏ bé, họ đang chọn một con đường khác để đi.\r\n    </p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TrungTamMuaSam%2F1%2Ftrungtam1.jpg?alt=media&token=a5369f6b-141c-46ee-ac40-ef514eaad0e2\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Cảnh mua bán tấp nập ở một cửa hàng Bách hóa Xanh tại Tây Ninh</p>\r\n    <p>Số lượng siêu thị Bách hóa Xanh tại các tỉnh đang tăng lên nhanh chóng cùng với hiệu quả vượt trội của các siêu\r\n        thị này.\r\n        Doanh thu trong tháng đầu của 2 cửa hàng mới khai trương trong tháng 5 vừa qua tại tỉnh Bà Rịa Vũng Tàu đạt 1,7\r\n        tỷ/shop,\r\n        cao hơn nhiều mức trung bình của toàn chuỗi với lượng khách ra vào khá cao đạt 550 lượt/ngày. Đặc biệt, trong\r\n        tháng 4,\r\n        lần đầu tiên, chuỗi Bách hóa Xanh có 2 cửa hàng có doanh thu vượt 4 tỷ đồng/tháng, điều đáng nói là, cả hai cửa\r\n        hàng này\r\n        đều nằm tại các tỉnh với lưu lượng khách trung bình “kỷ lục” từ 1.000 đến 1.200 lượt khách/ngày.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TrungTamMuaSam%2F1%2Ftrungtam2.jpg?alt=media&token=aec659eb-3bef-4e5c-9d53-0d85189ea2bd\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Bách hóa Xanh kết hợp Điện máy Xanh, một thử nghiệm thành công tại\r\n        tỉnh\r\n        hứa hẹn mang đến doanh thu và lợi nhuận cao hơn.</p>\r\n\r\n    <p>Trong một diễn biến khác, một tỉnh xa xôi như Bình Phước chứ không phải thành phố Hồ Chí Minh mới chính là nơi có\r\n        doanh\r\n        thu trung bình/1 siêu thị cao nhất trong số các tỉnh hiện có Bách hóa Xanh. Doanh thu bình quân 1 siêu thị ở đây\r\n        là 2,4\r\n        tỷ/siêu thị và lượt khách mua sắm tới 750 lượt/ngày. Tính chung sức bán của các siêu thị ở tỉnh là không hề kém\r\n        cạnh so\r\n        với các siêu thị tại thành phố Hồ Chí Minh, trung bình đạt 85-90% so với 1 siêu thị ở thành phố.</p>\r\n\r\n    <p>Cũng trong tuần vừa qua, Bách hóa Xanh tiếp tục thử nghiệm mô hình mới tại tỉnh Tây Ninh kết hợp giữa bán rau, củ\r\n        quả, thịt\r\n        cá và đồ gia dụng nhà bếp (sản phẩm của Điện máy Xanh) và nó cũng cho kết quả khả quan khi mỗi cửa hàng có thể\r\n        đóng góp thêm\r\n        20% từ gia dụng. Doanh số cửa hàng đạt hơn 1,2 tỷ chỉ trong 1 tuần đầu tiên mở bán.</p>\r\n    <p>Những tín hiệu khả quan này càng củng cố thêm sự thành công của Bách hóa Xanh, bởi suy cho cùng, tiến về các tỉnh\r\n        mới là mục\r\n        tiêu sau cùng của chuỗi này. Với quy mô thị trường cùng tiềm năng vô cùng to lớn, thị trường tỉnh chắc chắn sẽ\r\n        là “con gà\r\n        đẻ trứng vàng” cho Bách hóa Xanh.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TrungTamMuaSam%2F1%2Ftrungtam1.jpg?alt=media&token=a5369f6b-141c-46ee-ac40-ef514eaad0e2'),
(8, 'Giá heo hơi tăng vù vù lên 40.000 đồng/kg, chợ đầu mối \"cháy\" hàng', 'Theo phản ánh từ các chủ trang trại, giá heo hơi hôm nay (giá lợn hơi)\r\n            tại một số địa bàn miền Bắc đã tăng lên 40.000 đồng/kg', 10, 'Thị trường 24h', '<img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/ThiTruong24h%2F1%2F24h-1.jpg?alt=media&token=53578fc7-7073-47b0-b302-b6b02ca784e8\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Thương lái chọn mua heo hơi và vận chuyển lên xe đưa đi tiêu\r\n        thụ tại Chợ đầu mối gia súc - gia cầm Hà Nam. Ảnh: Trần Quang</p>\r\n    <p>Sáng nay, ông Vũ Văn Kỳ, chủ trang trại đang nuôi 600 lợn thịt ở xã Hải Triều, huyện Tiên Lữ (tỉnh Hưng Yên)\r\n        báo tin vui: Giá lợn hơi trên địa bàn đã đạt mức 40.000 đồng/kg. Mức giá này tăng khoảng 10.000 đồng/kg so\r\n        với vài ngày trước. Thương lái vào hỏi mua tới tấp, không còn lợn to mà bán.</p>\r\n\r\n    <p>Trò chuyện với PV qua điện thoại, ông Vũ Văn Kỳ phấn khởi cho biết: \"Không ngờ chỉ sau 1-2 ngày mà giá lợn hơi\r\n        tăng chóng mặt như vậy. Hiện tôi đang rất phấn khởi, có thêm động lực để giữ trại nghiêm ngặt trước \"bão\" dịch\r\n        tả lợn châu Phi. Tuy nhiên rất tiếc là không phải ai cũng còn lợn bán, nhiều gia đình chuồng trống không vì\r\n        đã bị virus dịch tả lợn tấn công, chết sạch. Có lẽ đây cũng là nguyên nhân khiến giá lợn hơi tăng chóng mặt\".\r\n    </p>\r\n\r\n    <p>Trước đó, anh Lê Văn Quyền, chủ trang trại ở huyện Lương Sơn (Hoà Bình) cũng cho biết, hôm nay Công ty chăn nuôi\r\n        CP Chi\r\n        nhánh miền Bắc tiếp tục tăng giá bán heo hơi tại trại thêm 2.000 đồng/kg, lên 36.000 đồng/kg tại kho. </p>\r\n    <p>Anh Quyền cũng cho biết, giá lợn hơi tại khu vực Hà Nội cũng đang tăng mạnh, lên khoảng 4.000 đồng/kg so với\r\n        trước,\r\n        đạt 36.000 đồng/kg. Hiện lợn trong dân, nhất là các hộ nuôi nhỏ lẻ không còn nhiều, có nơi dịch tả lợn châu Phi\r\n        đã\r\n        làm chết tới 40 - 60% tổng đàn nên chuyện xảy ra khan hiếm lợn bán trong một thời điểm là điều dễ hiểu. Với tình\r\n        hình\r\n        này, rất có thể giá lợn hơi sẽ còn tăng nữa.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/ThiTruong24h%2F1%2F24h-2.jpg?alt=media&token=4f7dc10a-6763-4ccc-8d3e-dbb05c11227f\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Thương lái chọn mua heo mảnh tại chợ đầu mối Hóc Môn (TP.Hồ Chí Minh).\r\n        Ảnh: Nguyên Vỹ </p>\r\n\r\n    <p>Anh Quyền cũng chia sẻ thêm, hiện trang trại của anh đang nuôi 60 lợn nái và vài trăm lợn thịt. Những ngày qua\r\n        anh\r\n        quán triệt việc cấm trại cực kì cẩn thận, \"nội bất xuất, ngoại bất nhập\" nên đàn lợn vẫn phát triển khoẻ mạnh,\r\n        trong khi bán kính xung quanh khoảng 3-4km, nhiều hộ đã bị dịch tả lợn châu Phi càn quét không còn con lợn nào.\r\n        Với giá lợn hơi như hiện nay là các chủ trang trại đã có lãi khá cao.</p>\r\n\r\n    <p>Tin vui là tại chợ đầu mối TP.Hồ Chí Minh, lượng heo hơi về chợ khoảng 4.800 con, thương lái bán rất chạy. Trước\r\n        đó,\r\n        ngày 4/6 lượng heo hơi về chợ 5.500 con, tình hình tiêu thụ của thương lái không thuận lợi. </p>\r\n    <p>Được biết, tại Chi nhánh CP miền Nam, giá heo hơi hôm nay 6/6 cũng tiếp tục được điều chỉnh. Theo đó, giá heo hơi\r\n        3 máu\r\n        cái tại trại là 37.500 đồng/kg, heo 2 máu cái ở mức 37.000 đồng/kg, heo 2 máu đực là 34.000 đồng/kg.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/ThiTruong24h%2F1%2F24h-1.jpg?alt=media&token=53578fc7-7073-47b0-b302-b6b02ca784e8'),
(9, 'Làm giàu không khó: Rinh ngay 5 lượng vàng từ Coca-Cola!', 'Một lượng vàng mỗi ngày đã “hot”, năm lượng vàng cho “ngày đặc\r\n            biệt” lại càng “nóng bỏng tay” gấp bội.', 10, 'Thị trường 24h', '<p style=\"font-weight: bold\">Gắn kết người Việt từ những giá trị Việt và giải thưởng “khủng”</p>\r\n    <p>Diễn ra từ ngày 25/04 đến 23/07, hàng ngàn người đã nhập cuộc săn vàng trong chương trình khuyến mãi\r\n        “Hứng khởi Việt Nam”. Chỉ cần tìm mã tham dự dưới nắp khoen lon và nhắn tin về tổng đài 8069 hoặc\r\n        trang Zalo chính thức của Coca-Cola, khách hàng đã nhận được một “vé” quay số trúng thưởng và có\r\n        cơ hội trở thành “triệu phú vàng” tiếp theo. Trong khi giải mỗi ngày - nắp khoén 1 lượng vàng được\r\n        công bố định kỳ tại fanpage Coca-Cola, giới trẻ vẫn không ngừng sôi sục hướng tới mục tiêu “khủng”\r\n        hơn là giải tháng - nắp khoén trị giá lên đến 5 lượng vàng, sẽ được quay số may mắn vào ngày 10/06 sắp tới.</p>\r\n\r\n    <p>Tuy nhiên, chương trình khuyến mãi “Hứng khởi Việt Nam” chỉ duy nhất đi kèm bộ lon đặc biệt “Việt Nam tôi yêu\r\n        , Coca-Cola tôi yêu”. Bên cạnh giải thưởng lớn khiến ai cũng muốn lao mình vào “cuộc săn”, những hình ảnh giản\r\n        đơn, gần gũi nhưng sống mãi trong lòng người Việt như xe máy, gánh hàng rong, chợ nổi… cùng hình ảnh của 6 thành\r\n        phố lớn Việt Nam được tôn vinh trên thiết kế lon cũng là nguyên nhân giúp bộ lon “Việt Nam tôi yêu, Coca-Cola\r\n        tôi yêu” trở nên đáng khao khát hơn bao giờ hết.</p>\r\n    <p>Anh Trương Văn Đạt (TP.HCM) – một “gương mặt vàng” của Coca-Cola, trong buổi trao giải, bênh cạnh những chia sẻ\r\n        về cảm xúc của mình, anh còn say mê kể về ký ức riêng của mình với chiếc xích lô, một trong sáu mẫu thiết kế\r\n        đặc biệt lần này mà anh vô cùng ấn tượng: “Hồi nhỏ mình thích nhất là được bon bon trên chiếc xích lô. Đó là\r\n        phương tiện “ngầu” và đặc biệt nhất trong ký ức của mình. Đi khắp cả thế giới, nhìn thấy chiếc xích lô thì biết\r\n        ngay đó là quê hương Việt Nam, không lẫn vào đâu được”.</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/ThiTruong24h%2F2%2F1.jpg?alt=media&token=6412a58e-b8a9-4389-9e20-d3fe72774ee4\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Ông Tôn Thất Đề (giữa) - Giám đốc thương mại toàn quốc Coca-Cola\r\n        Việt Nam trao thưởng cho anh Trương Văn Đạt (trái) và anh Nguyễn Đăng Nhung (phải).</p>\r\n\r\n    <p>“Mỗi lần cầm lon hình Cầu Rồng trên tay, mình lại nhớ về quê hương Đà Nẵng” – Anh Nguyễn Đăng Nhung,\r\n        một chủ nhân khác của nắp khoén Coca-Cola trị giá 1 lượng vàng bồi hồi. “Coca-Cola cũng là “nhân vật”\r\n        đã cùng mình và bạn bè chia vui khi đội tuyển Việt Nam vô địch AFF cup. Đó là cảm giác thực sự rất khó quên!”.\r\n    </p>\r\n\r\n    <p style=\"font-weight: bold\">5 lượng vàng về tay: Không bây giờ thì bao giờ?</p>\r\n    <p>Hàng ngàn “thợ săn” trong hành trình trở thành “triệu phú vàng” đều đang rạo rực mong đợi ngày 10/06\r\n        và 10/07 – hai ngày tiếp theo mà Coca-Cola quay số trao giải thưởng 5 lượng vàng. Khách hàng chưa may\r\n        mắn hãy xốc lại tinh thần và tiếp tục “săn lon” vì biết đâu mã tham dự chưa trúng giải đang ẩn chứa vàng\r\n        trong những lần quay số sắp tới. Những ai vẫn chưa biết đến “bí kíp làm giàu không khó” này, nếu không\r\n        phải bây giờ, liệu bạn sẽ có cơ hội nào khác để sở hữu “khối tài sản” 5 lượng vàng?</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/ThiTruong24h%2F2%2F1.jpg?alt=media&token=6412a58e-b8a9-4389-9e20-d3fe72774ee4\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Chủ nhân tiếp theo của nắp khoén vàng được “vinh danh” bên\r\n        cạnh giải thưởng giá trị rất có thể sẽ là bạn</p>\r\n    <p>Với thể lệ đơn giản, giải thưởng khổng lồ, “cuộc săn” dù đã diễn ra hơn một tháng với trên 30 người\r\n        “về đích” nhưng vẫn chưa khi nào hạ nhiệt. Nếu còn chần chừ không tham gia, chắc chắn bạn sẽ vô cùng hối tiếc!\r\n    </p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/ThiTruong24h%2F2%2F1.jpg?alt=media&token=6412a58e-b8a9-4389-9e20-d3fe72774ee4'),
(10, '6 hiểu lầm tai hại về “cậu nhỏ” nhưng 90% dân số tin là thật', 'Dưới đây là những hiểu lầm phổ biến và sự thật về cậu nhỏ có thể khiến bạn ngạc nhiên.', 12, 'Bệnh đàn ông', '<p style=\"font-weight: bold\">Bàn chân to, kích thước cậu nhỏ cũng to</p>\r\n    <p>Dù bạn tin hay không nhưng kích thước cậu nhỏ không liên quan nhiều với cỡ giày hoặc kích thước của bất kỳ phần\r\n        nào khác của cơ thể. Năm 1993, hai bác sĩ Canada đã đo chiều cao, kích thước bàn chân, và chiều dài dương vật\r\n        khi\r\n        kéo nhẹ của 63 người đàn ông, và mặc dù chiều dài của cậu nhỏ có liên quan đến cả chiều cao và kích thước của\r\n        bàn\r\n        chân nhưng mối tương quan này là khá yếu. Một lần nữa, vào năm 2002, một nghiên cứu từ London đã đo cậu nhỏ và\r\n        bàn\r\n        chân của 104 người đàn ông và thấy không có mối tương quan nào. Về cơ bản, nghiên cứu đã xác định rằng cách duy\r\n        nhất\r\n        để thực sự đánh giá kích thước cậu nhỏ là “nhìn tận mắt”.</p>\r\n\r\n    <p style=\"font-weight: bold\">Lớn hơn luôn tốt hơn</p>\r\n\r\n    <p>Các cậu nhỏ thường không khác nhau nhiều về kích thước. Theo Medical News Today, kích thước cậu nhỏ khi xìu là từ\r\n        khoảng 7-10cm.\r\n        Kích thước cậu nhỏ khi cương là 12-16cm. Theo Medical News Today, trung bình 85% phụ nữ cho biết hài lòng với\r\n        kích thước cậu nhỏ\r\n        của bạn tình. Tuy nhiên, nhiều người cho rằng “cậu nhỏ lớn hơn vẫn tốt hơn”. Và mặc dù sự thật là một số phụ nữ\r\n        thích quan hệ với\r\n        nam giới có “cậu nhỏ hùng dũng” nhưng cậu nhỏ “quá khổ” cũng có nhiều hạn chế.</p>\r\n    <p>Ví dụ, có một mối liên kết thú vị giữa kích thước dương vật và ngoại tình; một nghiên cứu năm 2014 chỉ ra rằng\r\n        phụ nữ trong một ngôi\r\n        làng ở Kenya có nhiều khả năng lừa dối người đàn ông có dương vật lớn hơn. Các nhà nghiên cứu phát hiện ra rằng\r\n        \"cậu nhỏ cứ tăng một\r\n        inch (tương đương với 2,54cm) chiều dài thì nguy cơ ngoại tình của “đối tác” tăng 1,5 lần.</p>\r\n    <p>Theo nghiên cứu này, lý do mà phụ nữ có nhiều khả năng không chung thủy với người đàn ông có cậu\r\n        nhỏ “quá khổ” là vì họ bị đau đớn khi quan hệ.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BenhDanOng%2F1%2FSKdanong.jpg?alt=media&token=97b56949-c930-4299-9aaa-3ef3fecf6420\"\r\n        alt=\"\">\r\n\r\n    <p style=\"font-weight: bold\">Cậu nhỏ vẫn cương lên sau khi “yêu” là muốn “chiến đấu” tiếp</p>\r\n    <p>Trên thực tế, để cậu nhỏ “chiến đấu” liên tục trong một đêm không phải là điều dễ dàng. Nam giới cần một khoảng\r\n        thời gian\r\n        để có thể tiếp tục “hiệp 2” và chỉ vì cậu nhỏ vẫn cương sau khi vừa kết thúc “hiệp 1” không có nghĩa là cậu nhỏ\r\n        đã sẵn\r\n        sàng chiến đấu tiếp.</p>\r\n    <p>Theo Women’s Health, thời gian cậu nhỏ duy trì cương cứng phụ thuộc vào chất lượng lưu thông máu của cậu nhỏ, mức\r\n        độ hưng\r\n        phấn hay việc có hay không dùng thuốc trợ giúp. Nhưng cho dù là 5 phút hay hàng nửa giờ thì tất cả đàn ông đều\r\n        cần một\r\n        khoảng thời gian phục hồi giữa các “hiệp”, bất kể cậu nhỏ còn cương cứng hay không.</p>\r\n\r\n    <p style=\"font-weight: bold\">Xuất tinh một lượng lớn tinh dịch</p>\r\n    <p>Một hiểu lầm phổ biến nữa là đàn ông thường xuất tinh nhiều tinh dịch khi đạt cực khoái. Trên thực tế thì\r\n        nam giới xuất tinh khoảng một thìa cà phê tinh dịch mỗi khi cực khoái. Và một thìa cà phê này bình thường\r\n        chứa khoảng 200 triệu tinh trùng trên mỗi ml tinh dịch. Tuy nhiên, nếu xuất tinh lượng dịch ít hơn có thể\r\n        là dấu hiệu của một vấn đề sức khỏe tiềm ẩn.</p>\r\n    <p style=\"font-weight: bold\">Kích thước cậu nhỏ thay đổi theo chủng tộc</p>\r\n    <p>Trong năm 2015, các nhà nghiên cứu Anh đã đo cậu nhỏ của hơn 15.000 người từ khắp nơi trên thế giới và xác\r\n        định rằng hoàn toàn không có sự tương quan giữa chủng tộc và kích thước cậu nhỏ. Theo các nghiên cứu, chiều\r\n        dài trung bình khi cương cứng của cậu nhỏ ở mỗi quốc gia là chỉ hơn 13 cm. Và mặc dù nghiên cứu này có một\r\n        số khó khăn (ví dụ, đa số nam giới trong nghiên cứu này là da trắng), các nghiên cứu khác cũng đã xác định\r\n        rằng mối liên quan giữa kích thước dương vật và chủng tộc dựa trên cảm tính hơn là khoa học thực tế.</p>\r\n    <p style=\"font-weight: bold\">Thuốc hoặc kem bôi có thể khiến cậu nhỏ lớn hơn</p>\r\n    <p>Nam giới cố gắng tăng kích thước của dương vật của họ trong nhiều thế kỷ nhưng vẫn không tìm ra viên thuốc\r\n        hay loại kem thần kì nào có thể làm tăng kích thước cậu nhỏ. Chế phẩm bổ sung và các thuốc như Viagra có thể\r\n        tăng oxit nitric, cải thiện lưu lượng máu. Tuy nhiên, điều này giống như là \"bơm lốp xe\" và không ảnh hưởng\r\n        đến kích thước chung của dương vật.</p>\r\n    <p>Mặc dù có vô số thuốc tăng kích cỡ được rao bán trên Internet và tại các cửa hàng thuốc, chưa có bằng chứng khoa\r\n        học ủng hộ hiệu quả của chúng, và trên thực tế, chúng thực sự có thể gây hại nhiều hơn lợi. Một khảo sát trên\r\n        Internet được tiến hành bởi FDA trong năm 2009 cho thấy 1/3 các chất bổ sung được khẳng định là có tác dụng cải\r\n        thiện hoạt động tình dục có chứa các thành phần thuốc tân dược hoặc các chất tương tự nhưng không được ghi trên\r\n        nhãn.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BenhDanOng%2F1%2FSKdanong.jpg?alt=media&token=97b56949-c930-4299-9aaa-3ef3fecf6420'),
(11, '\"Mất hứng\": Thắc mắc của những cô vợ trẻ', 'Nhiều phụ nữ trẻ\r\n            mới lập gia đình đã thấy ngán ngẩm tình dục, không còn cảm giác hứng thú, hưng phấn mỗi khi gần chồng…', 13, 'Bệnh phụ nữ', '<p>Theo nghiên cứu, phụ nữ tuổi 30 có nhu cầu và được hưởng khoái cảm tình dục nhiều hơn mọi lứa tuổi khác trong\r\n        cuộc đời bởi nhiều lý do: tự tin về cơ thể mình hơn và cũng ý thức hơn về nhu cầu và quyền lợi của mình; dám\r\n        chia sẻ với bạn tình về nhu cầu đó mạnh dạn hơn. Tuy nhiên, dù phụ nữ có nhiều điểm chung về đáp ứng và cảm\r\n        nhận tình dục nhưng mỗi người đều có những đặc điểm riêng. Cũng không nên coi là sau tuổi 30 thì mọi sự đều\r\n        xuống dốc, tuổi nhiều hơn thì tần suất quan hệ tình dục có thể giảm đi nhưng lại tăng lên về chất lượng.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BenhPhuNu%2F1%2Fskpn.jpg?alt=media&token=4dc77c65-6048-4105-89ec-1697b77f9bfb\"\r\n        alt=\"\">\r\n\r\n    <p>Vậy tại sao phụ nữ trong những điều kiện bình thường lại không thể có hưng phấn tình dục? Rối loạn này phần lớn\r\n        do có xung đột tinh tế về tâm lý trong mối quan hệ; nhiều khi chưa cần đến sự giúp đỡ của chuyên gia tâm lý mà\r\n        những chia sẻ chân thành giữa bạn bè hay người thân đã có thể giúp giải tỏa hay tìm ra nguyên nhân đích thực.\r\n        Nếu kết hợp với không có ham muốn thì có thể do có quá nhiều hormon prolactin, tuy hiếm nhưng cần làm test\r\n        (xét nghiệm) để loại trừ.</p>\r\n    <p>Xác định nguyên nhân của rối loạn là xem lại những điều kiện nào đã cản trở việc phát động cơ chế gây ra cảm xúc\r\n        tình dục (ví dụ có bệnh thực thể, suy buồng trứng sớm... thầy thuốc có thể phát hiện) nhưng cũng có thể do\r\n        nguyên\r\n        nhân tâm lý. Ngày nay, người ta đã khám phá ra rằng, dục năng (ham muốn tình dục), hưng phấn tình dục, khoái\r\n        cảm hay cảm giác đỉnh điểm là cả một cơ chế phức tạp của cảm xúc và được chi phối bởi hoạt động của các tuyến\r\n        nội tiết, nhưng những yếu tố tâm lý cũng có vai trò quan trọng làm cho phụ nữ gặp khó khăn về đáp ứng tình dục.\r\n        Người phụ nữ có thể tự trả lời một số câu hỏi trước khi tìm đến thầy thuốc hay chuyên gia tâm lý:</p>\r\n\r\n    <p style=\"font-weight: bold\">Có phải do sợ có thai?</p>\r\n    <p>Sự lo hãi này - dù có ý thức hay vô thức - cũng có thể ức chế ham muốn hay làm mất cảm giác đỉnh điểm, nhất là\r\n        với nhiều phụ nữ tin tuyệt đối rằng khoái cực là yếu tố chủ yếu gây ra có thai. Nhiều người sợ có thai đến mức\r\n        chỉ nghĩ đến thai nghén là đủ ức chế cung phản xạ dẫn đến khoái cực.</p>\r\n    <p>Có phải vì gặp sự cố trong đời sống (việc tang, thay đổi công việc...)? Mọi sự cố đều có thể ảnh hưởng đến đáp\r\n        ứng tình dục, bắt đầu từ khó khăn về ham muốn cho đến không thể đạt đến đỉnh điểm.</p>\r\n    <p style=\"font-weight: bold\">Có phải tình yêu đã suy giảm?</p>\r\n    <p>Điều kiện quan trọng nhất để có ham muốn tình dục và đòi hỏi nhiều yếu tố tinh tế phải được thường xuyên nâng\r\n        lên để phù hợp với nhau trong cuộc sống (ví dụ sự hiểu biết, sự quan tâm, săn sóc nhau, kỹ năng biết trao đổi\r\n        một cách tế nhị, cùng chia sẻ những sở thích...).</p>\r\n    <p>Khi không tự tìm ra lý do suy giảm cảm xúc tình dục thì cần tìm đến sự giúp đỡ của thầy thuốc hay chuyên gia tâm\r\n        lý.</p>\r\n    <p style=\"font-weight: bold\">Còn đáp ứng tình dục khi hành kinh?</p>\r\n    <p>Có tính cá biệt rất lớn. Một nghiên cứu vào năm 2004 cho thấy, phụ nữ có xu hướng hoạt động tình dục nhiều hơn\r\n        vào\r\n        thời điểm trước và trong giai đoạn rụng trứng (diễn ra từ ngày thứ 10 đến ngày 15 của chu kỳ kinh 30 ngày) khi\r\n        nồng\r\n        độ hormon tạo thể vàng (LH) tăng lên. Điều này như hàm nghĩa rằng cơ thể phụ nữ đã được mã hóa về gen học để\r\n        thuận\r\n        lợi cho sự sinh sản.</p>\r\n    <p>Thế nhưng, nhiều nghiên cứu khác lại thấy phụ nữ có nhiều triệu chứng không thuận lợi cho cảm xúc tình dục trước\r\n        khi hành kinh (cảm giác nặng nề, nhức đầu, tính tình thay đổi...), vì thế ít quan tâm đến tình dục hơn so với\r\n        thời\r\n        điểm khác trong chu kỳ. Xem ra, mỗi phụ nữ có cảm xúc khác nhau, không ai giống ai. Có nhiều lý do giải thích\r\n        phụ\r\n        nữ giảm quan tâm đến tình dục khi hành kinh, đó là do sự co thắt đau khi hành kinh, ra kinh nhiều hoặc cảm giác\r\n        nề,\r\n        giữ nước làm cho phụ nữ không thấy thoải mái.</p>\r\n    <p>Nếu cảm thấy giảm ham muốn khi hành kinh thì đó là vấn đề riêng của mỗi người nhưng cũng nên tư vấn thầy thuốc vì\r\n        giảm ham muốn có thể là dấu hiệu sớm của một số vấn đề sức khỏe.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BenhPhuNu%2F1%2Fskpn.jpg?alt=media&token=4dc77c65-6048-4105-89ec-1697b77f9bfb'),
(12, 'Trung Quốc: Suýt chết vì truyền trực tiếp nước ép trái cây vào máu', 'Một phụ nữ ở Trung Quốc đã suýt chết, sau khi cô truyền nước ép trái cây\r\n            trực tiếp vào mạch máu để cải thiện sức khỏe.', 14, 'Sức khỏe dinh dưỡng', '<img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SucKhoeDinhDuong%2F1%2Fskdd.jpg?alt=media&token=afcb338f-e29c-499d-9bbd-ef4a0ab77fe0\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Bà Zeng suýt chết vì truyền trực tiếp nước ép trái cây vào máu.</p>\r\n\r\n    <p>Người phụ nữ 51 tuổi tên Zeng, tại thành phố Quý Dương thuộc tỉnh Quý Châu, Trung Quốc, đã tự chế nước ép hỗn hợp\r\n        từ 20 loại trái cây khác nhau.</p>\r\n    <p> Sau đó, bà Zeng tự truyền nước ép trái cây tổng hợp vào mạch máu của mình bằng\r\n        cách sử dụng dụng cụ truyền nước y tế.</p>\r\n\r\n    <p>Theo tờ Xiaoxiang Morning Herald, bà Zeng cảm thấy ngứa và sốt suốt đêm sau khi truyền nước ép trái cây vào cơ\r\n        thể.</p>\r\n    <p>Khi chồng của bà Zeng thấy vợ có biểu hiện khó chịu và phát hiện dụng cụ truyền nước,\r\n        ông đã đưa vợ tới bệnh viện trường đại học Xiangnan để cấp cứu.</p>\r\n    <p> “Tôi nghĩ rằng trái cây tươi chứa nhiều chất dinh dưỡng và việc truyền chúng trực tiếp vào cơ thể sẽ tốt cho sức\r\n        khỏe”, bà Zeng nói.</p>\r\n    <p>Bác sĩ cho biết bà Zeng đã bị nhiễm trùng nặng, gây tổn thương gan, thận và tim. Bệnh nhân suýt tử vong do suy đa\r\n        tạng.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SucKhoeDinhDuong%2F1%2Fskdd.jpg?alt=media&token=afcb338f-e29c-499d-9bbd-ef4a0ab77fe0'),
(13, 'Muốn gan khỏe mạnh, bạn nên ăn những thực phẩm vàng dưới đây', 'Nếu ai đó đang tìm kiếm một loại thực phẩm giải độc gan, thanh nhiệt\r\n            tốt thì đừng bỏ quên những loại thực phẩm dưới đây.', 14, 'Sức khỏe dinh dưỡng', '<img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SucKhoeDinhDuong%2F2%2F1.jpg?alt=media&token=4200d6bf-7c76-49da-98df-fb9c7ce126fa\"\r\n        alt=\"\" class=\"banner\">\r\n    <p>PGS.TS.Nguyễn Duy Thịnh, Khoa Công nghệ thực phẩm, Đại học Bách Khoa Hà Nội cho biết, gan như\r\n        kho dự trữ dinh dưỡng của cơ thể. Muốn khỏe mạnh thì phải giữ gìn gan cẩn thận. Chỉ cần hỏng\r\n        gan thì các bộ phận khác sớm bị tàn phá nhanh nhất. PGS.TS.Nguyễn Duy Thịnh khuyến cáo, nếu\r\n        ai đó đang tìm kiếm một loại thực phẩm giải độc gan, thanh nhiệt tốt thì đừng bỏ quên những\r\n        loại thực phẩm dưới đây. </p>\r\n\r\n    <p style=\"font-weight: bold\">Rau xanh, sạch</p>\r\n    <img src=\"./1.jpg\" alt=\"\">\r\n    <p>Rau lá xanh, sạch là một trong những loại thực phẩm có khả năng giải độc gan.\r\n        Loại rau này có thể ăn sống hoặc nấu chín hoặc ép nước uống. Rau xanh chứa nhiều loại vitamin,\r\n        giá trị dinh dưỡng cao. Đặc biệt chất diệp lục trong rau xanh cực kỳ tốt cho gan.</p>\r\n    <p>Bên cạnh đó, rau xanh còn có khả năng loại bỏ chất độc trong máu, bảo vệ chức năng gan cực kỳ hiệu quả.</p>\r\n    <p style=\"font-weight: bold\">Cam, quýt, bưởi, chanh.</p>\r\n\r\n    <p>Cam, quýt, bưởi chứa nhiều vitamin C và chất chống oxy hóa, giúp tăng quá trình thanh lọc chất độc của gan.\r\n        Một nước cam, quýt, bưởi giúp đẩy mạnh quá trình tạo thành các enzyme giải độc gan và đào thải những độc tố\r\n        khác.</p>\r\n    <p>Ngoài ra, trong cam, quýt, bưởi, chanh còn chứa các chất giúp chuyển hóa các chất độc hại thành dạng dễ tan trong\r\n        nước và dễ dàng đào thải ra ngoài.</p>\r\n\r\n    <p style=\"font-weight: bold\">Cà rốt</p>\r\n    <p>Cà rốt chứa nhiều beta-carotene, giúp tăng cường sức khỏe của gan, điều chỉnh mức đường huyết\r\n        và giảm tình trạng viêm bên trong cơ thể.</p>\r\n    <p style=\"font-weight: bold\">Quả bơ</p>\r\n    <p>Ăn nhiều bơ giúp cơ thể sản xuất ra chất chống oxy hóa, giúp loại bỏ các độc tố và chất độc ra khỏi cơ thể, đặc\r\n        biệt là gan.</p>\r\n    <p style=\"font-weight: bold\">Actiso</p>\r\n    <p>Thường xuyên dùng actiso sẽ giúp tăng cường quá trình lọc các chất độc giúp mát gan, gan luôn khỏe mạnh.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SucKhoeDinhDuong%2F2%2F1.jpg?alt=media&token=4200d6bf-7c76-49da-98df-fb9c7ce126fa');
INSERT INTO `article` (`id`, `title`, `description`, `category_id`, `catname`, `content`, `status`, `ispremium`, `create_at`, `publish_at`, `view`, `avatar`) VALUES
(14, '>8 món ngon giúp trị bệnh từ trứng gà', 'Trứng gà là một trong những thực phẩm bổ dưỡng được nhiều người sử dụng.', 15, 'Bài thuốc dân gian', '<p style=\"font-weight: bold\">1. Trứng gà đúc lá mơ</p>\r\n                <p>Trứng gà, lá mơ tươi thái nhỏ, hành, dầu ăn, mắm muối gia vị vừa đủ trộn đều với trứng rán ăn tuần vài lần.\r\n                     Các vị phối hợp thành món ăn ngon bổ. Trị đi kiết đau bụng, viêm dạ dày ruột, trẻ em cam tích, tiêu hoá kém,\r\n                      viêm gan vàng da, nổi mụn ngứa gãi, phong thấp nhức mỏi.</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F1%2F1.jpg?alt=media&token=70a8fd70-24f5-41b1-a30d-b7f4e45fef60\" alt=\"\">\r\n                <p style=\"color: #0040ff; text-align:center;\">Trứng gà đúc lá mơ trị đi kiết đau bụng, viêm dạ dày ruột, \r\n                    trẻ em cam tích, tiêu hóa kém, viêm gan vàng da, nổi mụn ngứa gãi, phong thấp nhức mỏi.</p>\r\n\r\n                <p style=\"font-weight: bold\">2. Mướp đắng xào trứng gà</p>\r\n                <p>Mướp đắng thái lát, trứng gà, hành ngò, mắm muối gia vị vừa đủ xào ăn. Món ngon bổ mát bổ thận kiện tỳ, \r\n                    nhuận tràng, chữa chứng nội nhiệt miệng khô khát, táo bón, tả lỵ, mụn nhọt rôm sẩy, đái tháo đường, gút.</p>\r\n                \r\n                <p style=\"font-weight: bold\">3. Trứng gà ngải cứu</p>\r\n                <p>Trứng gà, ngải cứu tươi thái nhỏ, thịt nạc băm, hành tím, dầu ăn, mắm muối gia vị vừa đủ trộn đều với trứng rán ăn.\r\n                     Món ngon bổ, trị các chứng đau bụng do hàn, kinh nguyệt bế, có thai ra huyết, xuất huyết do yếu và hàn, đặc biệt \r\n                     là chảy máu tử cung.</p>\r\n\r\n                <p style=\"font-weight: bold\">4. Đậu phụ rán trứng gà</p>\r\n                <p>Đậu phụ cắt miếng, trứng gà, hành tây, cà chua thái lát, bột chiên, dầu ăn, tiêu, tỏi gia vị vừa đủ trộn đều rán chín ăn.\r\n                     Món ngon tác dụng bổ mát, trị chứng âm hư hỏa vượng đau đầu chóng mặt, miệng khô khát, da khô sần, tiểu đường, \r\n                     trẻ em gầy còi cọc, người lớn khó lên cân đều dùng rất tốt.</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F1%2F2.jpg?alt=media&token=fa08ef57-c4ab-42e3-a4f7-4a639e790f4f\" alt=\"\">\r\n                <p style=\"color: #0040ff; text-align:center;\">Đậu phụ rán trứng gà</p>\r\n\r\n                <p style=\"font-weight: bold\">5. Canh rau hẹ trứng gà</p>\r\n                <p>rau hẹ, trứng gà, cà chua, rau mùi, dầu ăn, muối gia vị vừa đủ nấu canh ăn. Món ngon, tác dụng bổ tỳ thận. \r\n                    Trị thận yếu, đau lưng, di mộng tinh sớm, dị ứng, rôm sẩy, nổi mề đay, viêm họng, ho, hen, tiêu hóa kém, \r\n                    nhiệt lỵ, trĩ, tiểu đường, bệnh gút, táo bón dùng đều tốt.</p>\r\n\r\n                <p style=\"font-weight: bold\">6. Canh bông bí nhồi trứng thịt</p>\r\n                <p>bông bí tươi, trứng gà, thịt heo, mộc nhĩ, nấm hương, hành lá, dầu ăn, tiêu, gia vị vừa đủ, các vị \r\n                    băm nhỏ nhồi vào bông bí nấu canh, hoặc chiên ăn. Món ngon bổ, trị chứng trẻ em gầy nóng, rôm sẩy,\r\n                     nhiều mồ hôi, người lớn huyết hư tay chân tê mỏi, tiểu đường đều dùng tốt.</p>\r\n\r\n                <p style=\"font-weight: bold\">7. Canh trứng gà</p>\r\n                <p>trứng gà, cà chua, hành, ngò, tiêu, nước gia vị vừa đủ nấu canh ăn. Món ngon bổ mát tỳ vị, tác dụng giúp\r\n                     trị tỳ vị nhiệt miệng táo bón kiết lỵ, da khô sần rôm sẩy, miệng khô khát, tiểu đường, thống phong, \r\n                     có thai nóng nhiệt.</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F1%2F3.jpg?alt=media&token=cd9e5017-3757-4a97-b722-0c9fecd77bca\" alt=\"\">\r\n                <p style=\"color: #0040ff; text-align:center;\">Canh hẹ nấu trứng gà</p>\r\n\r\n                <p style=\"font-weight: bold\">8. Súp trứng gà nấu nấm</p>\r\n                <p>trứng gà, thịt nạc lợn, cà chua, nấm hương, bột bắp ngô non, dầu mè, hành ngò gia vị vừa đủ nấu súp. \r\n                    Món ngon bổ, trị chứng trẻ em gầy nóng, rôm sẩy, nhiều mồ hôi, người lớn huyết hư, tay chân tê mỏi, \r\n                    tiểu đường, huyết áp cao, ăn ngủ kém.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F1%2F1.jpg?alt=media&token=70a8fd70-24f5-41b1-a30d-b7f4e45fef60'),
(15, 'Vừa rẻ vừa ngon, nhưng ít ai biết được đu đủ lại tốt cho sức khỏe đến vậy', 'Mùa nóng này chỉ cần tuần ăn vài ly sinh tố đu đủ là đã giảm được\r\n            rất nhiều rủi ro về bệnh tật rồi.', 15, 'Bài thuốc dân gian', '<p>Đu đủ chứa hàm lượng chất dinh dưỡng cao, giàu khoáng chất và vitamin như photpho, đồng, kali, sắt, canxi.\r\n        Bên cạnh đó, đu đủ cũng giàu chất xơ, chất chống oxi hóa. Lượng đường trong đu đủ chín cũng rất dễ hấp thụ,\r\n        cung cấp năng lượng cho cơ thể nhưng lại chứa lượng calo rất thấp, không lo bị béo.</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F2%2F1.jpg?alt=media&token=523db6f2-c665-4a8e-9f51-360960e011b8\"\r\n        alt=\"\">\r\n    <p style=\"font-weight: bold\">1.Cải thiện khả năng tiêu hóa</p>\r\n    <p>Lượng chất xơ cao giúp cho việc tiêu hóa trở nên dễ dàng hơn, ngăn ngừa chứng táo bón. Mỗi ngày chỉ\r\n        cần ăn 1 miếng đu đủ cũng đã có thể thanh lọc được hệ tiêu hóa.</p>\r\n\r\n    <p style=\"font-weight: bold\">2.Giảm viêm</p>\r\n    <p>Với những enzym như papain và chympopapain có trong đu đủ, nó có tác dụng chống viêm,\r\n        giảm khả năng bị các bệnh mãn tính.</p>\r\n\r\n    <p style=\"font-weight: bold\">3.Cung cấp các chất dinh dưỡng quan trọng</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F2%2F2.jpg?alt=media&token=4be0768c-8cbc-412f-ae40-227bd260c106\"\r\n        alt=\"\">\r\n    <p>Đu đủ có một lượng lớn vitamin A, E, C, B và các khoáng chất như kali, đồng...\r\n        Những chất này kết hợp, hỗ trợ nhau trong việc tái tạo tế bào. Đu đủ chứa vitamin\r\n        A rất lớn, có tác dụng tăng cường thị giác, ngăn chặn các bệnh đục nhân mắt.</p>\r\n\r\n    <p style=\"font-weight: bold\">4.Tăng cường hệ miễn dịch</p>\r\n    <p>Vitamin A và C giúp tăng cường khả năng miễn dịch, ngăn chặn sự phát triển của\r\n        các bệnh lây nhiễm như cúm, ho hay nhiễm trùng hô hấp.</p>\r\n    <p style=\"font-weight: bold\">5.Ngăn chặn bệnh tim</p>\r\n    <p>Lượng chất xơ, kali, vitamin dồi dào có trong đu đủ giúp ngăn ngừa các bệnh liên quan đến tim mạch.</p>\r\n    <p style=\"font-weight: bold\">6.Ngăn ngừa sự tích tụ máu</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F2%2F3.jpg?alt=media&token=141bd383-769a-483b-aa15-1d95d704fef2\"\r\n        alt=\"\">\r\n    <p>Những tụ máu trong cơ thể có thể làm chậm lại quá trình tuần hoàn máu, làm tắc động mạch chính.\r\n        Nó có thể dẫn đến các trường hợp nguy hiểm như đau tim hoặc đột quỵ. Đu đủ chứa protein fibrin\r\n        giúp giảm sự tích tụ, tăng khả năng lưu thông máu.</p>\r\n    <p style=\"font-weight: bold\">7.Tốt cho da</p>\r\n    <p>Vitamin E trong đu đủ giúp loại bỏ các tế bào bị tổn thương. Vitamin C và E sẽ tăng cường sự\r\n        sản xuất collagen khiến da mặt trông mịn hơn. Đu đủ chứa một lượng dầu cần thiết để giữ độ ẩm\r\n        cho da, giảm các triệu chứng bệnh liên quan đến da như vẩy nến.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BaiThuocDanGian%2F2%2F1.jpg?alt=media&token=523db6f2-c665-4a8e-9f51-360960e011b8'),
(16, 'Trường Giang bất ngờ đổi nghề \"bán bưởi kiếm cơm\"', 'Hồ Việt Trung tuy khoái chí nhưng tỏ ra hoang mang khi được Trường Giang\r\n            hướng dẫn cách hái bưởi, hái rau.', 17, 'Sao việt', ' <p>Hồ Việt Trung – khách mời của Muốn Ăn Phải Lăn Vào Bếp tuần này khiến Trường Giang vô cùng khoái\r\n        chí bởi sự hài hước duyên dáng và lăn xả đúng chất phụ bếp chuyên nghiệp.</p>\r\n\r\n    <p>Có lẽ từ lúc phát sóng đến giờ, chẳng có khách mời nào phải vất vả lặn lội rong ruổi vài chục km\r\n        để tìm đến địa điểm trải nghiệm của chương trình như Hồ Việt Trung. Cũng vì cái tật nghe\r\n        “ba chớp bá nháng” mà nam ca sĩ đi nhầm đường, một mạch cưỡi mô tô đến nơi cách địa điểm quay hàng chục km.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoViet%2F1%2F1.jpg?alt=media&token=299eea5a-ad02-4013-95e0-9a8843175538\"\r\n        alt=\"\" width=\"700\" height=\"400\">\r\n\r\n    <p>Giữa cái nắng chói chang của vùng quê Đông Nam Bộ, Hồ Việt Trung mệt lả mồ hôi và chớm nở ý định\r\n        “muốn bỏ về luôn”, nhưng sau đó bình tĩnh hồi tâm chuyển ý quyết đến cho bằng được nơi “bếp chính”\r\n        Trường Giang đang chuẩn bị “nồi niêu xoong chảo” đãi mình những món ngon thứ thiệt.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoViet%2F1%2F2.jpg?alt=media&token=d583c02c-0c2e-45e1-b3f9-4c2ef8332f29\"\r\n        alt=\"\" width=\"700\" height=\"400\">\r\n    <p>Đến nơi, chàng ca sĩ nhà ta bất ngờ khi hội ngộ nam danh hài nổi tiếng ở ngay quầy bán bưởi,\r\n        rồi sau đó cùng đàn anh thị phạm vườn bưởi đặc sản với đủ chủng loại.</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoViet%2F1%2F3.jpg?alt=media&token=5d143d05-7802-4459-9542-0d03437f2dbd\"\r\n        alt=\"\" width=\"700\" height=\"400\">\r\n    <p>Vừa nghe bếp chính hướng dẫn cách hái bưởi, vừa được tận tay vào vườn hái các loại rau, Hồ Việt Trung tuy\r\n        khoái chí nhưng tỏ ra hoang mang vì không biết Trường Giang sẽ đãi mình muốn gì mà sao\r\n        chỉ thấy toàn là màu xanh thực vật.</p>\r\n    <p>Quá hiểu tâm lý khách mời, Trường Giang rủ ngay đàn em cắm cần câu tôm. Lần này, Trường Giang giao nhiệm\r\n        vụ cho Hồ Việt Trung phải cố gắng bắt bằng được tôm, vì đó sẽ là nguyên liệu chính để chế biến ra món\r\n        ăn theo đúng mong muốn của nam ca sĩ.</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoViet%2F1%2F4.jpg?alt=media&token=e3d17c51-9989-4e60-a865-17944b09c01d\"\r\n        alt=\"\" width=\"700\" height=\"400\">\r\n    <p>Câu tôm nào phải chuyện đùa, cả 2 ngồi đợi mòn mỏi. Hồ Việt Trung giết thời gian bằng việc nghêu ngao hát,\r\n        còn bếp chính Trường Giang thì tranh thủ pha trò làm khách mời nhiều phen khó đỡ. Thấy tình hình có vẻ không ổn,\r\n        Trường Giang đành “bấm bụng” rút cần không câu nữa mà chạy đi mua cho đỡ mệt mỏi.</p>\r\n    <p>Tập 10 Muốn Ăn Phải Lăn Vào Bếp sẽ được phát sóng trên kênh truyền hình HTV2 lúc 21h30 Chủ Nhật – 09/06/2019.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoViet%2F1%2F3.jpg?alt=media&token=5d143d05-7802-4459-9542-0d03437f2dbd'),
(17, 'Á hậu Trang Nhung lấy chồng đại gia nói gì về việc chuyển hướng bán hàng online?', 'Sau 5 năm lấy chồng đại gia, cuộc sống kín tiếng của Á hậu\r\n            Trang Nhung khiến nhiều người tò mò.', 18, 'Talk cùng sao', '<p>Từ một nữ tiếp viên hàng không đến giải Á hậu Phụ nữ Việt Nam 2005, lọt top 15 siêu mẫu thế giới năm 2006 và\r\n        sau đó lấn sân phim ảnh, ca hát… Trang Nhung khiến nhiều đồng nghiệp kiêng nể, fan ngưỡng mộ và công chúng\r\n        bất ngờ về tài năng. Thế nhưng, ở đỉnh cao sự nghiệp, Trang Nhung lựa chọn kết hôn, làm mẹ bỉm sữa của hai\r\n        nhóc tỳ gây nhiều tiếc nuối.</p>\r\n    <p>Mới đây, người đẹp có ngờ có những chia sẻ mới về kinh doanh riêng nho nhỏ. Trò chuyện với Trang Nhung về\r\n        lĩnh vực mới, chân dài đa zi năng cô tiếp tục gây bất ngờ với những quan điểm truyền thống nhưng không kém phần\r\n        cá tính.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TalkCungSao%2F1%2F1.jpg?alt=media&token=3db17e15-781d-4107-8898-a59855025812\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Trang Nhung vắng bóng showbiz nhưng vẫn được khán giả\r\n        quan tâm và tò mò về cuộc sống riêng</p>\r\n\r\n    <p>- Chào Trang Nhung, sau thời gian khá dài im ắng trong showbiz, gần đây fan thấy chị bắt đầu kinh doanh\r\n        online. Liệu đây có phải là bước đệm cho chị rời làng giải trí để đến với vai trò mới?</p>\r\n    <p>- Tôi vốn có rất nhiều ấp ủ và mơ ước trong tương lai về làm đẹp và thời trang nhưng bản thân\r\n        lại bắt đầu việc bán hàng trên trang cá nhân rất tình cờ. Sau nhiều lần fan và bạn bè vào inbox hỏi\r\n        thăm và nhờ tư vấn về làm đẹp cũng như quần áo, phụ kiện, tôi đã quyết định bán những thứ nho nhỏ\r\n        tô điểm cho chị em phụ nữ. Điều đặc là tôi chỉ thích kinh doanh những thứ mà chỉ có mình có hoặc\r\n        rất ít người có thôi (cười). Thời gian của tôi khá bận rộn vì còn phải chăm lo cho gia đình, con\r\n        cái và phụ giúp chồng kinh doanh nhưng nếu có gì đẹp, lạ thì tôi lại chia sẻ với mọi người. </p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TalkCungSao%2F1%2F2.jpg?alt=media&token=571d06da-670d-48d2-837e-7b9c772759cd\"\r\n        alt=\"\" width=\"700\" height=\"450\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Trang Nhung, đạo diễn Hoàng Duy cùng quan điểm nuôi\r\n        dạy con và trân trọng những điều hướng về gia đình</p>\r\n    <p>- Lấy chồng đạo diễn Hoàng Duy với điều kiện kinh tế sung túc, con thứ 2 còn nhỏ. Lý do nào khiến\r\n        chị quyết định tự lập kinh doanh riêng?</p>\r\n    <p>- Trước đây tôi từng nghĩ, với bản tính mê công việc như này thì nếu có lấy chồng giàu thế nào hoặc\r\n        sau này có là triệu phú hay tỷ phú thì vẫn sẽ làm việc. Việc kiếm ra tiền và nhiều tiền là thước đo\r\n        cho sự thành công còn việc mình thích lao động thì do tính cách con người, mà bản tính thì hiếm khi\r\n        thay đổi lắm. Hơn nữa, tôi thấy mình còn quá trẻ để chỉ quanh quẩn với việc hưởng thụ. Tôi thấy cuộc\r\n        sống như vậy sẽ rất nhàm chán và bản thân vô dụng khi không làm gì. Tất nhiên, khi bắt tay vào kinh\r\n        doanh, tôi sẽ không làm nó ảnh hưởng tới việc chăm lo con cái, gia đình hay ảnh hưởng tới các công việc khác của\r\n        mình.</p>\r\n    <p>Mặc dù có nhân viên giúp tôi trả lời tin nhắn của khách cũng như nhân viên quản lý đơn hàng, phân phối... nhưng\r\n        mỗi lúc có thời\r\n        gian rảnh, tôi vẫn trực tiếp tư vấn cho các thượng đế của mình. Trong quá trình này, tôi rất vui và bất ngờ khi\r\n        phát hiện rằng\r\n        mình vẫn có rất nhiều fan trung thành yêu quý dù đã lui về hậu trường lâu rồi không hoạt động showbiz sôi nổi\r\n        nữa.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TalkCungSao%2F1%2F1.jpg?alt=media&token=3db17e15-781d-4107-8898-a59855025812'),
(18, '8 dị nhân quyền năng nhất vũ trụ X-men: Bất ngờ nhất là người số 4', 'X-Men: Dark Phoenix\" đánh dấu chặng đường dài 20 năm của thương hiệu\r\n                     Vũ trụ X-Men, trong đó gây ấn tượng nhất 8 nhân vật dị nhân dưới đây.', 19, 'Sao Hollywood và các nước khác', '<p>Là một thương hiệu siêu anh hùng kéo dài suốt 20 năm, X-Men ghi dấu ấn trong lòng khán giả \r\n                    nhờ những màn chiến đấu vô cùng hoành tráng của dàn dị Nhân có sức mạnh bá đạo.</p>\r\n                <p>Với X-Men: Dark Phoenix (tựa Việt: X-Men: Phượng Hoàng Bóng Tối), khán giả sẽ được dịp nhìn \r\n                    lại cả một chặng đường đã qua của thương hiệu với 8 cái tên quyền năng bậc nhất vũ trụ X-Men.</p>\r\n                <p style=\"font-weight: bold\">1. Wolverine</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F1.jpg?alt=media&token=79a1f395-6360-40d1-bca7-76497f529085\" alt=\"\">\r\n                <p>Xuất hiện tới 8 trong tổng số 12 phim, Wolverine của Hugh Jackman sở hữu lượng fan đông đảo và được\r\n                     xem là linh hồn của cả X-Men. Nhờ khả năng hồi phục phi thường mà Logan gần như bất tử trước những \r\n                     vũ khí thông thường của con người. Thậm chí, anh chàng còn chịu được cái nóng kinh hoàng của bom \r\n                     nguyên tử trong The Wolverine (2013) mà vẫn sống sót.</p>\r\n                <p>Cũng nhờ sức mạnh này mà anh chàng có tuổi thọ lên đến hàng trăm năm cũng như thực hiện được những\r\n                     nhiệm vụ “bất khả thi” như trở về quá khứ trong X-Men: Days of Future Past (2014).</p>\r\n                <p>Ngoài ra, sức tấn công của Logan cũng không hề yếu nhờ bộ vuốt bằng Adamantium sắc bén nhất \r\n                    thế giới. Wolverine được xem là vũ khí hoàn hảo mà nhiều nhà khoa học đều muốn chế tạo \r\n                    các chiến binh dựa nhân vật này.</p>\r\n\r\n                <p style=\"font-weight: bold\">2. Sebastian Shaw</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F2.jpg?alt=media&token=b4d23591-82aa-4181-8f01-01c384a8caa2\" alt=\"\">\r\n                <p>Xuất hiện trong X-Men: First Class (2011), Sebastian Shaw (Kevin Bacon) nhanh chóng \r\n                    trở thành một trong những ác nhân bá đạo nhất mà nhóm X-Men từng đối mặt.</p>\r\n                <p>Gã sở hữu trí tuệ hiếm có khi có thể thao túng cả hai phe Nga và Mỹ nhằm tạo ra Thế \r\n                    chiến III, cũng như chế tạo ra chiếc mũ bảo hộ đặc trưng khiến nhà ngoại cảm mạnh nhất \r\n                    X-Men là Giáo sư X (James McAvoy) cũng phải bó tay.</p>\r\n                <p>Tuy nhiên, khả năng ghê gớm nhất kẻ phản diện này chính là hấp thụ mọi năng lượng rồi biến \r\n                    thành sức mạnh bản thân. Siêu năng lực này không chỉ giúp Sebastian Shaw trẻ mãi không già \r\n                    mà còn “bất tử” trước mọi loại vũ khí và của sức mạnh của các dị nhân khác. Cả Giáo sư X lẫn \r\n                    Magnato (Michael Fassbender) phải kết hợp thì mới đủ sức hạ gục gã.</p>\r\n                \r\n                <p style=\"font-weight: bold\">3. Storm</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F3.jpg?alt=media&token=71f410f1-7e1a-4c9e-962a-e6fb1eb869ee\" alt=\"\">\r\n                <p>Storm không chỉ là một trong các thành viên đáng tin cậy mà còn sở hữu sức mạnh bậc nhất nhóm X-Men.\r\n                     Không chỉ là khả năng phóng điện thông thường, cô nàng còn có thể tạo ra cả một cơn bão hay lốc \r\n                     xoáy khổng lồ như đúng cái tên của mình.</p>\r\n                <p>Nếu bộc phát toàn bộ sức mạnh, Storm dư sức điều khiển mọi kiểu thời tiết trên diện rộng, điều chỉnh \r\n                    nhiệt độ môi trường, kiểm soát áp suất khí quyển,…</p>\r\n                <p>Sau loạt phim gốc do Halle Berry thủ vai, phiên bản Storm trẻ của nữ diễn viên Alexandra Shipp thủ vai\r\n                     cũng nhận được được nhiều tình cảm của khán giả. Sau màn “lật kèo” cùng nhóm X-Men tiêu diệt Apocalypse \r\n                     (Oscar Isaac), cô nàng sẽ tiếp tục sát cánh cùng đồng đội chống lại kẻ thù mạnh nhất từ trước tới nay là \r\n                     Phoenix Force (Thế lực Phượng hoàng).</p>\r\n\r\n                <p style=\"font-weight: bold\">4. Quicksilver</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F4.jpg?alt=media&token=4a72b98e-561a-4b2f-b5a9-93bb7384393e\" alt=\"\">\r\n                <p>Dù chỉ mới xuất hiện trong X-Men: Days of Future Past nhưng Quicksilver của Evan Peters nhanh chóng sở \r\n                    hữu lượng fan khổng lồ nhờ tính cách tưng tửng vô cùng đáng yêu.</p>\r\n                <p>Siêu năng lực của anh chàng cũng chỉ đơn giản ở việc siêu nhanh mà thôi. Ấy vậy mà sức mạnh này đã \r\n                    cứu cả nhóm X-Men trong rất nhiều tình huống hiểm nghèo. Việc anh có mặt trong danh sách 8 dị nhân\r\n                     quyền năng nhất quả là điều bất ngờ nhưng hoàn toàn hợp lý.</p>\r\n                <p>Mỗi lần trổ tài với những đại cảnh quay chậm hoành tráng cùng nền nhạc ấn tượng đã trở thành món “đặc sản” \r\n                    của riêng Quicksilver. Nhưng liệu anh chàng sẽ đối phó ra sao với một kẻ thù quá đỗi mạnh mẽ như Phoenix \r\n                    Force trong X-Men: Dark Phoenix đây?</p>\r\n\r\n                <p style=\"font-weight: bold\">5. Magneto</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F5.jpg?alt=media&token=31ea007e-2882-48e7-b116-33eb2372f8e5\" alt=\"\">\r\n                <p>Cùng với Giáo sư X, Magneto là nhân vật quan trọng bậc nhất loạt phim X-Men với 2 phiên bản do Ian McKellen \r\n                    và Michael Fassbender thủ vai.</p>\r\n                <p>Khả năng thao túng từ trường khiến anh gần như “vô đối” khi mọi thứ trên đời đều ít nhiều có cấu tạo từ \r\n                    kim loại. Magneto có thể nâng cả một sân vận động hay cầu Cổng Vàng khổng lồ lên không trung, sử dụng xe\r\n                     hơi làm khiên chắn, làm đổi hướng đạn bay, hút chất sắt ra khỏi cơ thể người,…</p>\r\n                <p>Thậm chí, anh còn có thể điều khiển cả những người máy Sentinels được thiết kế để tiêu diệt các dị nhân \r\n                    hay vô hiệu hóa khung xương Adamantium của Wolverine. Không những thế, Magneto còn có lối tư duy chiến \r\n                    thuật ít ai bì kịp nhờ quãng thời gian dài săn đuổi kẻ thù phát xít.</p>\r\n                <p>Sắp tới đây, anh sẽ lại đóng vai trò then chốt trong trận chiến với Phoenix Force khi chọn cách tiêu diệt \r\n                    thay vì bảo vệ Jean Grey như người bạn thân Charles Xavier.</p>\r\n\r\n                <p style=\"font-weight: bold\">6. Giáo sư X</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F6.jpg?alt=media&token=b1ef6365-4f91-4a92-bffe-3c1b5e458fd3\" alt=\"\">\r\n                <p>Dù mất khả năng đi lại thì Charles Xavier vẫn đủ sức trở thành đầu tàu của nhóm X-Men xuyên suốt hàng \r\n                    chục năm trời nhớ trí tuệ thiên tài và lòng cảm thông sâu sắc.</p>\r\n                <p>Được mệnh danh là nhà ngoại cảm quyền năng nhất hành tinh, Giáo sư X có thể thao túng tâm trí, đọc suy nghĩ,\r\n                     mở khóa cả những kí ức bị mất của đối phương. Nếu muốn, anh có thể đóng băng cả một sân bay hay khiến một \r\n                     người trở nên tàng hình trong mắt kẻ khác.</p>\r\n                <p>Thông qua Cerebro, Charles có thể kết nối trí não với toàn bộ nhân loại và thậm chí là tiêu diệt toàn bộ con \r\n                    người hoặc dị nhân nếu muốn. Khi bị Apocalypse thao túng, anh đã điều khiển và phóng toàn bộ vũ khí hạt nhân ra ngoài vũ trụ.</p>\r\n                <p>Giáo sư X sẽ tiếp tục là nhân vật quan trọng bậc nhất trong cuộc chiến với Phoenix Force nhờ khả năng trò chuyện bằng tâm trí \r\n                    với Jean Grey (Sophie Turner).</p>\r\n\r\n                <p style=\"font-weight: bold\">7. Apocalypse</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F7.jpg?alt=media&token=5356db2d-5bd3-42d0-8df3-49387a8646f1\" alt=\"\">\r\n                <p>Trước khi Phoenix Force xuất hiện thì Apocalypse chính là dị nhân bá đạo nhất vụ trụ X-Men. Được xem là kẻ đột biến đầu tiên,\r\n                     En Sabah Nur có thể chuyển từ cơ thể này sang cơ thể khác để cướp sức mạnh cũng như trở nên bất tử.</p>\r\n                <p>Tỉnh dậy sau hàng nghìn năm, gã có thể học toàn bộ tri thức nhân loại chỉ nhờ việc chạm vào TV trong vài phút. Apocalypse còn \r\n                    có thể điều khiển mọi vật tới dạng tế bào và tăng cường sức mạnh các dị nhân Storm, Psylocke (Olivia Munn), Magneto và Angel (Ben Hardy).</p>\r\n                <p>Tuy nhiên, khả năng đáng sợ nhất của tên phản diện này chính là sự thích nghi trong chiến đấu. Đối đầu với những X-Men mạnh \r\n                    mẽ như Quicksilver, Apocalypse có thể dễ dàng bắt kịp tốc độ của anh chàng trong chớp mắt hay dùng chính sức mạnh \r\n                    tâm trí của Giáo sư X để xóa bỏ toàn bộ vũ khí hạt nhân của con người.</p>\r\n                <p>Để tiêu diệt gã, cả nhóm X-Men phải kết hợp và nhờ tới sức mạnh tối thượng của Jean Grey.</p>\r\n\r\n                <p style=\"font-weight: bold\">8. Jean Grey</p>\r\n                <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F8.jpg?alt=media&token=8dc2fe57-72af-41f3-8c5d-c91bc4c84ed4\" alt=\"\">\r\n                <p>Từng xuất hiện trong X-Men: The Last Stand (2006) nhưng chưa được phát huy hết sức mạnh, Jean Grey của X-Men: Dark Phoenix \r\n                    hứa hẹn sẽ là kẻ thù mạnh mẽ nhất mà nhóm X-Men từng đối đầu.</p>\r\n                <p>Trong nguyên tác truyện tranh, Phoenix Force là một thực thể đại diện cho ánh sáng và sự sống của toàn vũ trụ và chọn Jean Grey làm vật chủ \r\n                    vì những tương thông về mặt tâm trí. Nhưng khi bị cảm xúc che mờ lí trí, nó sẽ biến thành Dark Phoenix – hiện thân của bóng tối \r\n                    và sự hủy diệt.</p>\r\n                <p>Chúng ta đã thấy Jean Grey dễ dàng thổi bay Apocalypse khi bộc phát sức mạnh tối đa nhưng tất cả vẫn chẳng là gì so với quyền năng \r\n                    xóa sổ cả một hành tinh trong chớp mắt của Dark Phoenix. Có lẽ, cả nhóm X-Men vẫn là chưa đủ khi họ không chỉ phải đối phó với \r\n                    sức mạnh tà ác bậc nhất mà còn là một người đồng đội từng sát cánh chiến đấu vào sinh ra tử.</p>\r\n                <p>Liệu các dị nhân sẽ lựa chọn ra sao hay dùng cách gì để chiến thắng trận chiến sắp tới đây? X-Men: Dark Phoenix hứa hẹn sẽ \r\n                    là bộ phim hoành tráng và cảm xúc bậc nhất, xứng đáng với chặng đường 20 năm của thương hiệu.</p>\r\n                <p>X-Men: Dark Phoenix (X-Men: Phượng Hoàng Bóng Tối) dự kiến khởi chiếu trên toàn quốc từ ngày 07.06.2019.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F1%2F1.jpg?alt=media&token=79a1f395-6360-40d1-bca7-76497f529085'),
(19, 'Nhan sắc mỹ miều của nàng công chúa \"gây sốt\" nhất màn ảnh hiện nay', 'Vẻ đẹp ấn tượng của một mỹ nhân người Anh mang nét đẹp gốc\r\n            Á tạo nên nét hiện đại cho nàng công chúa Jasmine.', 19, 'Sao Hollywood và các nước khác', '<img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F2%2F1.jpg?alt=media&token=6d276ac9-67ef-4185-ad7d-730231f8aba1\"\r\n        alt=\"\" width=\"700\" height=\"900\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Sau 27 năm kể từ khi bộ phim hoạt hình Aladdin công chiếu,\r\n        phiên bản người đóng mới được ra mắt. Cô công chúa xinh đẹp Jasmine gây sốt toàn cầu vì vẻ đẹp ấn tượng.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F2%2F2.jpg?alt=media&token=49c3fe53-43c0-4baf-a077-579c26400032\"\r\n        alt=\"\" width=\"700\" height=\"500\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Tuy nhiên, khi phim công chiếu, khán giả đã thay đổi\r\n        cách đánh giá này. Naomi toát lên vẻ đẹp vừa thánh thiện vừa thông minh, đúng như những gì\r\n        miêu tả về một công chúa trong truyện Aladdin.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F2%2F3.jpg?alt=media&token=38fd603e-641f-4ce5-a699-d53c4458942a\"\r\n        alt=\"\" width=\"700\" height=\"700\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Bên cạnh gương mặt đẹp hoàn hảo, trang phục của cô\r\n        công chúa này cũng khiến các fan rất ưng ý. Ngay lập tức, tên tuổi của Naomi Scott được tìm kiếm trên mạng xã\r\n        hội.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F2%2F4.jpg?alt=media&token=da2d4f83-87de-4d33-b875-09ff91f747fe\"\r\n        alt=\"\" width=\"700\" height=\"500\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Aladdin là bom tấn đầu tiên cô tham gia đóng vai nữ chính.\r\n        Trong buổi ra mắt phim tại Mỹ, Naomi gây ấn tượng bởi đôi mắt sâu thẳm. Ca khúc A Whole New World\r\n        cũng do Naomi thể hiện trong phim.</p>\r\n\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F2%2F5.jpg?alt=media&token=e4513a30-a28f-4249-91b6-428fbe39956a\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Ngoài ra nữ diễn viên 26 tuổi còn được chọn vào 1 trong\r\n        3 vai chính trong phim Angels làm lại từ phim Những thiên thần của Charlie.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/SaoHollywood%2F2%2F1.jpg?alt=media&token=6d276ac9-67ef-4185-ad7d-730231f8aba1'),
(20, 'Vì sao đội tuyển Việt Nam vô địch AFF Suzuki Cup 2018?', 'Chu kỳ 10 năm đã lặp lại, đội tuyển Việt Nam một lần nữa bước lên ngôi vị\r\n        cao nhất của giải đấu AFF Suzuki Cup 2018.', 2, 'Tin trong nước', '<img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F2%2Fqualuuniem.jpg?alt=media&token=c3346231-a539-49ce-a743-0b121419eded\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Ông Lee Young Kyun nhận quà lưu niệm từ đội tuyển Việt Nam\r\n    </p>\r\n    <p>Nhận xét về thành công ấn tượng của đội tuyển, ông Lee Young Kyun – Tổng giám đốc điều hành Lavifood,\r\n        đại diện cho nhà tài trợ bản quyền phát sóng giải đấu nhận xét: “Hành trình vô địch của đội tuyển\r\n        Việt Nam tại giải đấu lần này một hành trình đầy vinh quang và đáng tự hào. Nó không chỉ thổi bùng\r\n        tình yêu bóng đá mà còn truyền cảm hứng chiến thắng, khát vọng vươn đến đỉnh cao, tinh thần vượt qua\r\n        khó khăn, thử thách đến tất cả mọi người, đặc biệt là những người nông dân vốn yêu bóng đá cuồng\r\n        nhiệt.</p>\r\n\r\n    <p>Cũng như các cầu thủ thi đấu hết mình trên sân cỏ, những người nông dân cũng làm việc miệt mài trên\r\n        đồng ruộng để nông sản Việt Nam giành được những chiếc CUP vô địch về chất lượng trên thị trường\r\n        quốc tế”.</p>\r\n\r\n    <p>Từ vị thế của “kẻ lót đường”, bóng đá Việt Nam đã bất ngờ trở thành á quân U23 Châu Á, trở thành 4\r\n        đội mạnh nhất ASIAD 2018 rồi vững chãi, tự tin tiến đến chức vô địch AFF Suzuki Cup 2018. Điều gì đã\r\n        làm nên thành công tuyệt vời ấy của tuyển Việt Nam?</p>\r\n\r\n    <p style=\"font-weight: bold\">Tính kỉ luật</p>\r\n    <p>Trong suốt quá trình tập luyện và cả quá trình thi đấu cho AFF Cup 2018, đội tuyển Việt Nam đã có sự\r\n        tập trung cao độ và nghiêm túc. Suốt mùa giải, đội tuyển quốc gia Việt Nam không xuất hiện bất kỳ\r\n        một thông tin tiêu cực về tính kỷ luật như bỏ tập, nghỉ tập, đòi từ giã đội tuyển hay từ chối thi\r\n        đấu…</p>\r\n    <p>Tất cả các “chiến binh” áo đỏ đều tập luyện hăng say theo giáo án của Ban huấn luyện, sinh hoạt theo\r\n        lịch của chuyên gia và khi được ra sân thì cống hiến hết mình vì màu cờ sắc áo.</p>\r\n    <p style=\"font-weight: bold\">Sự tuân thủ đấu pháp của HLV</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F2%2FTuanthudauphap.jpg?alt=media&token=fcc44310-c5d9-4733-ad4d-959ef15913a9\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">Tuân thủ là một trong những yếu tố quan trọng để Việt Nam\r\n        lên ngôi vô địch AFF Cup 2018</p>\r\n    <p>Tính tuân thủ của đội tuyển Việt Nam ở giải đấu này là điều đáng khen ngợi. Những chiến lược, đấu\r\n        pháp của HLV đều được cầu thủ tuyệt đối tuân theo ngay cả khi điều này trái với sở trường và thói\r\n        quen thi đấu của họ.</p>\r\n    <p>Đó là lý do vì sao một Quang Hải thuận chân trái, chuyên gia cánh trái ở CLB vẫn tỏa sáng và thi đấu\r\n        đầy năng lượng ở đội tuyển khi phụ trách biên phản.</p>\r\n    <p>Đó là khi một Đức Huy vốn nổi tiếng với vai trò là một tiền vệ công vẫn chơi tốt khi trở thành “mỏ\r\n        neo” ở tuyến giữa. Đó là khi cả đội bóng vốn quen thuộc với sơ đồ 4-4-2 nhưng vẫn tập luyện và tập\r\n        luyện để thành thạo, nhuần nguyễn sơ đồ 3-4-3 hoặc biến thể 5-4-1 (khi cần phòng ngự)…</p>\r\n    <p>Sự tuân thủ này đã là lý do quan trọng để những tính toán chiến thuật của HLV Park Hang Seo phát huy\r\n        được tối đa tác dụng.</p>\r\n    <p style=\"font-weight: bold\">Đoàn kết, đồng thuận</p>\r\n    <p>Đoàn kết, đồng thuận không có nghĩa là cùng nhau làm mọi việc mà còn bao hàm cả việc nhắc nhở, tranh\r\n        luận, đấu tranh với những sai lầm của đồng đội để có được kết quả tốt nhất.</p>\r\n    <p>Đó chính là lý do vì sao người hâm mộ rưng rưng nước mắt khi thấy Công Phượng cõng Văn Toàn, khi thấy\r\n        áo Xuân Trường ghi tên những người đồng đội vắng mặt vì chấn thương, khi Quế Ngọc Hải trao lại băng\r\n        đội trưởng cho Văn Quyết khi nhận Cup vô địch… </p>\r\n    <p>Nhưng người hâm mộ còn vui hơn khi nhìn thấy Quế Ngọc Hải và thủ thành Đặng Văn Lâm “ầm ĩ” với nhau\r\n        trên sân cỏ, song vẫn thân thiết ở đời thường, tất cả chỉ để giúp nhau thi đấu tỉnh táo, tập trung\r\n        hơn.</p>\r\n    <p style=\"font-weight: bold\">Đầu tư cho khoa học thể thao </p>\r\n    <p>Những mùa giải trước, đội tuyển Việt Nam luôn gặp các vấn đề về thể lực. Thi đấu thăng hoa ở hiệp 1\r\n        để rồi hiệp 2 “lê chân” không nổi để kèm người. Nhưng với sự đầu tư cho khoa học thể thao đã giúp\r\n        các cầu thủ chúng ta phát triển thể chất, tối ưu hóa khả năng vận động, xây dựng chế độ dinh dưỡng\r\n        và tập luyện thi đấu phù hợp, phục hồi sau vận động, tâm lý, phòng tránh và trị liệu chấn thương…\r\n    </p>\r\n    <p>Trong khuôn khổ AFF Cup 2018, thể lực đã là một yếu tố không thể thiếu để Việt Nam góp phần làm nên\r\n        chiến thắng của đội tuyển, nhất là khi đối đầu với những đối thủ có thể trạng cao lớn và lối chơi\r\n        càn quét, nghiêng về thể lực như Malaysia, Philippines…</p>\r\n    <p style=\"font-weight: bold\">Tinh thần khát khao chiến thắng</p>\r\n    <img src=\"https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F2%2Fkhaokhatchienthang.jpg?alt=media&token=ebb76135-2e9e-49d0-a300-e8c8d3a5d927\"\r\n        alt=\"\">\r\n    <p style=\"color: #0040ff; text-align:center;\">CĐV Việt Nam hân hoan mừng đội tuyển vô địch AFF Cup 2018\r\n    </p>\r\n    <p>Điều cuối cùng cũng chính là điều quan trọng nhất, là tinh thần khát khao chiến thắng của các cầu\r\n        thủ. Mỗi một trận đấu, các cầu thủ đều thi đấu hết mình vì sự mong mỏi của người hâm mộ, vì niềm\r\n        khát khao 10 năm trời ròng rã, vì màu cờ sắc áo, vì tinh thần dân tộc.</p>\r\n    <p>Điển hình như trận chung kết lượt về, trung vệ Trần Đình Trọng đã dính chấn thương khá nặng nhưng anh\r\n        vẫn nhịn đau để thi đấu. Người hâm mộ không nhìn thấy bất kỳ dấu hiệu nào trên gương mặt, những bước\r\n        chạy hay những pha truy cản vô cùng chính xác của cầu thủ mang áo số 21 này. Chỉ chi tiết ấy thôi\r\n        cũng đủ thấy về sự kiên cường của Đình Trọng nói riêng và tuyển thủ Việt Nam nói chung.</p>\r\n    <p>Cuộc sống vốn là vòng quay bất tận. Chẳng thể có thành công mãi mãi nếu như chúng ta không cố gắng.\r\n        Chỉ sau giải AFF Cup 2018 khoảng 1 tháng, những chiến binh của Việt Nam sẽ bước vào hành trình mới\r\n        hứa hẹn sẽ khốc liệt hơn rất nhiều, đó là Asian Cup 2019, nơi chúng ta sẽ nằm chung bảng với những\r\n        ông lớn như Iran, Iraq và Yemen.</p>\r\n    <p>Đương nhiên, để chinh phục chặng đường phía trước, trong đó bao gồm việc bảo vệ thành công chức vô\r\n        địch AFF Cup 2018, đòi hỏi những cầu thủ cần nỗ lực hơn rất nhiều.</p>', 'new', 0, '2019-06-12', '2019-06-12', 1, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F2%2FVN.jpg?alt=media&token=75648d32-7dd0-40de-97bf-0f84436c6b51'),
(21, 'Google Assistant nhận giọng nói tiếng Việt tốt nhưng chưa tự nhiên', 'Trợ lý giọng nói của Google nhận chính xác giọng nói tiếng Việt, song cách xử lý các tình huống còn cứng nhắc.', 3, 'Tin quốc tế', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\"> Tiếng Việt được bổ sung vào Google Assistant từ ngày 1/5, trở thành trợ lý giọng nói đầu tiên mà người Việt Nam có thể sử dụng bằng ngôn ngữ bản địa. Tuy nhiên hiện tại, người dùng thiết bị Android cần đăng ký chương trình thử nghiệm của Google và các máy iOS phải chuyển tài khoản sang khu vực Mỹ mới có thể trải nghiệm tiếng Việt trên Google Assistant, trước khi dịch vụ này chính thức được giới thiệu tại Việt Nam vào ngày 6/5 tới.</p><p class=\"subtitle\"> <strong>Google Assistant làm được gì?</strong></p><p class=\"Normal\"> Trợ lý giọng nói có thể coi là bản cải tiến của tính năng ra lệnh bằng giọng nói, trong đó ngôn ngữ được xử lý tự nhiên hơn, có sự tương tác giữa người dùng với thiết bị theo cách gần gũi. Trên Assistant, trợ lý này có thể giúp người dùng thao tác mà không cần chạm vào điện thoại như yêu cầu gọi điện hoặc nhắn tin cho một ai đó, hẹn giờ, đặt nhắc nhở, lên lịch hẹn...</p><p class=\"Normal\"> Ngoài ra, người dùng có thể thiết lập các cài đặt trên điện thoại bằng giọng nói thông qua Google Assistant, bao gồm tắt hoặc mở Bluetooth, Wi-Fi, chế độ máy bay, đèn flash, điều chỉnh âm lượng... Trợ lý của Google cũng hỗ trợ ra lệnh để khởi chạy một ứng dụng bất kỳ đã được cài đặt trên smartphone.</p><p class=\"Normal\"> Tìm kiếm là thế mạnh của Google và trên Assistant thì nhiều thông tin được đưa ra dưới dạng câu trả lời bằng giọng nói, đi trực tiếp vào vấn đề thay vì người dùng cần đọc và chọn lọc lấy thông tin mình cần. Nó bao gồm câu hỏi về thời tiết, chỉ đường, tính toán khoảng cách giữa các địa điểm... Trợ lý của Google cũng có thể làm tính, chuyển đổi đại lượng, tính tỷ giá... trả lời các câu&nbsp;hỏi liên quan đến thể thao, du lịch, tìm nhà hàng, trạm xăng...</p><p class=\"Normal\"> Cập nhật ngôn ngữ của Google còn cho phép người dùng điều khiển các thiết bị thông minh trong nhà bằng tiếng Việt. Bạn có thể ra lệnh bật, tắt đèn, tăng giảm độ sáng, kích hoạt robot hút bụi... cũng như các sản phẩm khác hỗ trợ Home - chương trình quản lý nhà thông minh của Google.</p><p class=\"subtitle\"> <strong>Giọng nói tự nhiên nhưng cách xử lý còn \"cứng\"</strong></p><p class=\"Normal\"> Khả năng nhận dạng tiếng Việt trên Google Assistant rất tốt, có thể hoạt động tốt với giọng nói của các vùng miền, tiếng địa phương. So với giọng đọc trước đây áp dụng với Google Dịch hay Google Bản đồ, giọng nói tiếng Việt trên Assistant mềm mại, khá gần gũi với cách con người trò chuyện chứ không còn máy móc kiểu giọng của của \"chị Google\". Dù vậy, khả năng ngắt câu, phát hiện chữ viết tắt cần được cải thiện.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Cùng một nội dung nhưng cách hỏi khác nhau có thể làm khó Assistant.\" data-natural-h=\"443\" data-natural-width=\"500\" src=\"https://i-sohoa.vnecdn.net/2019/05/04/Screenshot-2019-05-04-15-17-45-7216-9816-1556958943.png\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Cùng một nội dung nhưng cách hỏi khác nhau có thể \"làm khó\" Assistant.</p></td></tr></tbody></table><p class=\"Normal\"> Hạn chế lớn của Google Assistant hiện nay phải kể đến cách xử lý câu hỏi theo tình huống tự nhiên. Chẳng hạn khi hỏi: \"500 đô la Mỹ bằng bao nhiêu Việt Nam đồng\", trợ lý không đưa ra được câu trả lời trực tiếp mà chỉ gợi ý các kết quả từ Google Search. Nhưng đổi câu hỏi thành: \"500 USD bằng bao nhiêu VND (đọc: vê-nờ-đê)\" thì Assistant đổi thẳng ra con số tính theo tỷ giá. Tương tự vậy, hỏi \"bao giờ đến Tết\" hay \"mấy ngày nữa đến Tết\" sẽ không có câu trả lời trực tiếp, trong khi đó hỏi \"bao lâu nữa đến Tết\" thì lại có kết quả là \"242 ngày\".</p><p class=\"Normal\"> Để Assistant hoạt động hiệu quả hơn, người dùng cần chuẩn hóa dữ liệu của mình. Chẳng hạn, một số người dùng có thói quen ghi danh bạ theo tên viết tắt như \"C Thủy\" tức \"Chị Thủy\" thì lúc muốn trợ lý giọng nói gọi điện, nhắn tin cho người này sẽ phải đọc theo kiểu tên viết tắt (đọc: xi-thủy hoặc cờ-thủy). Để tiện hơn, có thể gán mối quan hệ cho liên lạc này (nếu có), khi đó có thể ra lệnh dạng: \"Gọi điện thoại cho chị gái, anh trai, bố, mẹ...\" thay vì nhắc tên riêng.</p><p class=\"Normal\"> Đặc biệt, việc điều khiển các thiết bị smarthome theo tên riêng của phòng chưa tốt. Thử với nhiều tính huống, Google Assistant hiển thị các kết quả tìm kiếm thay vì thực hiện lệnh. Để khắc phục, người dùng có thể gán chức năng cho phòng thay vì dùng tên riêng, tương tự với tình huống của danh bạ. Mặc dù đã hỗ trợ tiếng Việt nhưng với các câu lệnh cho nhà thông minh, Assistant lại trả lời bằng tiếng Anh. Có thể nói hiện nay, trợ lý của Google chưa hỗ trợ tốt tiếng Việt để điều khiển nhà thông minh.</p><p class=\"subtitle\"> <strong>Assistant bỏ xa các trợ lý ảo khác</strong></p><p class=\"Normal\"> Còn những hạn chế nhưng Google là \"ông lớn\" duy nhất hiện nay có trợ lý ảo hỗ trợ tiếng Việt. Trong khi đó, Siri của Apple, Amazon của Alexa hay Cortana của Microsoft đều chưa thể dùng tiếng Việt. Hơn nữa, các dịch vụ khác trong hệ sinh thái của Google cũng đáp ứng tốt hơn nhu cầu của người dùng trong nước so với các hãng kia. Chẳng hạn, Google Maps được đánh giá thông minh, chi tiết và hỗ trợ tốt tại Việt Nam hơn Apple Maps.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Assistant trên Android có thể thực hiện một số tác vụ mà trên iOS không có.\" data-natural-h=\"333\" data-natural-width=\"500\" src=\"https://i-sohoa.vnecdn.net/2019/05/04/25DA970B-82FB-47FE-97AC-B73980-4335-3472-1556958943.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Assistant trên Android có thể thực hiện một số tác vụ mà trên iOS không có.</p></td></tr></tbody></table><p class=\"Normal\"> Tuy nhiên, mức độ \"thông minh\" giữa Assistant trên iOS chưa bằng Android do sự đồng bộ và tính chất bảo mật của hai hệ điều hành này. Một ví dụ là Assistant có thể bật, tắt Wi-Fi, Bluetooth trên smartphone Android nhưng với iPhone thì không. Hay khi muốn gọi điện, nhắn tin thì các máy iOS yêu cầu người dùng phải bấm \"Xác nhận\" hoặc \"Gửi\", trong khi máy Android không có bước này. Rõ ràng Assistant và Android cùng của Google phát triển nên sẽ tương thích tốt hơn.</p><p class=\"Normal\"> Việc Google Assistant dùng được với tiếng Việt sẽ giúp người dùng khai thác thêm các tính năng trên điện thoại thông minh, đặc biệt là với smarthome - xu hướng ngày càng nở rộ tại Việt Nam. Song song với sự thông minh của máy móc, người dùng cũng đứng trước thách thức về quyền riêng tư, khả năng bị nghe lén hay thông tin cá nhân được cho mục đích riêng của nhà sản xuất.</p><p class=\"author_mail\"> <strong>Đình Nam</strong> <a href=\"javascript:;\" class=\"email\" id=\"send_mail_author\" data-article-id=\"3918412\"><i class=\"ic ic-email\"></i></a></p></article>', 'new', 0, '2019-06-01', '2019-06-02', 112, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F2%2F1.jpg?alt=media&token=d09c32c7-cc29-43fa-bede-8c88410ecb06');
INSERT INTO `article` (`id`, `title`, `description`, `category_id`, `catname`, `content`, `status`, `ispremium`, `create_at`, `publish_at`, `view`, `avatar`) VALUES
(22, 'Làm sao để du lịch Việt Nam đón thêm khách nhà giàu?', 'Miễn visa, nâng cao chất lượng dịch vụ là một số hiến kế được đưa ra để du lịch Việt Nam có thể thu hút khách nhà giàu.', 2, 'Tin trong nước', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\" dir=\"ltr\"> Tại phiên hiến kế thuộc Diễn đàn Kinh tế tư nhân Việt Nam ngày 2/5, ông Lê Quang Tùng - Thứ trưởng Bộ Văn hoá Thể thao và Du lịch cho rằng Việt Nam có lợi thế về tài nguyên thiên nhiên và di sản phong phú. Mặc dù đạt tốc độ tăng trưởng nhanh, lượng <a href=\"https://vnexpress.net/du-lich/nhieu-dia-phuong-o-viet-nam-muon-co-san-bay-qua-tai-3917211.html\">khách quốc tế đến Việt Nam</a> vẫn còn ít, mức chi tiêu thấp hơn các quốc gia trong khu vực.</p><p class=\"subtitle\" dir=\"ltr\"> Khách quốc tế ở lâu nhưng chi tiêu thấp</p><p class=\"Normal\" dir=\"ltr\"> Ông Tùng chỉ ra những tồn tại trong cơ cấu khách quốc tế của du lịch Việt Nam. Tỷ trọng những thị trường có mức chi tiêu cao, lưu trú dài ngày như Australia (1.700 USD/ngày), Bắc Mỹ (1.500 USD/ngày), Tây Âu (1.300 USD/ngày) còn khiêm tốn.</p><p class=\"Normal\" dir=\"ltr\"> Đáng báo động là những thị trường chi tiêu cao lại có xu hướng giảm trong 3 năm gần đây. Cụ thể khách từ Bắc Mỹ (mức tăng trưởng từ 7,6% giảm xuống còn 5,8%), Australia (từ 5,2% xuống còn 2,8%), châu Âu (từ 14,6% xuống còn 13,1%). Trong khi khách châu Á thì tăng mạnh.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 499px;\"><tbody><tr><td> <img alt=\"Du lịch Việt Nam cần tập trung vào những thị trường khách chi trả cao, lưu trú dài ngày. Ảnh: Khương Nha.\" data-natural-h=\"334\" data-natural-width=\"499\" src=\"https://i-dulich.vnecdn.net/2019/05/03/hoi-an-vnexpress-1556861905-3145-1556863263.png\" data-width=\"499\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Du lịch Việt Nam cần tập trung vào những thị trường khách chi trả cao, lưu trú dài ngày. Ảnh: <em>Khương Nha.</em></p></td></tr></tbody></table><p class=\"Normal\" dir=\"ltr\"> So với các quốc gia trong khu vực như Singapore, Thái Lan, khách quốc tế đến Việt Nam nghỉ dài ngày nhưng chi tiêu ít hơn. Đại diện Bộ Văn hóa Thể thao và Du lịch dẫn chứng, khách đến Việt Nam lưu trú trung bình 9,5 ngày nhưng chỉ chi tiêu 96 USD mỗi ngày. Trong khi đến Singapore 3-4 ngày, họ chi tiêu 325 USD/ngày, Thái Lan là 9,6 ngày nhưng chi tiêu 132 USD/ngày...</p><p class=\"Normal\" dir=\"ltr\"> Ông Lê Quang Tùng nhận định thị phần khách quốc tế đến Việt Nam chưa phù hợp, nếu không giải quyết sớm thì đây sẽ là trở ngại lớn của du lịch Việt Nam trong thời gian tới.</p><p class=\"subtitle\" dir=\"ltr\"> Tập trung vào thị trường chi trả cao</p><p class=\"Normal\" dir=\"ltr\"> <span>Năm 2014, khách Trung Quốc chiếm khoảng 1/4 thị phần khách đến Việt Nam, nhưng đến cuối 2018,&nbsp; đã chiếm 1/3.&nbsp;</span>Ông Trương Tấn Sơn, Tổng công ty Du lịch Sài Gòn cho rằng t<span>hị trường này có tốc độ tăng trưởng nhanh nhưng nguồn thu chưa cao.</span></p><p class=\"Normal\" dir=\"ltr\"> \"Chúng ta cần tập trung vào những thị trường có chi trả cao như Bắc Mỹ, Tây Âu, Đông Âu, Nhật Bản, Ấn Độ...\", ông Sơn nhận định đây là những thị trường tiềm năng, có sự tăng trưởng trong thời gian gần đây.</p><p class=\"Normal\"> Lấy dẫn chứng về việc không cân bằng thị phần khách du lịch, ông Phạm Hà, đại diện công ty du lịch Sang Trọng cho rằng vịnh Hạ Long (Quảng Ninh) đang bị quá tải vì không chọn lọc khách. \"Phải hạn chế khách có mức chi trả thấp tại những khu du lịch cao cấp. Như vậy khách nhà giàu mới vào\", ông Hà nhấn mạnh.</p><p class=\"subtitle\" dir=\"ltr\"> Làm sao để khách chi tiêu nhiều hơn ở Việt Nam?</p><p class=\"Normal\"> Bên cạnh cân đối lại thị phần khách, những người làm du lịch lâu năm còn muốn tìm giải pháp để khách ở lại Việt Nam dài hơn và chi tiêu nhiều hơn.</p><p class=\"Normal\"> Ông Lê Thanh Tùng, Thứ trưởng Bộ Văn hóa Thể thao và Du lịch chỉ ra vấn đề quan trọng cần quan tâm lúc này là đẩy mạnh những sản phẩm du lịch chuyên đề; tập trung vào trải nghiệm cá nhân.</p><p class=\"Normal\"> Để làm được những điều này, ông Tùng cũng đưa ra 6 giải pháp cụ thể gồm: Đa dạng hóa, nâng cao chất lượng dịch vụ; Tiếp thị mạnh hơn vào những thị trường có mức chi trả cao; Cải thiện chính sách visa theo hướng thuận lợi cho du khách; Tăng khả năng kết nối hàng không; Quản lý điểm đến hiệu quả, đảm bảo an toàn, an ninh cho du khách. Cuối cùng là yếu tố con người, từ du lịch giản đơn đến du lịch cao cấp đều phải được đào tạo bài bản, đặc biệt là vấn đề ngoại ngữ, giao tiếp.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Visa, thủ tục hải quan và hàng không Việt Nam thời gian qua có nhiều cải thiện tích cực. Ảnh: Khương Nha.\" data-natural-h=\"334\" data-natural-width=\"500\" src=\"https://i-dulich.vnecdn.net/2019/05/03/Cathay-Hang-Khong-Viet-Nam-Vne-8882-5266-1556863264.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Visa, thủ tục hải quan và hàng không Việt Nam thời gian qua có nhiều cải thiện tích cực. Ảnh: <em>Khương Nha.</em></p></td></tr></tbody></table><p class=\"Normal\"> Một trong những đề xuất được nhiều doanh nghiệp và chuyên gia du lịch đưa ra là miễn visa cho càng nhiều nước càng tốt, đặc biệt là những thị trường chi trả cao. Ông Lương Hoài Nam - Phó TGĐ Công ty hàng không Ngôi Sao Việt, thành viên Hội đồng Tư vấn Du lịch (TAB) chia sẻ, khi ông tiếp xúc với những khách hàng giàu, họ luôn đặt câu hỏi: \"Tại sao những nước xung quanh như Singapore, Thái Lan, Malaysia đều miễn visa cho họ, còn Việt Nam thì không?</p><p class=\"Normal\"> Theo các chuyên gia du lịch, visa chính là nút thắt của du lịch Việt Nam, để thu hút thêm khách du lịch quốc tế, Việt Nam cần mở mở rộng danh sách miễn thị thực hoặc áp dụng chính sách visa linh hoạt cho những nhóm khách hàng tiềm năng hoặc thị trường có chi trả cao.</p><p class=\"Normal\"> Ngoài ra, hạ tầng hàng không cũng được xem là mắt xích quan trọng để thu hút khách nhà giàu đến Việt Nam. Khách hiện phải di chuyển nhiều chặng khi không có đường bay thẳng đến nhiều điểm du lịch nổi tiếng. Các nhà ga, sân bay luôn trong tình trạng quá tải. Thủ tục nhập cảnh cần cải thiện hơn. \"Nhiều khi khách chỉ mất một phút để qua cổng hải quan, nhưng phải chờ cả tiếng để có thể lấy được hành lý rồi mới ra sân bay\", ông Đinh Việt Phương, đại diện hàng không Vietjet Air góp ý.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Việt Nam cần đạo đạo được nguồn nhân sự cấp cao nếu muốn đón du khách cao cấp. Ảnh: Khương Nha.\" data-natural-h=\"334\" data-natural-width=\"500\" src=\"https://i-dulich.vnecdn.net/2019/05/03/Hang-Khong-Viet-Nam-Vnexpress-9769-1556863264.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Việt Nam cần đạo tạo được nguồn nhân sự cấp cao nếu muốn đón du khách cao cấp. Ảnh: <em>Khương Nha.</em></p></td></tr></tbody></table><p class=\"Normal\"> Tiếp đến là vấn đề nhân sự. \"Chúng ta phải tập trung vào đào tạo nhân lực. Muốn đón khách hàng cao cấp, phải có nhân lực cao cấp\", ông Phạm Hà nói.</p><p class=\"Normal\"> Bên cạnh việc quy hoạch lại các phân khúc, sản phẩm đặc trưng của mỗi vùng miền, du lịch Việt cần xác định sản phẩm đặc trưng. \"Đến Cuba, khách mua xì gà, đến Hàn Quốc thì mua nhân sâm. Còn đến Việt Nam khách mua gì?\", ông Trương Tấn Sơn đặt vấn đề.</p><p class=\"Normal\"> Các chuyên gia cho rằng nếu chúng ta vẫn loay hoay định hướng Việt Nam là điểm đến khám phá, du khách chỉ đến một lần rồi thôi. Nhưng nếu các sản phẩm du lịch được làm mới, Việt Nam được định hướng là điểm đến nghỉ dưỡng, mua sắm... sẽ tạo luồng gió mới, thu hút thêm nhiều du khách quốc tế, đặc biệt là nhóm khách có khả năng chi trả cao.</p><p class=\"Normal\"> <strong>Xem thêm:</strong> <strong><a href=\"https://vnexpress.net/du-lich/tranh-cai-ve-viec-mien-visa-de-thu-hut-them-khach-du-lich-nha-giau-3917710.html\"><em>Tranh cãi về việc miễn visa để thu hút khách nhà giàu.</em></a></strong></p>	</article>', 'new', 0, '2019-06-01', '2019-06-19', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F3%2F2.jpg?alt=media&token=dc687b22-9040-4672-ba9c-8e7a7b83993e'),
(23, 'Bí quyết kiếm nhiều tiền của triệu phú 26 tuổi', 'Josh Altman luôn tìm cách gây ấn tượng tốt với người đối diện ngay từ giây đầu tiên gặp mặt.', 5, 'Doanh nhân', '<article class=\"content_detail fck_detail width_common block_ads_connect\">\r\n<p class=\"Normal\">\r\nJosh Altman là nhà môi giới bất động sản hàng đầu Beverly Hills (California, Mỹ). Anh đã thực hiện các thương vụ tổng trị giá hơn 1 tỷ USD trên thế giới. Altman thành triệu phú năm 26 tuổi và là tác giả cuốn sách The Altman Close. Trên <em>CNBC</em>, anh đã chia sẻ cách kiếm được nhiều tiền hơn, dù làm bất kỳ công việc nào.</p><p class=\"Normal\">\r\nẤn tượng đầu tiên là quan trọng nhất. Nó chỉ diễn ra trong chưa đầy một giây thôi, nhưng sẽ ảnh hưởng lớn nhất đến cách mọi người đối xử với bạn. Là người môi giới bất động sản, với tôi, tương tác với khách hàng là tất cả. Nếu đến gặp họ với phong cách lịch sự, tự tin, tôi có khả năng chốt hợp đồng cao hơn. Khách hàng sẽ thích thú với những gì tôi giới thiệu và sẽ muốn tìm hiểu kỹ hơn.</p><p class=\"Normal\">\r\nCác nghiên cứu cũng cho thấy trong các sự kiện hẹn hò nhanh, người ta hình thành ý kiến về đối phương chỉ trong chưa đầy một giây, bất kể có bằng chứng nào củng cố quan điểm đó của họ hay không. Vì thế, nếu bạn muốn phát triển việc kinh doanh và kiếm nhiều tiền hơn, hãy nắm vững 9 nguyên tắc sau.</p><p class=\"subtitle\">\r\n1. Đừng tự khen mình</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td>\r\n<img alt=\"Josh Altman - nhà môi giới bất động sản hàng đầu Beverly Hills. Ảnh: El Online\" data-natural-h=\"371\" data-natural-width=\"500\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/05/josh-altman-1557042641-7856-1557042876.png\" data-width=\"500\" data-pwidth=\"500\"></td>\r\n</tr><tr><td>\r\n<p class=\"Image\">\r\nJosh Altman - nhà môi giới bất động sản hàng đầu Beverly Hills. Ảnh:<em> El Online</em></p>\r\n</td>\r\n</tr></tbody></table><p class=\"Normal\">\r\nNếu một người nói \"Tôi mới mua một trang trại ở Montana\" và bạn đáp lại bằng \"Còn tôi đã có một hòn đảo ở Caribbean rồi\", đó không phải là một cuộc hội thoại. Đó là nói nhảm.</p><p class=\"Normal\">\r\nNói chuyện với khách hàng như vậy sẽ khiến bạn mất cơ hội kinh doanh và tiền bạc. Hãy hướng lời nói của bạn theo chủ đề người khác vừa nói, dù trong đầu bạn đang nghĩ gì đi nữa.</p><p class=\"subtitle\">\r\n2. Biết lắng nghe</p><p class=\"Normal\">\r\nHãy lắng nghe như thể cả cuộc sống của bạn đang phụ thuộc vào nó vậy. Hành động này cũng cho thấy bạn tôn trọng khách hàng.</p><p class=\"Normal\">\r\nCũng có trường hợp, bạn sẽ gặp người chỉ chực hỏi câu nào đó để cắt ngang lời bạn và nói về bản thân mình. Nhưng hãy kiềm chế, đừng nói lại. Hãy cố lắng nghe, tìm hiểu về đối phương và nghĩ ra mẹo đàm phán.</p><p class=\"subtitle\">\r\n3. Đừng dùng nhiều từ chuyên môn</p><p class=\"Normal\">\r\nNói chuyện bằng từ ngữ quá chuyên môn sẽ khiến khách hàng của bạn cảm thấy bất an. Vì thế, hãy dùng từ thật đơn giản, dễ hiểu và rõ ràng. Chưa kể, nếu dùng từ hoa mỹ không cần thiết, bạn sẽ chẳng khác nào một kẻ dở hơi.</p><p class=\"subtitle\">\r\n4. Điều chỉnh cách nói chuyện cho phù hợp</p><p class=\"Normal\">\r\nCác nghiên cứu tâm lý chỉ ra rằng người ta sẽ bị thu hút bởi những người giống mình. Nếu đang nói chuyện với ai đó có giọng nhẹ nhàng, bạn cũng nên giảm âm lượng lại. Đừng lấn át họ. Hãy khiến họ cảm thấy thoải mái khi nói chuyện với bạn.</p><p class=\"subtitle\">\r\n5. Chú ý ăn mặc</p><p class=\"Normal\">\r\nHãy mặc theo phong cách \"thành công\", vì đó là điều bạn muốn truyền đạt đến người khác. Luôn mang theo những bộ đồ sạch sẽ, lịch sự trong xe vì bạn không biết khi nào sẽ cần chúng đâu. Chuẩn bị trước vẫn hơn.</p><p class=\"Normal\">\r\nKhách hàng của tôi đa phần là giới siêu giàu. Vì thế, kể cả khi một tỷ phú đến gặp tôi trong bộ dạng quần short và giày thể thao sờn rách, tôi vẫn sẽ mặc vest.</p><p class=\"subtitle\">\r\n6. Nhập đúng vai</p><p class=\"Normal\">\r\nTôi từng có thời gian muốn giảm cân. Vì thế, lúc nào tôi cũng mặc đồ tập, ăn uống lành mạnh, đến phòng gym và chơi với những người thích tập luyện. Tất cả đều nhằm mục đích rèn luyện từ tư tưởng đến hành động.</p><p class=\"subtitle\">\r\n7. Biết cách khen ngợi</p><p class=\"Normal\">\r\nLà người môi giới bất động sản, tôi thường khen ngợi khách hàng hoặc bất động sản một chút. Ví dụ, tôi sẽ tìm các yếu tố của căn nhà mà mình thấy thích, và nói với chủ nhà rằng: \"Tôi thích phần đá ốp này đấy\" hoặc \"Phần mặt ngoài này thật chi tiết\".</p><p class=\"Normal\">\r\nĐó không phải là những lời nịnh nọt. Nó hướng đến mục đích chung của cả hai bên, là kiếm tiền và chốt hợp đồng.</p><p class=\"subtitle\">\r\n8. Nhìn vào mắt người đối diện</p><p class=\"Normal\">\r\nĐây là lời khuyên kinh điển trong giao tiếp. Nếu không nhìn vào mắt người khác, bạn sẽ tạo ấn tượng mình đang giấu điều gì đó.</p><p class=\"subtitle\">\r\n9. Luôn có thái độ tích cực</p><p class=\"Normal\">\r\nNăng lượng sẽ thu hút năng lượng. Vì thế, hãy luôn giữ thái độ tích cực. Luôn mỉm cười. Ai cũng thích những người như vậy.</p><p class=\"Normal\" style=\"text-align:right;\">\r\n<strong>Hà Thu</strong> <em>(theo CNBC)</em></p>	</article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F4%2F3.jpg?alt=media&token=615ec02b-dac5-4f9c-b5e7-eb7304c077ed'),
(24, 'Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong Diễn tập', 'Chủ tịch Triều Tiên ngày 4/5 tới thị sát trực tiếp vụ phóng thử tên lửa tầm ngắn và pháo phản lực tại thao trường gần thành phố Wonsan.', 3, 'Tin quốc tế', '<article class=\"content_detail fck_detail width_common\">\r\n<div id=\"article_content\" class=\"block_ads_connect\">\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456912\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/1-1557036384_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/1-1557036384_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Lãnh đạo Triều Tiên Kim Jong-un ngày 4/5 trực tiếp giám sát cuộc &amp;quot;diễn tập tấn công&amp;quot; của các đơn vị phòng thủ ở khu vực tiền phương và duyên hải phía đông, nhắm vào các mục tiêu trên Biển Nhật Bản (Hàn Quốc gọi là Biển Đông).&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Trong bức ảnh do hãng thông tấn trung ương Triều Tiên (KCNA) công bố hôm nay, ông Kim Jong-un cầm ống nhòm lớn ngồi trong một lều bạt dã chiến để theo dõi cuộc diễn tập. Phía sau ông là màn hình cho thấy một đảo đá nhỏ nhô lên giữa biển.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_0\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456912\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/1-1557036384_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Lãnh đạo Triều Tiên Kim Jong-un ngày 4/5 trực tiếp giám sát cuộc &amp;quot;diễn tập tấn công&amp;quot; của các đơn vị phòng thủ ở khu vực tiền phương và duyên hải phía đông, nhắm vào các mục tiêu trên Biển Nhật Bản (Hàn Quốc gọi là Biển Đông).&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Trong bức ảnh do hãng thông tấn trung ương Triều Tiên (KCNA) công bố hôm nay, ông Kim Jong-un cầm ống nhòm lớn ngồi trong một lều bạt dã chiến để theo dõi cuộc diễn tập. Phía sau ông là màn hình cho thấy một đảo đá nhỏ nhô lên giữa biển.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nLãnh đạo Triều Tiên Kim Jong-un ngày 4/5 trực tiếp giám sát cuộc \"diễn tập tấn công\" của các đơn vị phòng thủ ở khu vực tiền phương và duyên hải phía đông, nhắm vào các mục tiêu trên Biển Nhật Bản (Hàn Quốc gọi là Biển Đông).</p>\r\n<p>\r\nTrong bức ảnh do hãng thông tấn trung ương Triều Tiên (KCNA) công bố hôm nay, ông Kim Jong-un cầm ống nhòm lớn ngồi trong một lều bạt dã chiến để theo dõi cuộc diễn tập. Phía sau ông là màn hình cho thấy một đảo đá nhỏ nhô lên giữa biển.</p></div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456913\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/2-1557036384_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/2-1557036384_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Ông Kim dùng ống nhòm quan sát quả tên lửa rời khỏi bệ phóng. Trên bàn của ông là một tấm bản đồ, nhiều khả năng thể hiện khu vực mục tiêu của tên lửa.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Quả tên lửa này được KCNA gọi là &amp;quot;vũ khí dẫn đường chiến thuật&amp;quot;, không phải tên lửa đạn đạo tầm xa. Triều Tiên tuyên bố mục đích của cuộc diễn tập là kiểm tra khả năng vận hành và độ chính xác của vũ khí dẫn đường chiến thuật và các tổ hợp pháo phản lực tầm xa.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Triều Tiên hai lần thử &amp;quot;vũ khí chiến thuật công nghệ cao&amp;quot; vào tháng 11/2018 và tháng 4 năm nay nhưng không tiết lộ chi tiết về vũ khí này.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_1\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456913\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/2-1557036384_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Ông Kim dùng ống nhòm quan sát quả tên lửa rời khỏi bệ phóng. Trên bàn của ông là một tấm bản đồ, nhiều khả năng thể hiện khu vực mục tiêu của tên lửa.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Quả tên lửa này được KCNA gọi là &amp;quot;vũ khí dẫn đường chiến thuật&amp;quot;, không phải tên lửa đạn đạo tầm xa. Triều Tiên tuyên bố mục đích của cuộc diễn tập là kiểm tra khả năng vận hành và độ chính xác của vũ khí dẫn đường chiến thuật và các tổ hợp pháo phản lực tầm xa.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Triều Tiên hai lần thử &amp;quot;vũ khí chiến thuật công nghệ cao&amp;quot; vào tháng 11/2018 và tháng 4 năm nay nhưng không tiết lộ chi tiết về vũ khí này.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nÔng Kim dùng ống nhòm quan sát quả tên lửa rời khỏi bệ phóng. Trên bàn của ông là một tấm bản đồ, nhiều khả năng thể hiện khu vực mục tiêu của tên lửa.</p>\r\n<p>\r\nQuả tên lửa này được KCNA gọi là \"vũ khí dẫn đường chiến thuật\", không phải tên lửa đạn đạo tầm xa. Triều Tiên tuyên bố mục đích của cuộc diễn tập là kiểm tra khả năng vận hành và độ chính xác của vũ khí dẫn đường chiến thuật và các tổ hợp pháo phản lực tầm xa.</p>\r\n<p>\r\nTriều Tiên hai lần thử \"vũ khí chiến thuật công nghệ cao\" vào tháng 11/2018 và tháng 4 năm nay nhưng không tiết lộ chi tiết về vũ khí này.</p></div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60.125%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456914\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/5-1557036385_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/5-1557036385_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Ông Kim Jong-un và một sĩ quan cấp cao quân đội Triều Tiên chăm chú theo dõi cuộc diễn tập tên lửa và pháo phản lực.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Giới quan sát cho rằng sự mập mờ của Triều Tiên về đặc tính của loại &amp;quot;vũ khí chiến thuật mới&amp;quot; có thể nhằm khiến tình báo phương Tây gặp khó khăn trong đánh giá tác động thực sự của vụ thử tới tiến trình đàm phán về vấn đề phi hạt nhân hóa bán đảo Triều Tiên.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_2\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456914\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/5-1557036385_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Ông Kim Jong-un và một sĩ quan cấp cao quân đội Triều Tiên chăm chú theo dõi cuộc diễn tập tên lửa và pháo phản lực.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Giới quan sát cho rằng sự mập mờ của Triều Tiên về đặc tính của loại &amp;quot;vũ khí chiến thuật mới&amp;quot; có thể nhằm khiến tình báo phương Tây gặp khó khăn trong đánh giá tác động thực sự của vụ thử tới tiến trình đàm phán về vấn đề phi hạt nhân hóa bán đảo Triều Tiên.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nÔng Kim Jong-un và một sĩ quan cấp cao quân đội Triều Tiên chăm chú theo dõi cuộc diễn tập tên lửa và pháo phản lực.</p>\r\n<p>\r\nGiới quan sát cho rằng sự mập mờ của Triều Tiên về đặc tính của loại \"vũ khí chiến thuật mới\" có thể nhằm khiến tình báo phương Tây gặp khó khăn trong đánh giá tác động thực sự của vụ thử tới tiến trình đàm phán về vấn đề phi hạt nhân hóa bán đảo Triều Tiên.</p></div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456915\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/6-1557036385_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/6-1557036385_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Hình ảnh quả tên lửa tầm ngắn rời khỏi bệ phóng. Bộ tổng tham mưu quân đội Hàn Quốc (JCS) cho biết vụ phóng diễn ra ở bãi thử gần thành phố Wonsan, miền đông Triều Tiên.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Các thao trường gần thành phố Wonsan được Triều Tiên sử dụng cho nhiều vụ thử tên lửa đạn đạo và huấn luyện lực lượng pháo binh trong thời gian gần đây.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_3\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456915\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/6-1557036385_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Hình ảnh quả tên lửa tầm ngắn rời khỏi bệ phóng. Bộ tổng tham mưu quân đội Hàn Quốc (JCS) cho biết vụ phóng diễn ra ở bãi thử gần thành phố Wonsan, miền đông Triều Tiên.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Các thao trường gần thành phố Wonsan được Triều Tiên sử dụng cho nhiều vụ thử tên lửa đạn đạo và huấn luyện lực lượng pháo binh trong thời gian gần đây.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nHình ảnh quả tên lửa tầm ngắn rời khỏi bệ phóng. Bộ tổng tham mưu quân đội Hàn Quốc (JCS) cho biết vụ phóng diễn ra ở bãi thử gần thành phố Wonsan, miền đông Triều Tiên.</p>\r\n<p>\r\nCác thao trường gần thành phố Wonsan được Triều Tiên sử dụng cho nhiều vụ thử tên lửa đạn đạo và huấn luyện lực lượng pháo binh trong thời gian gần đây.</p></div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456916\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/7-1557036385_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/7-1557036385_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Giới chuyên gia nhận định loại tên lửa mới của Triều Tiên có thể là bản sao tên lửa đạn đạo tầm ngắn 9K720 Iskander của Nga, có khả năng mang theo đầu đạn hạt nhân.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Tên lửa này có thể sử dụng nhiên liệu rắn, xuất hiện lần đầu trong lễ duyệt binh kỷ niệm 70 năm thành lập Quân đội Nhân dân Triều Tiên tháng 2/2018.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_4\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456916\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/7-1557036385_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Giới chuyên gia nhận định loại tên lửa mới của Triều Tiên có thể là bản sao tên lửa đạn đạo tầm ngắn 9K720 Iskander của Nga, có khả năng mang theo đầu đạn hạt nhân.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Tên lửa này có thể sử dụng nhiên liệu rắn, xuất hiện lần đầu trong lễ duyệt binh kỷ niệm 70 năm thành lập Quân đội Nhân dân Triều Tiên tháng 2/2018.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nGiới chuyên gia nhận định loại tên lửa mới của Triều Tiên có thể là bản sao tên lửa đạn đạo tầm ngắn 9K720 Iskander của Nga, có khả năng mang theo đầu đạn hạt nhân.</p>\r\n<p>\r\nTên lửa này có thể sử dụng nhiên liệu rắn, xuất hiện lần đầu trong lễ duyệt binh kỷ niệm 70 năm thành lập Quân đội Nhân dân Triều Tiên tháng 2/2018.</p></div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456917\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/11-1557036385_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/11-1557036385_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Ngoài tên lửa tầm ngắn, quân đội Triều Tiên hôm qua còn phóng thử các giàn pháo phản lực đa nòng tầm xa.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Pháo phản lực cỡ nòng 240 mm của Triều Tiên có tầm bắn 40-60 km, có khả năng phóng hết cơ số đạn trong 45 giây.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_5\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456917\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/11-1557036385_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Ngoài tên lửa tầm ngắn, quân đội Triều Tiên hôm qua còn phóng thử các giàn pháo phản lực đa nòng tầm xa.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Pháo phản lực cỡ nòng 240 mm của Triều Tiên có tầm bắn 40-60 km, có khả năng phóng hết cơ số đạn trong 45 giây.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nNgoài tên lửa tầm ngắn, quân đội Triều Tiên hôm qua còn phóng thử các giàn pháo phản lực đa nòng tầm xa.</p>\r\n<p>\r\nPháo phản lực cỡ nòng 240 mm của Triều Tiên có tầm bắn 40-60 km, có khả năng phóng hết cơ số đạn trong 45 giây.</p></div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456918\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/8-1557036385_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/8-1557036385_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Mẫu vũ khí này có thể được phát triển trên cơ sở pháo phản lực 200 mm BMD-20 và pháo phản lực 240 mm BM-24 có trong biên chế quân đội Triều Tiên.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_6\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456918\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/8-1557036385_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Mẫu vũ khí này có thể được phát triển trên cơ sở pháo phản lực 200 mm BMD-20 và pháo phản lực 240 mm BM-24 có trong biên chế quân đội Triều Tiên.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nMẫu vũ khí này có thể được phát triển trên cơ sở pháo phản lực 200 mm BMD-20 và pháo phản lực 240 mm BM-24 có trong biên chế quân đội Triều Tiên.</p></div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\" style=\"width: 100%; position: relative;\">\r\n<div class=\"block_thumb_slide_show\" style=\"padding-bottom: 60%;background: #e2e2e2;\">\r\n<img style=\"position: absolute; cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/v2/helper/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456919\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/3-1557036385_r_680x0.jpg\" data-original=\"https://i-vnexpress.vnecdn.net/2019/05/05/3-1557036385_680x0.jpg\" class=\"left displayAfterResize vne_lazy_image lazyloaded\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Giới quan sát nhận định vụ thử ngày 4/5 cho thấy lãnh đạo Triều Tiên đang mất dần kiên nhẫn với Mỹ trong đàm phán phi hạt nhân hóa bán đảo Triều Tiên, sau khi hội nghị thượng đỉnh lần hai với Tổng thống Mỹ Donald Trump cuối tháng 2 không đạt kết quả.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Tuy nhiên, Trump ngày 4/5 vẫn bày tỏ niềm tin ông Kim Jong-un không thất hứa, cho rằng Mỹ và Triều Tiên sẽ đạt được đồng thuận trong vấn đề phi hạt nhân hóa.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_7\">\r\n<noscript>\r\n<img style=\"position:absolute\" class=\"displayAfterResize\" alt=\"Kim Jong-un chăm chú theo dõi tên lửa khai hỏa trong \'diễn tập tấn công\'\" data-reference-id=\"26456919\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/3-1557036385_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p&amp;gt;\r\n	Giới quan sát nhận định vụ thử ngày 4/5 cho thấy lãnh đạo Triều Tiên đang mất dần kiên nhẫn với Mỹ trong đàm phán phi hạt nhân hóa bán đảo Triều Tiên, sau khi hội nghị thượng đỉnh lần hai với Tổng thống Mỹ Donald Trump cuối tháng 2 không đạt kết quả.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n	Tuy nhiên, Trump ngày 4/5 vẫn bày tỏ niềm tin ông Kim Jong-un không thất hứa, cho rằng Mỹ và Triều Tiên sẽ đạt được đồng thuận trong vấn đề phi hạt nhân hóa.&amp;lt;/p&amp;gt;\"/>\r\n</noscript>\r\n<a href=\"javascript:void(0)\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n</div>\r\n<div class=\"desc_cation\"><p>\r\nGiới quan sát nhận định vụ thử ngày 4/5 cho thấy lãnh đạo Triều Tiên đang mất dần kiên nhẫn với Mỹ trong đàm phán phi hạt nhân hóa bán đảo Triều Tiên, sau khi hội nghị thượng đỉnh lần hai với Tổng thống Mỹ Donald Trump cuối tháng 2 không đạt kết quả.</p>\r\n<p>\r\nTuy nhiên, Trump ngày 4/5 vẫn bày tỏ niềm tin ông Kim Jong-un không thất hứa, cho rằng Mỹ và Triều Tiên sẽ đạt được đồng thuận trong vấn đề phi hạt nhân hóa.</p></div>\r\n</div>\r\n</div>\r\n<div class=\"fck_detail width_common\">\r\n<p></p><p style=\"text-align:right;\">\r\nẢnh:&nbsp;<em>KCNA</em></p><p></p>\r\n</div>\r\n</article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F5%2F4.jpg?alt=media&token=60c4ed6a-9cca-4ebc-8436-735ba4c0e114'),
(25, 'Đầu tuần miền Bắc và Trung nắng nóng', 'Thứ ba nắng nóng xuất hiện ở Tây Bắc Bộ và Trung Trung Bộ, đến thứ tư mở rộng  toàn miền Bắc và Trung, vùng núi nóng trên 39 độ.', 2, 'Tin trong nước', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\"> Chịu ảnh hưởng của rãnh áp thấp có trục đi qua Bắc Bộ, từ ngày 27/5 tới nay Bắc Bộ và Thanh Hóa liên tục có mưa. Tổng lượng mưa trong tuần ở các địa phương phổ biến 100-200 mm, một số tỉnh như Lai Châu mưa trên 300 mm. Trời mát mẻ, chỉ trong ngưỡng 24-33 độ C.&nbsp;</p><p class=\"Normal\"> Hôm nay, rãnh áp thấp gây mưa ở Bắc Bộ suy yếu, mưa giảm, trời hửng nắng từ 10h. Một số nơi như Hà Nội, trời hửng nhưng vẫn lất phất mưa. Đang giữa hè nhưng tiết trời dịu mát, ban đêm chỉ 24 độ, dự báo cao nhất ngày 31 độ C.</p><p class=\"Normal\"> Cơ quan khí tượng cho biết, từ thứ hai 3/6, miền Bắc chịu ảnh hưởng của hoàn lưu vùng thấp nóng phía tây, trời nắng, nhiệt độ tăng lên 32 ở Đông Bắc Bộ và 34 ở Tây Bắc Bộ. Một số nơi vẫn mưa về đêm và sáng.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 100%;\"><tbody><tr><td> <img alt=\"Đầu tháng 6, hoa phượng rực đỏ trong nhiều khuôn viên chung cư ở Hà Nội.&nbsp;\" data-natural-h=\"526\" data-natural-width=\"750\" src=\"https://i-vnexpress.vnecdn.net/2019/06/02/hoa-phuong1-4392-1559452049.jpg\" data-width=\"750\" data-pwidth=\"500\" style=\"width: 100%;\"></td></tr><tr><td><p class=\"Image\"> Đầu tháng 6, hoa phượng rực đỏ trong nhiều khuôn viên chung cư ở Hà Nội.&nbsp;</p></td></tr></tbody></table><p class=\"Normal\"> Từ thứ ba 4/6, Tây Bắc Bộ và Trung Trung Bộ (từ Quảng Bình trở vào đến Quảng Ngãi) sẽ xảy ra nắng nóng, nhiệt độ cao nhất lên 35-37. Từ thứ tư 5/6 trở đi, nắng nóng mở rộng ra khắp miền Bắc và Trung.</p><p class=\"Normal\"> Nhiệt độ miền Bắc thứ tư phổ biến 35-37, vùng núi Tây Bắc có thể trên 38. Miền Trung nắng nóng 36-38 độ, vùng núi phía tây có nơi trên 39 độ. Đây là mức nhiệt đo trong lều khí tượng, thực tế ngoài trời sẽ cao hơn 2-4 độ C.</p><p class=\"Normal\"> Dự báo đợt nắng nóng ở miền Bắc và Trung sẽ kéo dài đến chủ nhật 9/6. Từ tối và đêm chủ nhật, miền Bắc sẽ lại đón một đợt mưa giông diện rộng, chấm dứt nắng nóng. Miền Trung nóng duy trì hết ngày 10/6.</p><p class=\"Normal\"> Tây Nguyên và Nam Bộ từ thứ hai gió mùa tây nam sẽ hoạt động mạnh trở lại, mưa tăng lên, có nơi mưa to (50-100 mm/24 giờ) và giông mạnh. Trong cơn giông, người dân cần đề phòng khả năng lốc xoáy, mưa đá và gió giật mạnh.</p><p class=\"Normal\"> Nhận định về thời tiết tháng 6, Trung tâm dự báo khí tượng thủy văn quốc gia cho biết, do ảnh hưởng của không khí lạnh nén rãnh áp thấp, Bắc Bộ và Bắc Trung Bộ có khả năng xuất hiện 3-4 đợt mưa giông, vùng núi và trung du phía Bắc có thể mưa to, gây nguy cơ lũ quét, sạt lở đất, ngập úng ở vùng thấp trũng.</p><p class=\"Normal\"> Miền Bắc và Trung có khả năng xuất hiện 2-3 đợt nắng nóng diện rộng, Tây Bắc và vùng núi Trung Bộ có nơi nắng nóng gay gắt (trên 37 độ C). Nửa cuối tháng 6, một xoáy thuận nhiệt đới có thể xuất hiện ở biển Đông.</p> </article>', 'new', 0, NULL, NULL, 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinTrongNuoc%2F3%2Fmienbac.jpg?alt=media&token=97019ee1-38bc-4545-8206-3eb98183bbcf'),
(26, 'Trực thăng quân sự Venezuela rơi, 7 sĩ quan thiệt mạng', 'Chiếc trực thăng đâm vào núi khi chở các sĩ quan tới dự một cuộc diễn tập quân sự có sự tham dự của Tổng thống Maduro.', 3, 'Tin quốc tế', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Một trực thăng quân sự của Venezuela. Ảnh: AP.\" data-natural-h=\"300\" data-natural-width=\"500\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/2-9984-1557040383.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Một trực thăng quân sự Cougar của Venezuela. Ảnh: <em>JetPhoto</em>.</p></td></tr></tbody></table><p class=\"Normal\"> Quân đội Venezuela hôm 4/5 xác nhận 7 quân nhân, trong đó có hai trung tá và 5 sĩ quan cấp thấp hơn, đã thiệt mạng khi trực thăng chở họ&nbsp;gặp nạn ở ngoại ô thủ đô Caracas. Hiện nguyên nhân trực thăng rơi đang được tiến hành điều tra, làm rõ. Tổng thống Nicolas Maduro đã gửi lời chia buồn sâu sắc tới thân nhân các quân nhân thiệt mạng và gọi đây là sự cố đáng tiếc.</p><p class=\"Normal\"> Theo Bộ Quốc phòng Venezuela, chiếc trực thăng Cougar lao vào núi khi đang trên hành trình từ thủ đô Caracas tới San Carlos, bang Cojede, nơi Tổng thống Maduro thị sát một cuộc diễn tập của quân đội được tổ chức chỉ một tuần sau cuộc đảo chính bất thành của phe đối lập do Juan Guaido dẫn đầu.</p><p class=\"Normal\"> Truyền hình quốc gia Venezuela hôm 4/5 phát đi hình ảnh cho thấy ông Maduro đội mũ đứng giữa các học viên, binh sĩ quân đội, bắt tay và thăm các căn cứ quân sự trước khi thị sát tập trận ở Cojede. Động thái này của Tổng thống Venezuela được cho là nhằm kêu gọi quân đội sẵn sàng đáp trả những âm mưu thù địch, gây bất ổn cho đất nước.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Vị trí thủ đô Caracas của Venezuela. Đồ họa: Worldatlas.\" data-natural-h=\"300\" data-natural-width=\"500\" src=\"https://i-vnexpress.vnecdn.net/2019/05/05/1-6631-1557040383.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Vị trí thủ đô Caracas của Venezuela. Đồ họa:<em> Worldatlas</em>.</p></td></tr></tbody></table><p class=\"Normal\"> Cùng ngày, thủ lĩnh đối lập Guaido kêu gọi người dân xuống đường biểu tình, phản đối lực lượng vũ trang ủng hộ Tổng thống Maduro. Khi đám đông khoảng 150 người biểu tình tập trung gần Dinh Tổng thống ở Caracas , đụng độ với lực lượng an ninh đã nổ ra khiến 5 người thiệt mạng.</p><p class=\"Normal\"> Cuộc khủng hoảng kinh tế, chính trị ở Venezuela vẫn không ngừng leo thang kể từ khi Guaido với sự hậu thuẫn của Mỹ tự xưng là tổng thống lâm thời, thách thức chính quyền do ông Maduro đứng đầu. Washington ủng hộ phe đối lập bằng cách áp đặt thêm nhiều lệnh cấm vận mới với chính quyền Maduro, khiến cho cuộc sống người dân càng thêm khó khăn.</p><p align=\"right\" class=\"Normal\"> <strong>Mai Lâm</strong> (Theo <em>AP</em>)</p> </article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TinQuocTe%2F6%2Fvenezelua.jpg?alt=media&token=eec74eab-4aab-4017-95e5-ff8a65df944a');
INSERT INTO `article` (`id`, `title`, `description`, `category_id`, `catname`, `content`, `status`, `ispremium`, `create_at`, `publish_at`, `view`, `avatar`) VALUES
(27, 'Khối bất động sản \'khủng\' của Mark Zuckerberg', 'Ngoài căn nhà ở Palo Alto, Mark Zuckerberg sở hữu gần chục biệt thự, mảnh đất và đồn điền rộng hàng trăm ha.', 5, 'Doanh nhân', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><div id=\"article_content\" class=\"fck_detail width_common block_ads_connect\"><div class=\"item_slide_show clearfix\" style=\"width:960px;position:relative\"><div class=\"desc_cation\"><p> Nơi ở chính của vợ chồng Mark Zuckerberg là căn biệt thự rộng hơn 465 m2 tại Palo Alto, được mua vào tháng 5/2011 với giá 7 triệu USD. Các năm tiếp theo, anh mua thêm 4 căn nhà tại&nbsp;Palo Alto, với số tiền tổng cộng 43 triệu USD.<br><br> Sau khi mua, anh cho chủ cũ thuê lại và lên kế hoạch phá dỡ chúng vào năm 2016. Tuy nhiên, chính quyền thành phố không chấp thuận. Do đó,&nbsp;Mark trình kế hoạch xin cải tạo 2 căn và xây lại 2 căn.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:960px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455055\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m2-1556955294_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m2-1556955294_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Năm 2013,&nbsp;Zuckerberg chi 10 triệu USD mua một căn nhà phố rộng 510 m2 tại&nbsp;San Francisco. Ngôi nhà được xây từ năm 1928, nằm trên khu đất rộng 910 m2 và có 6,5 m2 mặt tiền vỉa hè. Nó nằm giữa khu phố Noe Valley và khu phố Mission ở Dolores Heights, trên đường 21.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_1\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Năm 2013,&nbsp;Zuckerberg chi 10 triệu USD mua một căn nhà phố rộng 510 m2 tại&nbsp;San Francisco. Ngôi nhà được xây từ năm 1928, nằm trên khu đất rộng 910 m2 và có 6,5 m2 mặt tiền vỉa hè. Nó nằm giữa khu phố Noe Valley và khu phố Mission ở Dolores Heights, trên đường 21.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:960px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455056\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m3-1556955542_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m3-1556955542_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Căn nhà nằm gần công viên Dolores, thuộc quận Mission. Sau khi mua, Mark Zuckerberg chi 1,6 triệu USD để cải tạo, bao gồm sửa chữa nhà bếp, phòng tắm và xây thêm phòng truyền thông, phòng rượu, nhà kính. Việc cải tạo tốn 17 tháng, khiến hàng xóm viết thư phàn nàn vì vỉa hè bị lấn chiếm và một chiếc SUV nhà Mark đậu trái phép.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_2\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Căn nhà nằm gần công viên Dolores, thuộc quận Mission. Sau khi mua, Mark Zuckerberg chi 1,6 triệu USD để cải tạo, bao gồm sửa chữa nhà bếp, phòng tắm và xây thêm phòng truyền thông, phòng rượu, nhà kính. Việc cải tạo tốn 17 tháng, khiến hàng xóm viết thư phàn nàn vì vỉa hè bị lấn chiếm và một chiếc SUV nhà Mark đậu trái phép.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:750px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455057\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m4-1556955812_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m4-1556955812_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Năm 2014, Mark Zuckerberg chi 100 triệu USD để mua 2 mảnh đất ở đảo&nbsp;Kauai, Hawaii. Chúng có diện tích tổng cộng là 300 ha, nằm trên một bãi biển cô lập. Anh và vợ nói mua để &amp;quot;bảo tồn vẻ đẹp tự nhiên của chúng&amp;quot;. Năm 2017, Mark chi thêm 45 triệu USD để mua một mảnh đất 36 ha khác.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_3\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Năm 2014, Mark Zuckerberg chi 100 triệu USD để mua 2 mảnh đất ở đảo&nbsp;Kauai, Hawaii. Chúng có diện tích tổng cộng là 300 ha, nằm trên một bãi biển cô lập. Anh và vợ nói mua để \"bảo tồn vẻ đẹp tự nhiên của chúng\". Năm 2017, Mark chi thêm 45 triệu USD để mua một mảnh đất 36 ha khác.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:750px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455058\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m5-1556956543_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m5-1556956543_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Miếng đất thứ nhất rộng gần 160 ha, nằm gần một bãi biển bị cô lập. Miếng thứ hai là một đồn điền có tên&nbsp;Kahu\'aina, rộng 144 ha với giá 66 triệu USD. Đồn điền này trồng mía và vài loại cây khác như gừng, nghệ, đu đủ, nuôi dê và rùa.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_4\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Miếng đất thứ nhất rộng gần 160 ha, nằm gần một bãi biển bị cô lập. Miếng thứ hai là một đồn điền có tên&nbsp;Kahu\'aina, rộng 144 ha với giá 66 triệu USD. Đồn điền này trồng mía và vài loại cây khác như gừng, nghệ, đu đủ, nuôi dê và rùa.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:750px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455059\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m6-1556956686_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m6-1556956686_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Đồn điền được chính quyền chấp thuận xây 80 căn nhà. Tuy nhiên, Mark Zuckerberg chỉ xây một căn rộng hơn 560 m2, gồm hai phòng ngủ, khu điều hành, phòng an ninh, nhà để xe.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_5\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Đồn điền được chính quyền chấp thuận xây 80 căn nhà. Tuy nhiên, Mark Zuckerberg chỉ xây một căn rộng hơn 560 m2, gồm hai phòng ngủ, khu điều hành, phòng an ninh, nhà để xe.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:960px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455060\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m7-1556956899_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m7-1556956899_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Mùa đông năm ngoái, Mark chi 59 triệu USD mua hai biệt thự ven hồ Tahoe, bang&nbsp;Nevada.&nbsp;Giao dịch diễn ra bí mật và không được tiết lộ đến tháng 5/2019. Anh&nbsp;mua thông qua một công ty trách nhiệm hữu hạn và một người quản lý tài sản cao cấp để giữ các giao dịch riêng tư. Các đại lý ký thỏa thuận không tiết lộ và xóa hình ảnh các tài sản khỏi trang web của họ.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_6\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Mùa đông năm ngoái, Mark chi 59 triệu USD mua hai biệt thự ven hồ Tahoe, bang&nbsp;Nevada.&nbsp;Giao dịch diễn ra bí mật và không được tiết lộ đến tháng 5/2019. Anh&nbsp;mua thông qua một công ty trách nhiệm hữu hạn và một người quản lý tài sản cao cấp để giữ các giao dịch riêng tư. Các đại lý ký thỏa thuận không tiết lộ và xóa hình ảnh các tài sản khỏi trang web của họ.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:960px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455061\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m8-1556957225_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m8-1556957225_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Căn nhà thứ nhất rộng 495 m2, xây từ những năm 1920 trên một mảnh đất rộng 2,4 ha. Biệt tự có 6 phòng ngủ, 5 phòng tắm với bến tàu riêng và cây cối xanh tốt xung quanh.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_7\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Căn nhà thứ nhất rộng 495 m2, xây từ những năm 1920 trên một mảnh đất rộng 2,4 ha. Biệt tự có 6 phòng ngủ, 5 phòng tắm với bến tàu riêng và cây cối xanh tốt xung quanh.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:803px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455062\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m9-1556957524_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m9-1556957524_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Biệt thự thứ hai nằm trên mảnh đất 1,5ha, có 8 phòng ngủ, 9,5 phòng tắm, trần nhà cao và lò sưởi đốt củi. Nó còn có 60 m bờ sông tư nhân với bến thuyền riêng dài 15 m cùng một sân rộng nhìn ra hồ. Nhà đi kèm một người quản gia.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_8\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Biệt thự thứ hai nằm trên mảnh đất 1,5ha, có 8 phòng ngủ, 9,5 phòng tắm, trần nhà cao và lò sưởi đốt củi. Nó còn có 60 m bờ sông tư nhân với bến thuyền riêng dài 15 m cùng một sân rộng nhìn ra hồ. Nhà đi kèm một người quản gia.</p></div></div><div class=\"item_slide_show clearfix\" style=\"width:719px;position:relative\"><div class=\"block_thumb_slide_show\" style=\"background: #e2e2e2;text-align:left\"> <img style=\"cursor: url(&quot;https://s.vnecdn.net/vnexpress/restruct/j/v390/utils/slideshow/css/slideshow/images/icons/zoom_cursor.png&quot;), auto;\" data-reference-id=\"26455063\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m10-1556957803_r_680x0.jpg\" data-original=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/m10-1556957803_680x0.jpg\" class=\"vne_lazy_image lazyloaded\" alt=\"Khối bất động sản \'khủng\' của Mark Zuckerberg\" data-component-caption=\"&amp;lt;p&amp;gt; Ngoài hai biệt thự, anh còn sở hữu 182 m bờ sông tư nhân ở hồ&nbsp;Tahoe. Tài sản của Mark tại quanh hồ này dự kiến tiếp tục mở rộng. Anh đang đàm phán để mua một biệt thự thứ ba. Chưa rõ hai biệt thự cũ sẽ bị phá bỏ hay vẫn duy trì.&amp;lt;/p&amp;gt;\" id=\"vne_slide_image_9\"> <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a></div><div class=\"desc_cation\"><p> Ngoài hai biệt thự, anh còn sở hữu 182 m bờ sông tư nhân ở hồ&nbsp;Tahoe. Tài sản của Mark tại quanh hồ này dự kiến tiếp tục mở rộng. Anh đang đàm phán để mua một biệt thự thứ ba. Chưa rõ hai biệt thự cũ sẽ bị phá bỏ hay vẫn duy trì.</p></div></div></div><div class=\"fck_detail width_common\"><p style=\"text-align:right;\"> <strong>Phiên An</strong> (<em>theo Business Insider</em>)</p></div> </article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F3%2Fmark.jpg?alt=media&token=aa93e4b0-d3bb-417d-815b-b69dee9bd095'),
(28, 'Bàn tay vàng của Warren Buffett liệu đã hết phép?', 'Nhiều tài sản ưa thích của Buffett, như Wells Fargo và Coca-Cola, không có diễn biến tốt, còn Kraft Heinz mất giá 20% năm nay.', 5, 'Doanh nhân', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\"> Cổ phiếu Berkshire Hathaway - công ty đầu tư của tỷ phú Warren Buffett - từ đầu năm nay chỉ tăng 6,6%. Trong khi đó, chỉ số S&amp;P 500 đã tăng gần 18%.</p><p class=\"Normal\"> Berkshire Hathaway nắm nhiều cổ phiếu có diễn biến tốt năm nay, như Apple, Bank of America và American Express. <span style=\"color:#696969;\"><strong>Tuy nhiên, một số mã ưa thích của Warren Buffett, nổi bật là Wells Fargo và Coca-Cola, không được như vậy. </strong></span>Kraft Heinz thậm chí còn là một thảm họa, khi mất giá hơn 20% năm nay.</p><p class=\"Normal\"> Trong đại hội cổ đông thường niên của Berkshire cuối tuần này, Buffett và Phó chủ tịch - Charlie Munger có lẽ sẽ phải giải đáp nhiều câu hỏi của cổ đông về các cổ phiếu này, kế hoạch M&amp;A của Berkshire và người kế nhiệm Buffett.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Warren Buffett trong một buổi đại hội cổ đông của công ty. Ảnh: WSJ\" data-natural-h=\"333\" data-natural-width=\"500\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/03/warren-buffett-2-1556877740-2158-1556877880.png\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Warren Buffett trong một buổi đại hội cổ đông của công ty. Ảnh: <em>WSJ</em></p></td></tr></tbody></table><p class=\"Normal\"> Kraft Heinz là tài sản đặc biệt của Berkshire. Vì Kraft Heinz còn có một cổ đông lớn khác là công ty đầu tư cổ phần tư nhân 3G. Vai trò của Berkshire là mua và nắm giữ, hơn là có các động thái mạnh tay khác.</p><p class=\"Normal\"> Sau khi Kraft và Heinz hợp nhất cách đây 4 năm, dưới sức ép của 3G, công ty mới đã cắt giảm chi phí đáng kể. Tuy nhiên, họ vẫn thất bại trong việc đầu tư vào sản phẩm mới thu hút được người trẻ.</p><p class=\"Normal\"> Cổ đông sẽ quan tâm đến việc liệu Buffett có còn tin 3G và CEO mới của Kraft Heinz - Miguel Patricio có thể lật ngược con thuyền này hay không. Nếu không, Kraft Heinz có thể có kết cục như IBM - công ty Buffett từng rất ưa chuộng, nhưng đã bị ông bán hết cổ phiếu trong vài năm qua.</p><p class=\"Normal\"> Coca-Cola cũng vậy. Họ đang phải mở rộng sang các dòng sản phẩm lành mạnh hơn như sữa, nước, trà để theo kịp sự thay đổi khẩu vị của người dùng. Buffett có lẽ sẽ phải lý giải vì sao ông vẫn nghĩ công ty này là tài sản cốt lõi, đặc biệt trong thời điểm ông cũng muốn tham gia vào ngành chăm sóc sức khỏe.</p><p class=\"Normal\"> Cổ đông cũng có thể gây sức ép lên Buffett về việc chọn người dọn dẹp scandal cho Wells Fargo. CEO nhà băng này - Tim Sloan gần đây đã từ chức. Và họ vẫn đang tụt lại so với các đối thủ.</p><p class=\"Normal\"> <span style=\"color:#696969;\"><strong>Vấn đề kế nhiệm của Buffett cũng có thể được nêu ra trong đại hội cổ đông tuần này.</strong></span> Đến tháng 8, nhà thông thái vùng Omaha sẽ bước sang tuổi 89. Munger thì đã tròn 95 tuổi. Vì thế, ông sẽ không phải là người kế vị Buffett.</p><p class=\"Normal\"> Tỷ phú đến nay vẫn giữ bí mật về người này. Năm 2015, ông chỉ cho biết: \"Chúng tôi đã có người thích hợp đảm nhận vị trí CEO của tôi. Đó là người sẵn sàng nhận công việc này ngay khi tôi qua đời hoặc nghỉ hưu\".</p><p class=\"Normal\"> Giới quan sát cho rằng một trong hai lãnh đạo hàng đầu của Berkshire hiện tại sẽ nhận vị trí này. Đó là Greg Abel - CEO Berkshire Hathaway Energy Company và Ajit Jain - người đứng đầu mảng bảo hiểm của công ty. Cả Buffett và Munger đều thường xuyên khen ngợi hai người này. Năm ngoái, họ cũng được đưa vào hội đồng quản trị công ty.</p><p class=\"Normal\"> Dù vậy, hai cá nhân quản lý danh mục đầu tư hàng đầu của Berkshire - Ted Weschler và Todd Combs cũng là những cái tên đáng chú ý. Họ sẽ tiếp tục quản lý danh mục khổng lồ của công ty sau khi cả Munger và Buffett rút chân.</p><p class=\"Normal\"> <span style=\"color:#696969;\"><strong>Một vấn đề khác cổ đông cũng rất quan tâm là khối tiền mặt của Berkshire. </strong></span>Trong thư gửi cổ đông đầu năm nay, Buffett cho biết công ty có 112 tỷ USD tiền mặt. Đây là con số khá lớn và Buffett muốn thực hiện một thương vụ M&amp;A.</p><p class=\"Normal\"> Có nhiều đồn đoán rằng Buffett có thể mua một hãng bay lớn. Berkshire hiện nắm cổ phần trong 4 hãng hàng không hàng đầu của Mỹ, là Southwest, American, Delta và United. Vấn đề là thị trường đi lên khiến các công ty này trở nên đắt đỏ hơn nhiều. Và Buffett nói rằng ông không muốn chi quá nhiều cho các thương vụ.</p><p class=\"Normal\"> Một lựa chọn khác cho Berkshire là tăng mua lại cổ phiếu, giúp tăng EPS (lợi nhuận trên mỗi cổ phiếu), đem lại giá trị cho cổ đông. Công ty này cũng đã nới lỏng quy tắc về mua lại cổ phiếu năm ngoái và kể từ đó rất tích cực mua lại.</p><p class=\"Normal\"> Nhà đầu tư cũng có thể thắc mắc liệu Buffett có trả cổ tức hay không. Đến nay, tỷ phú vẫn khẳng định không có ý định này.</p><p class=\"Normal\"> Ngoài ra, Buffett còn có thể đóng vai trò như ngân hàng để dùng núi tiền mặt đó. Berkshire Hathaway tuần này cho biết họ có kế hoạch đầu tư 10 tỷ USD vào công ty dầu mỏ - Occidental Petroleum để giúp hãng này cạnh tranh với Chevron trong thương vụ mua lại đối thủ - Anadarko Petroleum.</p><p class=\"Normal\" style=\"text-align:right;\"> <strong>Hà Thu </strong><em>(theo CNN)</em></p>	</article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/DoanhNhan%2F4%2Fwarrent.png?alt=media&token=d8634ca2-030c-432a-b085-a91ea64c0467'),
(29, 'Chuyên gia lo GDP giảm nếu siết tín dụng bất động sản cao cấp', 'Việc siết tín dụng có thể giáng thêm một cú sốc cho thị trường địa ốc đang gặp khó khăn từ nhiều phía, theo các chuyên gia. ', 6, 'Tài chính', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\"> Ngân hàng Nhà nước vừa công bố dự thảo&nbsp;lấy ý kiến với Thông tư quy định các giới hạn, tỷ lệ bảo đảm an toàn&nbsp;của các nhà băng với đề xuất, nâng&nbsp;hệ số rủi ro mà các nhà băng phải áp dụng khi trích lập dự phòng cho các khoản vay cá nhân dư nợ từ 3 tỷ đồng sẽ là 150%. Cơ quan điều hành cho biết, việc sửa quy định nhằm hạn chế tín dụng cho phân khúc bất động sản cao cấp.&nbsp;</p><p class=\"Normal\"> Tuy nhiên, các chuyên gia, doanh nghiệp bất động sản lo ngại điều này sẽ ảnh hưởng ngay đến thị trường địa ốc vốn đang có nhiều khó khăn, dấu hiệu chững lại.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Một góc Hà Nội nhìn từ trên cao. Ảnh: Giang Huy\" data-natural-h=\"274\" data-natural-width=\"500\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/04/cc-hanoi-750-8571-1556938196.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Một góc Hà Nội nhìn từ trên cao. Ảnh: <em>Giang Huy</em></p></td></tr></tbody></table><p class=\"Normal\"> Ông Nguyễn Quốc Hiệp, Chủ tịch HĐQT Tập đoàn GP Invest nhận định thị trường bất động sản năm 2019&nbsp;có thể gặp&nbsp;khó khăn vì nhiều lý do. Thứ nhất là yêu cầu&nbsp;rà soát dự án của Kiểm toán&nbsp;Nhà nước hoặc các cơ quan thanh tra. Quyết định này&nbsp;mang tính chất hồi tố, lật lại cả những dự án đã xong mà trước đây ở&nbsp;giai đoạn triển khai,&nbsp;cơ quan quản lý&nbsp;đã chấp thuận&nbsp;cho làm.&nbsp;Chủ trương rà soát dự án&nbsp;sẽ ảnh hưởng tiến độ một số dự án ở thành phố lớn.&nbsp;</p><p class=\"Normal\"> Khi thị trường bất động sản bị chậm lại cũng đồng nghĩa các ngành&nbsp;liên quan như vật liệu, sắt thép, xi măng, nội thất...&nbsp;bị ảnh hưởng và tác động đến tăng trưởng GDP. Do đó, ông cho rằng&nbsp;ở góc độ vĩ mô cần xem xét chủ trương này sẽ ảnh hưởng ra sao, tránh trục trặc về mặt vĩ mô.&nbsp;Thứ nữa, theo ông Hiệp, hiện một số tỉnh, thành có khả năng&nbsp;điều chỉnh tăng khung&nbsp;giá đất.&nbsp;&nbsp;</p><p class=\"Normal\"> Chưa kể, phía các nhà băng siết tín dụng bất động sản sẽ&nbsp;tiềm ẩn&nbsp;nguy cơ giảm nguồn&nbsp;cung, giá đất tăng và&nbsp;thị trường có thể&nbsp;gặp&nbsp;khó khăn.</p><p class=\"Normal\"> \"Bất động sản là lĩnh vực quan trọng, đóng góp lớn cho GDP. Tuy nhiên, chúng ta cầm một&nbsp;con dao sắc nhưng nếu không cẩn thận thì&nbsp;dễ đứt tay. Nếu chính sách vận dụng tốt thì&nbsp;lĩnh vực này sẽ&nbsp;thúc đẩy GDP nhưng không khéo thì&nbsp;dẫn đến thị trường&nbsp;phát triển&nbsp;quá nóng. Ngược lại, nếu&nbsp;để lạnh quá lại khiến&nbsp;GDP tụt, không đạt được tốt độ tăng trưởng\", ông Hiệp nói. Do đó, theo ông, việc sử dụng các&nbsp;công cụ như thế nào cần sự điều tiết và phối kết hợp với nhau giữa các bộ, ngành để&nbsp;tạo sự ổn định, để không có những chính sách bất thường khiến thị trường&nbsp;lúc nóng lúc lạnh...&nbsp;</p><p class=\"Normal\"> Hiệp hội Bất động sản TP HCM cũng chỉ ra một loạt những thách thức đối với thị trường. Doanh nghiệp bất động sản đối diện với nhiều rủi ro, thách thức, thậm chí có nguy cơ phá sản, do tính thiếu ổn định, khó đoán định về&nbsp;chính sách, điển hình là việc&nbsp;áp dụng \"hồi tố\" với một số trường hợp gần đây. Hơn nữa, thị trường bất động sản&nbsp;hiện&nbsp;bị giảm quy mô, sụt giảm nguồn cung dự án dẫn đến sụt giảm nguồn cung căn hộ, nhà ở, giá cả có xu thế tăng do quy luật cung, cầu (cung ít, cầu nhiều).&nbsp;</p><p class=\"Normal\"> Đơn vị này dẫn chứng, từ tháng 3/2017 đến nay, thị trường bất động sản TP HCM liên tục bị sụt giảm. Năm 2018, quy mô thị trường giảm 34% so với năm 2017; Quý I/2019, số lượng dự án được Sở Xây dựng phê duyệt giảm đến 67%. Đồng thời, theo Savills, số lượng căn hộ giảm 57% so với cùng kỳ năm ngoái.&nbsp;Thu ngân sách từ tiền sử dụng đất giảm khoảng 70%.&nbsp;</p><p class=\"Normal\"> Với những lý do đó, đại diện Hiệp hội cho rằng quyết định siết tín dụng với bất động sản có thể khiến thị trường chao đảo, đồng thời tác động đến nguồn thu ngân sách.&nbsp;</p><p class=\"Normal\"> Ông Nguyễn Văn Đính,&nbsp;Phó tổng thư ký Hiệp hội Bất động sản Việt Nam&nbsp;cho rằng, việc khuyến khích phát triển các phân khúc thấp hơn là cần thiết, song không nên vì vậy mà phải hy sinh phân khúc nhà ở cao cấp.&nbsp;Bởi theo ông,&nbsp;hiện phân khúc này thu hút nhiều người nước ngoài&nbsp;– nhóm khách hàng đến Việt Nam sinh sống và làm việc ngày càng gia tăng cùng với xu hướng hội nhập.&nbsp;</p><p class=\"Normal\"> Việt Nam cũng&nbsp;đặt mục tiêu&nbsp;tạo ra những không gian sống chất lượng, đảm bảo hạ tầng, tiện ích để phục vụ nhu cầu của bộ phận khách hàng có thu nhập cao, trong đó có người nước ngoài.</p><p class=\"Normal\"> Ông cũng cho rằng, thị trường bất động sản nói chung và phân khúc cao cấp hiện nay thanh khoản không có đột biến, giá bán chưa có dấu hiệu tăng nóng bất thường. Bởi vậy, ông cho rằng, không cần thiết phải áp dụng biện pháp siết tín dụng để tránh tác động đến những lĩnh vực liên quan.&nbsp;</p><p class=\"Normal\" style=\"text-align:right;\"> <strong>Nguyễn Hà</strong></p>	</article>', 'new', 0, NULL, NULL, 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TaiChinh%2F2%2Ftaichinh.jpg?alt=media&token=f32a08b1-e2a5-452a-b1e7-65a7ea2dbb45'),
(30, 'Giải ngân đầu tư công tiếp tục chậm', 'Tới cuối tháng 4, 39 Bộ, địa phương có tỷ lệ giải ngân vốn đầu tư công dưới 10% kế hoạch được giao năm 2019.', 6, 'Tài chính', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\"> Theo báo cáo của Bộ Tài chính, sau 4 tháng đầu năm, hơn 68.500 tỷ&nbsp;giải ngân vốn đầu tư công qua hệ thống kho bạc, đạt 16,45% kế hoạch Quốc hội giao. Tỷ lệ giải ngân vốn nước ngoài thấp hơn cùng kỳ. Tại cuộc họp về vấn đề này, Bộ Tài chính đánh giá tỷ lệ giải ngân như vậy là khá thấp nếu so với mức 85,5% kế hoạch Quốc hội giao.</p><p class=\"Normal\"> Trong đó, 39 Bộ, ngành, địa phương được nêu tên ở nhóm giải ngân thấp, dưới 10%, như Bộ Kế hoạch &amp; Đầu tư xấp xỉ 8%, Bộ Nông nghiệp &amp; Phát triển nông thôn gần 6%, Bộ Y tế hơn 2,5%... và các địa phương Vĩnh Phúc, Đồng Tháp, Khánh Hoà, Tiền Giang, TP HCM.</p><p class=\"Normal\"> 20 Bộ, địa phương có tỷ lệ giải ngân vốn công trên 30%, như&nbsp;Ban quản lý lăng Chủ tịch Hồ Chí Minh gần 58%, Tập đoàn Điện lực Việt Nam (EVN) khoảng 42%, Lào Cai trên 44,2%, Phú Thọ 42%...&nbsp;</p><p class=\"Normal\"> Nguyên nhân theo đại diện các Bộ là cuối tháng 2 mới hoàn thành phân khai chi tiết kế hoạch đầu tư vốn năm 2019 và nhập dự toán cho các dự án trên hệ thống kho bạc. Vì lẽ đó, nhiều Bộ, ngành hiện mới trong quá trình hoàn chỉnh thủ tục đấu thầu, lựa chọn nhà thầu, hồ sơ tạm ứng thanh toán...&nbsp;</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Gần 68.500 tỷ đồng vốn giải ngân qua hệ thống kho bạc 4 tháng đầu năm 2019.&nbsp;\" data-natural-h=\"332\" data-natural-width=\"500\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/03/tien-NH-1624-1556873894.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Gần 68.500 tỷ đồng vốn giải ngân qua hệ thống kho bạc 4 tháng đầu năm 2019.&nbsp;</p></td></tr></tbody></table><p class=\"Normal\"> Trước thực tế con số giải ngân vốn đầu tư công thấp, tại cuộc họp về tình hình giải ngân vốn đầu tư công,&nbsp;Phó thủ tướng Vương Đình Huệ \"giục\" đẩy nhanh việc chuẩn bị thực hiện, chọn nhà thầu và sớm khởi công dự án.</p><p class=\"Normal\"> Chủ đầu tư theo dõi chặt chẽ tình hình giải ngân của từng dự án và đề xuất cấp có thẩm quyền điều chỉnh kế hoạch từ dự án giải ngân chậm sang dự án có nhu cầu bổ sung vốn hoặc dự án đã có khối lượng hoàn thành, tránh điều chỉnh kế hoạch dồn vào cuối năm.&nbsp;Trường hợp hồ sơ đã hoàn thiện, có khối lượng nghiệm thu, các đơn vị thanh toán ngay với kho bạc, không gây nợ đọng xây dựng cơ bản.</p><p class=\"Normal\"> Phó thủ tướng cũng yêu cầu các Bộ, địa phương tăng cường kiểm tra, xử lý vi phạm trong triển khai kế hoạch đầu tư công và làm rõ trách nhiệm, xử lý nghiêm các cá nhân vi phạm, cố tình cản trở, làm chậm tiến độ giải ngân vốn...</p><p class=\"Normal\"> Bộ Kế hoạch &amp; Đầu tư đề xuất phương án phân bổ kế hoạch năm 2019 còn lại&nbsp; theo nguyên tắc đã được quy định tại các văn bản pháp luật, Nghị quyết của Quốc hội, Chính phủ và trình Thủ tướng quyết định.</p><p class=\"Normal\" style=\"text-align:right;\"> <strong>Anh Minh</strong></p>	</article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TaiChinh%2F3%2Ftaichinh1.jpg?alt=media&token=13bba671-24c3-4d5f-9989-43fc7ab5ae15'),
(31, 'Lợi ích nhóm trong \'cuộc chiến\' chung cư', 'Hiện tượng căng băng rôn trở thành một trào lưu ở các dự án, mà đằng sau đó đôi khi có dấu hiệu “lợi ích nhóm”.', 7, 'Bất động sản', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\"> Một chủ đầu tư có dự án vừa bàn giao cách đây vài tháng cho biết, từ khi triển khai có một khách hàng là chủ doanh nghiệp ngỏ ý muốn làm nhà thầu cung cấp vật liệu và nội thất cho công trình. Tuy nhiên, do không đảm bảo tiêu chí của chủ đầu tư nên họ bị trượt gói thầu. Suốt thời gian sau đó, vị này thường xuyên chia sẻ với khách hàng các thông tin xuyên tạc về chủ đầu tư như năng lực tài chính, tiến độ dự án...&nbsp;</p><p class=\"Normal\"> \"Khi dự án sắp bàn giao đúng tiến độ thì khách hàng hết lần này đến lần khác xúi giục cư dân gửi đơn khiếu nại về chất lượng công trình với những lỗi như màu sơn, hình ảnh không giống tài liệu bán hàng... Gần đây, anh ta đưa ra điều kiện cho họ tham gia gói thầu vào một dự án khác mà chúng tôi đang triển khai\", vị đại diện chủ đầu tư cho hay.</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"Cư dân tổ chức căng băng rôn tại một dự án ở Hà Nội. Ảnh: Cư dân cung cấp\" data-natural-h=\"270\" data-natural-width=\"500\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/03/quy-bao-tri-500-1531-1556855210.jpg\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td><p class=\"Image\"> Cư dân tổ chức căng băng rôn tại một dự án ở Hà Nội. Ảnh: <em>Cư dân cung cấp</em></p></td></tr></tbody></table><p class=\"Normal\"> Vài tuần trước, sắp đến hạn tòa nhà phải tổ chức hội nghị nhà chung cư của dự án, khách hàng này đề nghị chủ đầu tư bỏ phiếu ủng hộ (số phiếu của những căn hộ và diện tích vẫn do chủ đầu tư sở hữu) để họ được tham gia vào ban quản trị.&nbsp;</p><p class=\"Normal\"> Ông chia sẻ thêm, vị này tập hợp một nhóm số ít cư dân thường xuyên gửi đơn kiện tới các cơ quan báo chí hoặc thường xuyên đăng tải các video căng băng rôn, gây ảnh hưởng uy tín của chủ đầu tư.</p><p class=\"Normal\"> \"Và anh ấy cũng nói nếu không được tạo điều kiện cho những đề nghị trên thì họ sẽ thường xuyên tổ chức căng băng rôn. Chúng tôi rất bế tắc trong việc xử lý với trường hợp khách hàng này\", ông này nói.&nbsp;</p><p class=\"Normal\"> Câu chuyện \"lợi ích nhóm\" trong các tranh chấp ở các dự án chung cư không phải hiếm.&nbsp;Cư dân một dự án đi vào bàn giao 2 năm nay cho hay, trước khi chung cư có ban quản trị, một nhóm cư dân rất năng nổ tham gia vào việc đấu tranh quyền lợi chung. Họ lập một nhóm thảo luận trên mạng xã hội, thường xuyên chia sẻ các thông tin tố cáo chủ đầu tư và bày tỏ quan điểm luôn đứng về phía cư dân.&nbsp;</p><p class=\"Normal\"> \"Khi đó họ thường xuyên tổ chức căng băng rôn ở sảnh toà nhà, thậm chí kéo lên tận trụ sở của chủ đầu tư. Đơn thư khiếu nại cũng thường xuyên được gửi đến chính quyền, các cơ quan báo, đài\", cư dân này nói, đồng thời cho biết thêm bởi sự \"nhiệt tình\" đó nên khi hội nghị nhà chung cư được tiến hành cách đây nửa năm, các thành viên này tham gia ứng cử thì được bầu ngay vào ban quản trị, nhận bàn giao quỹ bảo trì hàng chục tỷ đồng.&nbsp;</p><p class=\"Normal\"> Tuy nhiên, sau đó, những thành viên trong ban quản trị không còn nhiệt tình tham gia các hoạt động trong toà nhà. Trên nhóm cộng đồng cư dân, những người có ý kiến trái chiều lập tức bị xoá tài khoản khỏi nhóm. Mọi ý kiến của cư dân bị đặt ở chế độ kiểm duyệt.&nbsp;</p><p class=\"Normal\"> \"Giờ đây họ nói rằng cư dân có ý kiến gì thì gửi văn bản, họ không có thời gian trả lời và giải quyết các vấn đề vụn vặt. Những cư dân tranh luận trên nhóm không cùng quan điểm đều bị xoá tài khoản khỏi nhóm\", một cư dân nói. Anh này cũng cho biết, các thành viên trong ban quản trị còn thường xuyên cho các đơn vị kinh doanh, quảng cáo vào toà nhà để phát tờ rơi, bán thông tin cá nhân của cư dân.&nbsp;</p><p class=\"Normal\"> Hiện ở một số dự án sắp bàn giao, tình trạng căng thẳng giữa cư dân và chủ đầu tư ngày càng diễn ra với tần suất nhiều hơn, từ chung cư bình dân cho đến cao cấp. Một chủ đầu tư đã đưa vào vận hành nhiều dự án tại Hà Nội và TP HCM cho biết, tranh chấp thường xảy ra sau khi ban quản lý toà nhà thông báo hết thời gian miễn phí dịch vụ (thường miễn vài tháng khi mới bàn giao) và bắt đầu thu phí. Và mỗi khi xảy ra các bất đồng, một số cư dân thường kích động những thành viên khác xuống đường căng băng rôn, chụp ảnh nhằm gây áp lực về mặt truyền thông. Một số cư dân còn chọn thời điểm chủ đầu tư đang triển khai bán hàng ở một dự án khác và đến đúng văn phòng kinh doanh đó để căng băng rôn.&nbsp;</p><p class=\"Normal\"> \"Có những người tự nhận ban đại diện cư dân nhưng thực tế chỉ là một nhóm nhỏ, còn đa số cư dân không có bất đồng hoặc cũng giải quyết với chủ đầu tư trên tinh thần xây dựng. Đôi khi trong những cuộc đấu tranh như vậy, thay vì đòi quyền lợi chung thì họ chỉ nhằm gây sức ép để đạt được thoả thuận của mình. Ví dụ có người đề nghị chúng tôi giảm số tiền phải nộp vào đợt cuối khi nhận nhà hoặc yêu cầu chủ đầu tư mua lại căn hộ với giá chênh cao\", lãnh đạo một doanh nghiệp bất động sản chia sẻ.&nbsp;</p><p class=\"Normal\"> Ông Lê Hoàng Châu, Chủ tịch Hiệp hội Bất động TP HCM nhận định có xuất hiện dấu hiệu nhóm lợi ích khi cố tình tạo nên sự lộn xộn ở các chung cư.</p><p class=\"Normal\"> Thậm chí, tại một số dự án, có đối tượng móc nối với thành phần xã hội đen, khống chế cộng đồng dân cư ở đó để được bầu vào ban quản trị, nhằm thao túng và trục lợi các quỹ. Trong số đó, số tiền lớn nhất được những nhóm lợi ích này \"ngắm\" đến đều tập trung vào quỹ bảo trì ở mỗi chung cư lên tới hàng chục tỷ đồng. Ngoài ra, ông cho rằng cũng không loại trừ những trường hợp đấu tranh vì mục đích cá nhân khác mà khó có thể biết được.&nbsp;</p><p class=\"Normal\" style=\"text-align:right;\"> <strong>Nguyễn Hà</strong></p>	</article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BatDongSan%2F2%2Fbatdongsan.jpg?alt=media&token=b0592d19-e7a2-4d2b-a5c7-cda1c6632016'),
(32, 'Tiềm năng đầu tư bất động sản nghỉ dưỡng Hạ Long', 'Hạ tầng giao thông, hệ thống tiện ích đồng bộ giúp Hạ Long thu hút nhà đầu tư, nhất là phần khúc bất động sản nghỉ dưỡng.', 7, 'Bất động sản', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p> Theo báo cáo về thị trường bất động sản nghỉ dưỡng công bố hồi tháng 9/2018 của CBRE, Việt Nam ghi nhận mức tăng trưởng khách du lịch quốc tế bình quân đạt 27% trong 2 năm vừa qua, nằm trong những quốc gia có đà tăng trưởng cao nhất khu vực Đông Á.</p><p> Đà tăng trưởng ấn tượng này được thúc đẩy bởi sự phát triển hệ thống cơ sở hạ tầng, giao thông, sự phổ biến của những hãng hàng không trong nước, các hãng hàng không nước ngoài mở đường bay đến Việt Nam và những động thái của Chính phủ nhằm khuyến khích và thúc đẩy nền du lịch quốc gia.</p><p> Một trong những thị trường mới nổi hiện nay là Hạ Long với cơ sở hạ tầng đồng bộ, du lịch phát triển.</p><p> <strong>Hệ thống hạ tầng giao thông phát triển</strong></p><p> Sau hơn hai thập kỷ phụ thuộc vào sức hút của Vịnh di sản, Hạ Long đang chuyển mình mạnh mẽ cùng với hệ thống hạ tầng giao thông và cơ sở lưu trú đẳng cấp. Du khách đến đây có thể di chuyển bằng đường bộ, đường thuỷ hoặc đường hàng không thông qua hệ thống cao tốc, cảng biển và sân bay hiện đại.&nbsp;</p><p> Bên cạnh đó, hệ thống tiện ích hiện đại cũng được đầu tư nhằm thu hút khách du lịch. Thành phố biển này sở hữu chuỗi nhà phố thương mại hiện đại ven biển và ven vịnh, hệ thống công viên giải trí, Bảo tàng Quảng Ninh, cáp treo Nữ Hoàng và sắp tới là Hầm Cửa Lục. Hạ Long cũng thường xuyên tổ chức các sự kiện văn nghệ, giải trí sôi động, các chương trình triển lãm, hội chợ...</p><p> Không chỉ thưởng ngoạn cảnh biển, tham quan vịnh di sản, du khách còn được trải nghiệm văn hóa địa phương với ngư dân làng chài hoặc trải nghiệm các sản phẩm du lịch độc đáo riêng tại các khu vực đảo Tuần Châu, Bãi Cháy, khu vực trung tâm thành phố Hạ Long tại Hòn Gai.</p><p class=\"Normal\"> Theo thống kê của&nbsp;Sở Du lịch Quảng Ninh, tính riêng 2 tháng đầu năm, tổng khách du lịch ước đạt trên 2,7 triệu lượt, khách quốc tế đạt gần một triệu lượt, tăng 14% so với cùng kỳ năm 2018, trong đó chủ yếu là Hạ Long.&nbsp;</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"polyad\" data-natural-h=\"282\" data-natural-width=\"500\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/03/3-5-201919-319317447-w500-4738-1556878723.png\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td class=\"CaptionImg\"><p class=\"Image\"> Nhiều dự án nghỉ dưỡng cao cấp tại Hạ Long xuất hiện thời gian qua.</p></td></tr></tbody></table><p> <strong>Ưu thế khi đầu tư Best Western Premier Sapphire Ha Long</strong></p><p class=\"Normal\"> Hạ tầng giao thông, hệ thống tiện ích đồng bộ đang tạo lợi thế cho Hạ Long phát triển, thu hút nhiều nhà đầu tư, nhất là phần khúc bất động sản nghỉ dưỡng. Một trong những dự án tiêu biểu tại thành phố biển là&nbsp;<span style=\"color:rgb(34,34,34);\">khu căn hộ cao cấp Best Western Premier Sapphire Ha Long của DOJILand.</span></p><p> Nằm trên cung đường bao biển Bến Đoan, Best Western Premier Sapphire Ha Long sở hữu nhiều ưu điểm như vị trí độc tôn bên vịnh thiên đường, tầm nhìn đẹp, đơn vị quản lý vận hành đẳng cấp thế giới. Dự án còn kết nối tiện lợi các hạ tầng giao thông trọng yếu gồm cao tốc Hà Nội - Hải Phòng, cảng tàu khách và sân bay quốc tế.&nbsp;</p><table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"width: 500px;\"><tbody><tr><td> <img alt=\"polyad\" data-natural-h=\"280\" data-natural-width=\"500\" src=\"https://i-kinhdoanh.vnecdn.net/2019/05/03/3-5-201940-w500-1190-1556878725.png\" data-width=\"500\" data-pwidth=\"500\"></td></tr><tr><td class=\"CaptionImg\"><p class=\"Image\"> Best Western Premier Sapphire Ha Long là một trong những dự án tiềm năng tại Hạ Long.</p></td></tr></tbody></table><p> Đại diện DOJILand cho biết, với số vốn ban đầu chỉ từ 450 triệu đồng, khách hàng có thể nhận căn hộ với tỷ suất sinh lời cao. Chủ đầu tư cam kết sinh lời theo tỷ lệ 90:10 (90% lợi nhuận thuộc về khách mua, 10% chủ đầu tư hưởng). Nhờ đó, hầu hết sản phẩm đều giao dịch thành công ngay mỗi lần mở bán.</p><p> Tháng 5/2019, chủ đầu tư còn hỗ trợ vay vốn lãi suất 0% trong 30 tháng từ ngân hàng Vietcombank và TPBank cùng quà tặng kim cương trị giá 50 triệu cho những chủ nhân tiếp theo tại Best Western Premier Sapphire Ha Long.</p><div class=\"box_brief_info\"><p class=\"Normal\"> Chi tiết liên hệ hotline: 1800 6918. Website:&nbsp;<a href=\"http://bestwesternpremiersapphirehalong.vn/\" target=\"_blank\">www.bestwesternpremiersapphirehalong.vn</a></p><p class=\"Normal\"> Địa chỉ dự án: Số 1, Đường Bến Đoan, Phường Hồng Gai, TP Hạ Long, Quảng Ninh</p><p class=\"Normal\"> Đơn vị tư vấn và quản lý bán hàng: Công ty TNHH Phát triển và Kinh doanh bất động sản Weland</p><p class=\"Normal\"> Đại lý phân phối chính thức: Sàn BĐS MG Land, Tây Đô Land, Four Home, Real Homes Invest, Nhật Minh Land, An Cư.</p></div><p class=\"Normal\" style=\"text-align:right;\"> <strong>Tâm Anh</strong></p>	</article>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/BatDongSan%2F3%2Fbatdongsan1.png?alt=media&token=12e886a0-24bb-44d0-803f-266d3a88ed14');
INSERT INTO `article` (`id`, `title`, `description`, `category_id`, `catname`, `content`, `status`, `ispremium`, `create_at`, `publish_at`, `view`, `avatar`) VALUES
(33, 'Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng', 'Sài Gòn có nhiều trung tâm thương mại sầm uất, do đó nếu dịp 30/4-1/5 này không đi chơi xa thì danh sách những điểm mua sắm này cũng khiến bạn mất cả ngày để khám phá.', 9, 'Trung tâm mua sắm', '<div class=\"entry-body\" data-role=\"content\"><h2 style=\"text-align: justify;\">Saigon Centre (Takashimaya)</h2><p style=\"text-align: justify;\">Với những \"tín đồ\" mê shopping thì nhất định không thể không biết đến cái tên cực hot trong suốt thời ngày gần đây đó chính là Saigon Centre.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/2321_100637_00_Saigon_Centre_N13_966x668.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Đến <em>Saigon Centre</em>, bạn sẽ thỏa thích lựa chọn hàng trăm thương hiệu nổi tiếng trong và ngoài nước từ thời trang đến ẩm thực. (Ảnh: Saigon Centre)</td></tr></tbody></table><p style=\"text-align: justify;\">Takashimaya tọa lạc bên trong Saigon Centre, nổi tiếng là tập đoàn bán lẻ hàng đầu Nhật Bản được nhiều người ưa chuộng. Hiện đây là một trong những trung tâm thương mại mới và hiện đại nhất tại Sài Gòn, nơi tích hợp rất nhiều những hoạt động hiện đại, cập nhật theo trào lưu giới trẻ hiện nay.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/00/1730_sheispennylane.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Một quán cafe sang trọng trong Takashimaya. (Ảnh: @sheispennylane)</td></tr></tbody></table><p style=\"text-align: justify;\">Nơi đây được biết đến là trung tâm mua sắm chính hiệu dành cho những người trẻ sang chảnh tại Sài Gòn với những nhãn hàng cao cấp như MontBlanc, Moschino, Versace, Kenzo, Tommy Hilfiger, Valentino,…</p><p style=\"text-align: justify;\">Địa chỉ: 65 Lê Lợi, P. Bến Nghé, Quận 1, TP. HCM</p><h3 style=\"text-align: justify;\">Crescent Mall</h3><p style=\"text-align: justify;\">Crescent Mall được coi là trung tâm thương mại lớn đầu tiên tại Việt Nam đạt tiêu chuẩn quốc tế. Với thiết kế hình vòng cung độc đáo, sang trọng kết hợp hơn 134 cửa hàng bán lẻ được phân bổ khắp năm tầng lầu hiện đại và rộng lớn, nơi đây xứng đáng là một trong những trung tâm mua sắm bậc nhất cho người dân Sài Thành.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/1707_Crescent-Mall-H1_Elle_VN.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Không gian sang trọng và hiện đại của Crescent Mall. (Ảnh: Elle VN)</td></tr></tbody></table><p style=\"text-align: justify;\">Bên cạnh là địa điểm mua sắm quen thuộc thì Crescent Mall còn biết đến là nơi diễn ra rất nhiều sự kiện giao lưu khác nhau. Từ những buổi liveshow của các nghệ sĩ nổi tiếng, biểu diễn thời trang, chương trình hòa nhạc tới những sự kiện mắt sản phẩm đã thu hút một lượng lớn người tới tham gia.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/1907_crescent-mall-outside-night.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Ngoài ra lợi thế của Crescent Mall chính là tọa lạc ngay sát bên hồ bán nguyệt, có khu vực công viên thoáng mát và hiện đại. (Ảnh: Crescent Mall)</td></tr></tbody></table><p style=\"text-align: justify;\">Đến đây, bạn có thể sống \"trọn một ngày\" tại Crescent Mall với lịch trình ăn chơi lý tưởng như cafe tại các cửa hàng Phúc Long, Highlands Coffee, Coffee Bean &amp; Tea Leaf, thỏa sức mua sắm với vô số các cửa hàng thời trang, phụ kiện, làm đẹp, công nghệ, tham quan ăn uống tại khu tổ hợp foodcourt và xem phim.</p><p style=\"text-align: justify;\">Địa chỉ: 101 Tôn Dật Tiên, P. Tân Phú, Quận 7, TP. HCM</p><h2 style=\"text-align: justify;\"><strong>Trung tâm thương mại Vạn Hạnh - Vạn Hạnh Mall</strong></h2><p style=\"text-align: justify;\">Vạn Hạnh Mall chính thức đi vào hoạt động vào ngày 25/01/2018 đã là tụ điểm mua sắm, giải trí được giới trẻ thành phố yêu thích. Nơi đây đáp ứng đa dạng nhu cầu mua sắm của khách hàng từ thời trang, mỹ phẩm, nội thất, đồ gia dụng cho đến ẩm thực, giáo dục và giải trí với 7 tầng lầu mua sắm thương mại.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/1241__ngocquyha_.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Lối kiến trúc ngỡ như trời Tây của Vạn Hạnh Mall. (Ảnh: @_ngocquyha_)</td></tr></tbody></table><p class=\"normalPara\" style=\"text-align: justify;\">Bước vào Vạn Hạnh Mall với đầy đủ các dịch vụ nên không cần phải lo lắng đến việc tìm chỗ ăn hay chỗ chơi. Khu trung tâm thương mại này là điểm tập trung của các thương hiệu lớn và uy tín như: Đại siêu thị Co.opXtra, Phương Nam Book City, CGV, trung tâm giải trí Power Bowl, trung tâm anh ngữ ILA, các thương hiệu thời trang quốc tế như Charles &amp; Keith, Pedro, Mujosh, Nike, Levi\'s, Adidas.. và hơn 50 thương hiệu ẩm thực, nhà hàng, cà phê nổi tiếng như Starbucks Coffee, Highlands Coffee, R&amp;B Tea, Sumo BBQ, Sushi Kei, Seoul Garden, The Pizza Company, Buffalo Wild Wings,...</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/1559_Minhthuy75___vanhanhmall.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Bên trong Phương Nam Book City. (Ảnh: @Minhthuy75)</td></tr></tbody></table><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/1444_bi_cat.ddcn_van_hanh_mall.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Đây là điểm check-in hot của giới trẻ trong thời gian gần đây. (Ảnh: @bi_cat.ddcn)</td></tr></tbody></table><p class=\"normalPara\">Địa chỉ: 11 Sư Vạn Hạnh, Quận 10, TP.HCM.</p><h2 class=\"normalPara\" style=\"text-align: justify;\">Aeon Mall Shopping Center Tân Phú</h2><p style=\"text-align: justify;\">Được đưa vào hoạt động đầu năm 2011, Aeon Mall (Tân Phú) từng gây nên cơn sốt mua sắm cho người dân và các bạn trẻ ở Sài Gòn. Trung tâm thương mại mang phong cách mua sắm Nhật Bản, với đội ngũ nhân viên phục vụ chuyên nghiệp và chu đáo sẽ đem đến cho khách hàng những trải nghiệm thú vị khi mua sắm tại đây.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/0205_nhung-nuoc-co-chien-luoc-cua-aeon-o-viet-nam.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Aeon Mall Tân Phú sở hữu diện tích rất rộng giúp cho khách hàng thoải mái mua sắm. (Ảnh: Aeon Mall)</td></tr></tbody></table><p style=\"text-align: justify;\">Đặc biệt, đừng lo ngại địa điểm này quá xa so với trung tâm vì Aeon Mall đã có sẵn các tuyến xe buýt miễn phí để khách hàng có thể di chuyển từ trung tâm thành phố tới mua sắm và ngược lại.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/10/1008_sushi-aeon-mall-8.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Bạn có thể tìm mua sushi với giá rẻ tại đây. (khoảng 5.000 - 10.000 đồng/chiếc). (Ảnh: Aeon Mall)</td></tr></tbody></table><p style=\"text-align: justify;\">Địa chỉ: 30 Bờ Bao Tân Thắng, P. Sơn Kỳ, Quận Tân Phú, TP. HCM</p><h2 style=\"text-align: justify;\">Saigon Garden</h2><p style=\"text-align: justify;\">Điều làm nên sự khác biệt của Saigon Garden so với các trung tâm thương mại khác bởi lối kiến trúc hiện đại và gần gũi với thiên nhiên. Toàn bộ ngôi nhà đều được phủ xanh bằng các loại cây cối từ mặt đất kéo lên đến đỉnh. Một phong cách hoàn toàn khác biệt so với những tòa cao tầng hoặc hàng quán ở xung quanh đó.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/09/5614_img-9270-1450885011474.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Nằm cạnh con phố đi bộ Nguyễn Huệ nổi tiếng lúc nào cũng luôn tấp nập du khách trong và ngoài nước tới tham quan. (Ảnh: Kenh14)</td></tr></tbody></table><p style=\"text-align: justify;\">Khu vực tầng trệt là nơi mua sắm chính của SaiGon Garden với sự xuất hiện của những thương hiệu toàn cầu. Khu vực được nhiều người quan tâm nhất khi tới đây đó chính khu giữa của trung tâm thương mại. Nơi đây được thiết kế giống như một con phố nhỏ với rất nhiều nhà hàng Tây – Âu và cửa hàng thời trang nằm 2 bên.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/09/5608_img-9233-1450885010313.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Không gian được bao phủ bởi cây xanh. (Ảnh: Kenh14)</td></tr></tbody></table><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/09/5612_img-9263-1450885011276.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Ở trên tầng 2, 3 và 4 là nơi các bạn thưởng thức các loại đồ ăn cùng với thức uống hấp dẫn của Nhật, Hàn, Thái, Việt Nam. (Ảnh: Kenh14)</td></tr></tbody></table><p style=\"text-align: justify;\">Địa chỉ: 99 Nguyễn Huệ , Quận 1, TP. HCM.</p><h2 style=\"text-align: justify;\">Vincom Đồng Khởi</h2><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td style=\"text-align: justify;\"><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/09/2437_cliffpham.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Vincom Đồng Khởi là khu thương mại nổi bật tại trung tâm quận 1. (Ảnh: @cliffpham)</td></tr></tbody></table><p style=\"text-align: justify;\">Nếu như nhắc tới các trung tâm thương mại Sài Gòn mà bỏ qua Vincom Đồng Khởi thì sẽ là một thiếu sót rất lớn. Nơi đây một thời được mệnh danh là địa điểm check-in hot của giới trẻ Sài Thành.</p><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"MASTERCMS_TPL_TABLE\" style=\"width: 100%;\"><tbody><tr><td><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" border=\"0\" class=\"__img_mastercms\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/09/5817_HM_dvincom_dong_khoi.jpg\" style=\"max-width:100%; padding:0px; margin:0px;\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\"></td></tr><tr><td>Thương hiệu thời trang H&amp;M được giới trẻ săn đón tại Vincom Đồng Khỏi. (Ảnh: H&amp;M)</td></tr></tbody></table><p style=\"text-align: justify;\">Với những thương hiệu thời trang giá rẻ như Zara, H&amp;M, Pull&amp;Bear, Stradivarius… Vincom Đồng Khởi trở thành một địa điểm mua sắm quần áo hấp dẫn tại Sài Gòn. Với 5 tầng dịch vụ trải dài từ khu B3 đến L2, Vincom Đồng Khởi đáp ứng mọi nhu cầu của giới trẻ.</p><p style=\"text-align: justify;\">Địa chỉ: 72 Lê Thánh Tôn, Bến Nghé, Quận 1, TP. HCM.</p><p style=\"text-align: center;\"><span style=\"color:#b22222;\"><strong>XEM THÊM</strong></span></p><table class=\"__mb_article_in_image __mb_article_in_image_large\"><tbody><tr><td align=\"left\" valign=\"top\" width=\"115\"><a class=\"__mb_article_in_image_large_box\" href=\"https://vietnammoi.vn/mach-nho-5-quan-cafe-yen-tinh-tranh-nong-o-sai-gon-ngay-nghi-le-98069.htm\" title=\"Mách nhỏ 5 quán café yên tĩnh “tránh nóng” ở Sài Gòn ngày nghỉ lễ\"><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/29/00/croped/thumbnail/2924_angelina_bui.jpg\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\" width=\"115\"> </a></td><td align=\"left\" style=\"vertical-align: top;\" valign=\"top\"><a href=\"https://vietnammoi.vn/mach-nho-5-quan-cafe-yen-tinh-tranh-nong-o-sai-gon-ngay-nghi-le-98069.htm\" title=\"Mách nhỏ 5 quán café yên tĩnh “tránh nóng” ở Sài Gòn ngày nghỉ lễ\">Mách nhỏ 5 quán café yên tĩnh “tránh nóng” ở Sài Gòn ngày nghỉ lễ</a><p><span class=\"tpl_desc\">Thay vì đi du lịch xa, bạn có thể tìm đến những quán café yên tĩnh này để “tránh nóng” trong dịp lễ 30/4-1/5 này ...</span></p></td></tr></tbody></table><table class=\"__mb_article_in_image __mb_article_in_image_large\"><tbody><tr><td align=\"left\" valign=\"top\" width=\"115\"><a class=\"__mb_article_in_image_large_box\" href=\"https://vietnammoi.vn/muon-kieu-nghi-le-304-cua-nguoi-sai-gon-98020.htm\" title=\"Muôn kiểu nghỉ lễ 30/4 của người Sài Gòn\"><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/anhttl/042018/28/19/thumbnail/3138_mot.gif\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\" width=\"115\"> </a></td><td align=\"left\" style=\"vertical-align: top;\" valign=\"top\"><a href=\"https://vietnammoi.vn/muon-kieu-nghi-le-304-cua-nguoi-sai-gon-98020.htm\" title=\"Muôn kiểu nghỉ lễ 30/4 của người Sài Gòn\">Muôn kiểu nghỉ lễ 30/4 của người Sài Gòn</a><p><span class=\"tpl_desc\">Năm nay dịp nghỉ lễ 30/4-1/5 kéo dài 4 ngày nên nhiều Sài Gòn có những dự định khác nhau. Có người chọn tour du ...</span></p></td></tr></tbody></table><table class=\"__mb_article_in_image __mb_article_in_image_large\"><tbody><tr><td align=\"left\" valign=\"top\" width=\"115\"><a class=\"__mb_article_in_image_large_box\" href=\"https://vietnammoi.vn/dam-minh-trong-thien-nhien-hoang-so-mua-le-lon-97800.htm\" title=\"Đắm mình trong thiên nhiên hoang sơ mùa lễ lớn\"><img alt=\"top cac trung tam thuong mai dep va mat nhat sai gon cho du khach mua sam tranh nong\" src=\"https://cdn.vietnammoi.vn/stores/news_dataimages/huongttc/042018/27/19/croped/thumbnail/dam-minh-trong-thien-nhien-hoang-so-mua-le-lon-59-.3228.jpg\" title=\"Top các trung tâm thương mại đẹp và mát nhất Sài Gòn cho du khách mua sắm, tránh nóng\" width=\"115\"> </a></td><td align=\"left\" style=\"vertical-align: top;\" valign=\"top\"><a href=\"https://vietnammoi.vn/dam-minh-trong-thien-nhien-hoang-so-mua-le-lon-97800.htm\" title=\"Đắm mình trong thiên nhiên hoang sơ mùa lễ lớn\">Đắm mình trong thiên nhiên hoang sơ mùa lễ lớn</a><p><span class=\"tpl_desc\">Một số điểm đến hấp dẫn, hút khách Sài Gòn dịp nghỉ lễ 30/4 - 1/5 mà không cần đi quá xa.</span></p></td></tr></tbody></table></div>', 'new', 0, NULL, '2019-06-02', 0, 'https://firebasestorage.googleapis.com/v0/b/th16news-a9ac4.appspot.com/o/TrungTamMuaSam%2F2%2Ftrungtam.jpg?alt=media&token=9a4c3bb5-087f-4c79-ac98-a30d46ffbb2a'),
(34, 'Giá xăng tiếp tục tăng mạnh', 'Mỗi lít xăng E5 RON 92 tăng 985 đồng, xăng RON 95 thêm 956 đồng sau điều chỉnh từ 16h chiều 2/5. ', 10, 'Thị trường 24h', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><p class=\"Normal\"> Thông tin từ liên Bộ Công Thương - Tài chính, giá bán lẻ xăng dầu tiếp tục được điều chỉnh tăng lần thứ 3 liên tục từ giữa tháng 3. Cụ thể, xăng E5 RON 92 tăng thêm 985 đồng một lít, xăng RON 95 mức tăng 956 đồng. Các mặt hàng dầu cũng tăng 311 - 385 đồng một lít, kg tuỳ loại.</p><p class=\"Normal\"> Sau tăng giá, xăng RON 95 vượt mức 22.000 đồng lít, lên tối đa 22.191 đồng; xăng E5 RON 92 tối đa 20.688 đồng một lít. Dầu diesel có mức giá mới 17.695 đồng, dầu hoả 16.625 đồng.</p><div style=\"width:100%;text-align:center;\"><div id=\"chart-930\" data-component=\"true\" data-component-type=\"chart\" data-component-value=\"930\" data-highcharts-chart=\"0\"><div id=\"highcharts-8p4v2sc-0\" class=\"highcharts-container \" style=\"position: relative; overflow: hidden; width: 500px; height: 400px; text-align: left; line-height: normal; z-index: 0; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-family: Arial; color: rgb(51, 51, 51); font-size: 12px; font-weight: normal; font-style: normal;\"><svg version=\"1.1\" class=\"highcharts-root\" style=\"font-family:Arial;font-size:12px;color:#333;font-weight:normal;font-style:normal;fill:#333;\" xmlns=\"http://www.w3.org/2000/svg\" width=\"500\" height=\"400\" viewBox=\"0 0 500 400\"><desc>Created with Highstock 5.0.9</desc><defs><clipPath id=\"highcharts-8p4v2sc-1\"><rect x=\"0\" y=\"0\" width=\"404\" height=\"248\" fill=\"none\"></rect></clipPath><clipPath id=\"highcharts-8p4v2sc-3\"><rect x=\"0\" y=\"0\" width=\"404\" height=\"248\" fill=\"none\"></rect></clipPath></defs><rect fill=\"#ffffff\" class=\"highcharts-background\" x=\"0\" y=\"0\" width=\"500\" height=\"400\" rx=\"0\" ry=\"0\"></rect><rect fill=\"none\" class=\"highcharts-plot-background\" x=\"86\" y=\"48\" widt